+#!/usr/bin/env perl
+
+use strict;
+use warnings;
+use FindBin;
+use lib "$FindBin::Bin/../lib";
+
+# Try to load Catalyst::ScriptRunner
+eval {
+    require Catalyst::ScriptRunner;
+    Catalyst::ScriptRunner->import();
+};
+
+# If Catalyst::ScriptRunner is not found, provide a clear error message
+if ($@) {
+    die "ERROR: Catalyst::ScriptRunner not found.\n\n" .
+        "Please install it by running one of these commands:\n\n" .
+        "For global installation (recommended):\n" .
+        "  sudo cpanm Catalyst::ScriptRunner\n\n" .
+        "For local installation:\n" .
+        "  cpanm --local-lib=~/perl5 Catalyst::ScriptRunner\n" .
+        "  echo 'eval $(perl -I$HOME/perl5/lib/perl5 -Mlocal::lib)' >> ~/.bashrc\n" .
+        "  source ~/.bashrc\n\n" .
+        "Or run the provided installation script:\n" .
+        "  chmod +x install_global_dependencies.sh\n" .
+        "  ./install_global_dependencies.sh\n";
+}
+
+# Run the application
+Catalyst::ScriptRunner->run('Comserv', 'Server');
+
+1;

