+#!/usr/bin/env perl
+
+use strict;
+use warnings;
+
+print "Checking for Catalyst::ScriptRunner...\n";
+
+# Try to load Catalyst::ScriptRunner
+eval {
+    require Catalyst::ScriptRunner;
+};
+
+if ($@) {
+    print "Catalyst::ScriptRunner is NOT installed.\n";
+    print "Error: $@\n";
+    
+    # Print @INC paths
+    print "\nPerl is looking for modules in these directories (@INC):\n";
+    foreach my $path (@INC) {
+        print "  $path\n";
+    }
+} else {
+    print "Catalyst::ScriptRunner is installed!\n";
+    print "Version: $Catalyst::ScriptRunner::VERSION\n";
+    
+    # Find the path to the module
+    my $module_path = $INC{"Catalyst/ScriptRunner.pm"};
+    print "Module path: $module_path\n";
+}
+
+# Check for other Catalyst modules
+print "\nChecking for other Catalyst modules:\n";
+
+my @modules = (
+    'Catalyst::Runtime',
+    'Catalyst::Devel',
+    'Catalyst::Plugin::ConfigLoader',
+    'Catalyst::Plugin::Static::Simple',
+    'Catalyst::Plugin::StackTrace'
+);
+
+foreach my $module (@modules) {
+    eval "require $module";
+    if ($@) {
+        print "  $module: NOT installed\n";
+    } else {
+        my $version_var = $module . "::VERSION";
+        my $version = eval "\$$version_var";
+        print "  $module: Installed (version: $version)\n";
+    }
+}
+
+print "\nPerl version: $]\n";
+print "Perl executable: $^X\n";
