+#!/usr/bin/perl
+use strict;
+use warnings;
+use File::Slurp;
+
+# Script to modify .tt files when direct access is restricted
+
+sub modify_tt_file {
+    my ($file_path, $old_text, $new_text) = @_;
+    
+    # Read file
+    my $content = read_file($file_path);
+    
+    # Replace content
+    $content =~ s/\Q$old_text\E/$new_text/g;
+    
+    # Write back
+    write_file($file_path, $content);
+    
+    print "Modified: $file_path\n";
+}
+
+# Test modification on documentation_update_summary.tt
+my $file_path = '/home/shanta/PycharmProjects/comserv2/Comserv/root/Documentation/documentation_update_summary.tt';
+my $old_version = "Comserv/root/Documentation/documentation_update_summary.tt,v 0.02 2025/01/02 shanta Exp shanta";
+my $new_version = "Comserv/root/Documentation/documentation_update_summary.tt,v 0.03 2025/01/16 shanta Exp shanta";
+
+if (-f $file_path) {
+    modify_tt_file($file_path, $old_version, $new_version);
+} else {
+    print "File not found: $file_path\n";
+}
+
+print "Script completed.\n";
