--- Comserv/root/static/css/themes/apiary.css
+++ Comserv/root/static/css/themes/apiary.css
@@ -40,5 +40,98 @@
     color: var(--bee-yellow);
 }

+/* Inspection-specific styles for Apiary theme */
+.inspection-list {
+    display: flex;
+    flex-direction: column;
+    gap: 15px;
+}
+
+.inspection-item {
+    background: var(--table-header-bg);
+    border-radius: 8px;
+    padding: 15px;
+    border-left: 4px solid var(--primary-color);
+}
+
+.inspection-header {
+    display: flex;
+    justify-content: space-between;
+    align-items: center;
+    margin-bottom: 10px;
+}
+
+.inspection-date {
+    font-weight: bold;
+    color: var(--text-color);
+}
+
+.inspection-status {
+    padding: 4px 12px;
+    border-radius: 12px;
+    font-size: 12px;
+    font-weight: bold;
+    text-transform: uppercase;
+}
+
+.inspection-status.excellent {
+    background: var(--success-color);
+    color: white;
+}
+
+.inspection-status.good {
+    background: var(--link-color);
+    color: white;
+}
+
+.inspection-status.fair {
+    background: var(--warning-color);
+    color: var(--text-color);
+}
+
+.inspection-status.poor,
+.inspection-status.critical {
+    background: #dc3545;
+    color: white;
+}
+
+.inspection-details {
+    display: grid;
+    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
+    gap: 10px;
+    margin-bottom: 10px;
+    font-size: 14px;
+}
+
+.inspection-notes {
+    margin: 10px 0;
+    font-size: 14px;
+    font-style: italic;
+    color: #666;
+}
+
+.inspection-actions {
+    display: flex;
+    gap: 10px;
+    margin-top: 10px;
+}
+
+/* Responsive design for inspections */
+@media (max-width: 768px) {
+    .inspection-header {
+        flex-direction: column;
+        align-items: flex-start;
+        gap: 10px;
+    }
+    
+    .inspection-details {
+        grid-template-columns: 1fr;
+    }
+    
+    .inspection-actions {
+        justify-content: center;
+    }
+}
+
 /* All other container styles are inherited from base-containers.css */
 /* This ensures consistency across all themes while maintaining Apiary's unique bee styling */

