--- Comserv/root/static/js/documentation.js
+++ Comserv/root/static/js/documentation.js
@@ -0,0 +1,170 @@
+/**
+ * Documentation System JavaScript
+ * Handles interactive elements in the documentation system
+ */
+
+document.addEventListener('DOMContentLoaded', function() {
+    // Initialize expandable sections
+    initExpandableSections();
+    
+    // Initialize file type tabs
+    initFileTabs();
+});
+
+/**
+ * Initialize expandable sections
+ */
+function initExpandableSections() {
+    document.querySelectorAll('.section-toggle').forEach(header => {
+        header.addEventListener('click', function() {
+            this.classList.toggle('expanded');
+            // Find the next .section-content element within the same section
+            const section = this.closest('.doc-section');
+            const content = section.querySelector('.section-content');
+            
+            if (content.classList.contains('expanded')) {
+                content.style.maxHeight = '0';
+                content.classList.remove('expanded');
+                // Update ARIA attributes
+                this.setAttribute('aria-expanded', 'false');
+                content.setAttribute('aria-hidden', 'true');
+            } else {
+                content.style.maxHeight = content.scrollHeight + 'px';
+                content.classList.add('expanded');
+                // Update ARIA attributes
+                this.setAttribute('aria-expanded', 'true');
+                content.setAttribute('aria-hidden', 'false');
+            }
+        });
+        
+        // Set initial ARIA attributes
+        const isExpanded = header.classList.contains('expanded');
+        header.setAttribute('aria-expanded', isExpanded ? 'true' : 'false');
+        header.setAttribute('role', 'button');
+        header.setAttribute('tabindex', '0');
+        
+        // Add keyboard support
+        header.addEventListener('keydown', function(e) {
+            if (e.key === 'Enter' || e.key === ' ') {
+                e.preventDefault();
+                this.click();
+            }
+        });
+        
+        // Find the content section and set its ARIA attributes
+        const section = header.closest('.doc-section');
+        const content = section.querySelector('.section-content');
+        if (content) {
+            content.setAttribute('aria-hidden', isExpanded ? 'false' : 'true');
+        }
+    });
+    
+    // Initialize all expanded sections
+    document.querySelectorAll('.section-content.expanded').forEach(content => {
+        content.style.maxHeight = content.scrollHeight + 'px';
+    });
+}
+
+/**
+ * Initialize file type tabs
+ */
+function initFileTabs() {
+    // Add click handlers for file type tabs
+    document.querySelectorAll('.file-type-tab').forEach(tab => {
+        tab.addEventListener('click', function(evt) {
+            showFileType(evt, this.getAttribute('data-file-type'));
+        });
+        
+        // Set ARIA attributes
+        tab.setAttribute('role', 'tab');
+        tab.setAttribute('aria-selected', tab.classList.contains('active') ? 'true' : 'false');
+        tab.setAttribute('tabindex', '0');
+        
+        // Add keyboard support
+        tab.addEventListener('keydown', function(e) {
+            if (e.key === 'Enter' || e.key === ' ') {
+                e.preventDefault();
+                this.click();
+            }
+        });
+    });
+}
+
+/**
+ * Show file type content when tab is clicked
+ * @param {Event} evt - The click event
+ * @param {string} fileType - The file type to show
+ */
+function showFileType(evt, fileType) {
+    var contents = document.getElementsByClassName('section-content');
+    for (var i = 0; i < contents.length; i++) {
+        contents[i].style.display = 'none';
+        contents[i].classList.remove('active');
+    }
+    
+    var tabs = document.getElementsByClassName('file-type-tab');
+    for (var i = 0; i < tabs.length; i++) {
+        tabs[i].classList.remove('active');
+        tabs[i].setAttribute('aria-selected', 'false');
+    }
+    
+    var activeContent = document.getElementById(fileType);
+    if (activeContent) {
+        activeContent.style.display = 'grid';
+        activeContent.classList.add('active');
+        activeContent.style.maxHeight = activeContent.scrollHeight + 'px';
+    }
+    
+    if (evt && evt.currentTarget) {
+        evt.currentTarget.classList.add('active');
+        evt.currentTarget.setAttribute('aria-selected', 'true');
+    }
+}
+
+/**
+ * Search documentation
+ * @param {string} query - The search query
+ */
+function searchDocumentation(query) {
+    // This is a placeholder for future search functionality
+    console.log('Searching for:', query);
+    
+    // Hide all items that don't match the search
+    const cards = document.querySelectorAll('.doc-card');
+    let matchCount = 0;
+    
+    cards.forEach(card => {
+        const title = card.querySelector('h3').textContent.toLowerCase();
+        const description = card.querySelector('p').textContent.toLowerCase();
+        const searchText = query.toLowerCase();
+        
+        if (title.includes(searchText) || description.includes(searchText)) {
+            card.style.display = 'block';
+            matchCount++;
+        } else {
+            card.style.display = 'none';
+        }
+    });
+    
+    // Show a message if no results found
+    const noResultsMsg = document.getElementById('no-search-results');
+    if (noResultsMsg) {
+        noResultsMsg.style.display = matchCount === 0 ? 'block' : 'none';
+    }
+    
+    // Expand all sections when searching
+    if (query.length > 0) {
+        document.querySelectorAll('.section-toggle').forEach(toggle => {
+            toggle.classList.add('expanded');
+            toggle.setAttribute('aria-expanded', 'true');
+            
+            const section = toggle.closest('.doc-section');
+            const content = section.querySelector('.section-content');
+            if (content) {
+                content.classList.add('expanded');
+                content.style.maxHeight = content.scrollHeight + 'px';
+                content.setAttribute('aria-hidden', 'false');
+            }
+        });
+    }
+}

