+package Comserv::Util::DatabaseSchemaCache;
+
+use strict;
+use warnings;
+use Moose;
+use Try::Tiny;
+use Time::HiRes qw(time);
+use Data::Dumper;
+
+# Cache storage
+has 'cache' => (
+    is => 'rw',
+    isa => 'HashRef',
+    default => sub { {} }
+);
+
+# Cache timestamps
+has 'cache_timestamps' => (
+    is => 'rw',
+    isa => 'HashRef',
+    default => sub { {} }
+);
+
+# Cache TTL in seconds (default: 5 minutes)
+has 'cache_ttl' => (
+    is => 'rw',
+    isa => 'Int',
+    default => 300
+);
+
+# Singleton instance
+our $instance;
+
+sub instance {
+    my $class = shift;
+    return $instance ||= $class->new();
+}
+
+# Initialize cache at application startup
+sub initialize_cache {
+    my ($self, $c) = @_;
+    
+    my $logging = Comserv::Util::Logging->new();
+    $logging->log_with_details($c, 'info', __FILE__, __LINE__, 'DatabaseSchemaCache', 
+        "Initializing database schema cache at startup");
+    
+    # Initialize cache structure
+    $self->cache({
+        backends => {},
+        ency => {
+            connection_status => 'unknown',
+            table_list => [],
+            table_count => 0,
+            last_updated => 0
+        },
+        forager => {
+            connection_status => 'unknown',
+            table_list => [],
+            table_count => 0,
+            last_updated => 0
+        },
+        production_backend => undef,
+        last_backend_check => 0
+    });
+    
+    # Cache production backend information
+    $self->cache_production_backend($c);
+    
+    # Cache basic database connection info (but not detailed schemas yet)
+    $self->cache_basic_database_info($c);
+    
+    $logging->log_with_details($c, 'info', __FILE__, __LINE__, 'DatabaseSchemaCache', 
+        "Database schema cache initialization completed");
+}
+
+# Cache production backend information
+sub cache_production_backend {
+    my ($self, $c) = @_;
+    
+    my $logging = Comserv::Util::Logging->new();
+    
+    try {
+        my $hybrid_db = $c->model('HybridDB');
+        my $available_backends = $hybrid_db->get_available_backends() || {};
+        
+        # Find production backend (highest priority available backend)
+        my $production_backend;
+        my $highest_priority = 999;
+        
+        foreach my $backend_name (keys %$available_backends) {
+            my $backend_info = $available_backends->{$backend_name};
+            if ($backend_info->{available}) {
+                my $priority = $backend_info->{config}->{priority} || 999;
+                if ($priority < $highest_priority) {
+                    $highest_priority = $priority;
+                    $production_backend = $backend_name;
+                }
+            }
+        }
+        
+        $self->cache->{production_backend} = $production_backend;
+        $self->cache->{backends} = $available_backends;
+        $self->cache->{last_backend_check} = time();
+        
+        $logging->log_with_details($c, 'info', __FILE__, __LINE__, 'DatabaseSchemaCache', 
+            "Cached production backend: " . ($production_backend || 'none') . 
+            " from " . scalar(keys %$available_backends) . " available backends");
+            
+    } catch {
+        my $error = $_;
+        $logging->log_with_details($c, 'error', __FILE__, __LINE__, 'DatabaseSchemaCache', 
+            "Error caching production backend: $error");
+    };
+}
+
+# Cache basic database connection information
+sub cache_basic_database_info {
+    my ($self, $c) = @_;
+    
+    my $logging = Comserv::Util::Logging->new();
+    
+    # Cache ency database basic info
+    try {
+        my $admin_controller = $c->controller('Admin');
+        my $ency_tables = $admin_controller->get_ency_database_tables($c);
+        
+        $self->cache->{ency} = {
+            connection_status => 'connected',
+            table_list => $ency_tables,
+            table_count => scalar(@$ency_tables),
+            last_updated => time()
+        };
+        
+        $logging->log_with_details($c, 'info', __FILE__, __LINE__, 'DatabaseSchemaCache', 
+            "Cached ency database info: " . scalar(@$ency_tables) . " tables");
+            
+    } catch {
+        my $error = $_;
+        $self->cache->{ency}->{connection_status} = 'error';
+        $self->cache->{ency}->{error} = $error;
+        
+        $logging->log_with_details($c, 'error', __FILE__, __LINE__, 'DatabaseSchemaCache', 
+            "Error caching ency database info: $error");
+    };
+    
+    # Cache forager database basic info
+    try {
+        my $admin_controller = $c->controller('Admin');
+        my $forager_tables = $admin_controller->get_forager_database_tables($c);
+        
+        $self->cache->{forager} = {
+            connection_status => 'connected',
+            table_list => $forager_tables,
+            table_count => scalar(@$forager_tables),
+            last_updated => time()
+        };
+        
+        $logging->log_with_details($c, 'info', __FILE__, __LINE__, 'DatabaseSchemaCache', 
+            "Cached forager database info: " . scalar(@$forager_tables) . " tables");
+            
+    } catch {
+        my $error = $_;
+        $self->cache->{forager}->{connection_status} = 'error';
+        $self->cache->{forager}->{error} = $error;
+        
+        $logging->log_with_details($c, 'error', __FILE__, __LINE__, 'DatabaseSchemaCache', 
+            "Error caching forager database info: $error");
+    };
+}
+
+# Get cached database comparison (optimized version)
+sub get_cached_database_comparison {
+    my ($self, $c, $force_refresh) = @_;
+    
+    my $logging = Comserv::Util::Logging->new();
+    
+    # Check if cache needs refresh
+    if ($force_refresh || $self->_cache_needs_refresh()) {
+        $logging->log_with_details($c, 'info', __FILE__, __LINE__, 'DatabaseSchemaCache', 
+            "Cache refresh needed, updating backend information");
+        $self->cache_production_backend($c);
+    }
+    
+    # Build optimized comparison structure
+    my $comparison = {
+        backends => $self->cache->{backends} || {},
+        ency => $self->cache->{ency},
+        forager => $self->cache->{forager},
+        summary => {
+            total_databases => 2,
+            connected_databases => 0,
+            total_tables => 0,
+            tables_with_results => 0,
+            tables_without_results => 0,
+            results_without_tables => 0,
+            total_backends => scalar(keys %{$self->cache->{backends} || {}}),
+            available_backends => scalar(grep { 
+                $self->cache->{backends}->{$_}->{available} 
+            } keys %{$self->cache->{backends} || {}})
+        },
+        cache_info => {
+            last_updated => $self->cache->{last_backend_check},
+            cache_age => time() - $self->cache->{last_backend_check},
+            production_backend => $self->cache->{production_backend}
+        }
+    };
+    
+    # Update summary counts
+    if ($self->cache->{ency}->{connection_status} eq 'connected') {
+        $comparison->{summary}->{connected_databases}++;
+        $comparison->{summary}->{total_tables} += $self->cache->{ency}->{table_count};
+    }
+    
+    if ($self->cache->{forager}->{connection_status} eq 'connected') {
+        $comparison->{summary}->{connected_databases}++;
+        $comparison->{summary}->{total_tables} += $self->cache->{forager}->{table_count};
+    }
+    
+    $logging->log_with_details($c, 'info', __FILE__, __LINE__, 'DatabaseSchemaCache', 
+        "Returned cached database comparison with " . $comparison->{summary}->{total_tables} . 
+        " total tables from cache");
+    
+    return $comparison;
+}
+
+# Get detailed schema comparison for a specific database (lazy loading)
+sub get_detailed_schema_comparison {
+    my ($self, $c, $database_name) = @_;
+    
+    my $logging = Comserv::Util::Logging->new();
+    
+    $logging->log_with_details($c, 'info', __FILE__, __LINE__, 'DatabaseSchemaCache', 
+        "Loading detailed schema comparison for database: $database_name");
+    
+    # This method will load detailed table comparisons only when requested
+    # This is where the expensive operations from the original get_database_comparison 
+    # method would be moved to, but only executed on-demand
+    
+    my $admin_controller = $c->controller('Admin');
+    my $detailed_comparison = {};
+    
+    if ($database_name eq 'ency') {
+        try {
+            my $result_table_mapping = $admin_controller->build_result_table_mapping($c, 'ency');
+            my @table_comparisons = ();
+            
+            foreach my $table_name (@{$self->cache->{ency}->{table_list}}) {
+                my $table_comparison = $admin_controller->compare_table_with_result_file_v2($c, $table_name, 'ency', $result_table_mapping);
+                push @table_comparisons, $table_comparison;
+            }
+            
+            $detailed_comparison = {
+                table_comparisons => \@table_comparisons,
+                result_table_mapping => $result_table_mapping
+            };
+            
+        } catch {
+            my $error = $_;
+            $logging->log_with_details($c, 'error', __FILE__, __LINE__, 'DatabaseSchemaCache', 
+                "Error loading detailed ency schema: $error");
+            $detailed_comparison->{error} = $error;
+        };
+    }
+    elsif ($database_name eq 'forager') {
+        try {
+            my $result_table_mapping = $admin_controller->build_result_table_mapping($c, 'forager');
+            my @table_comparisons = ();
+            
+            foreach my $table_name (@{$self->cache->{forager}->{table_list}}) {
+                my $table_comparison = $admin_controller->compare_table_with_result_file_v2($c, $table_name, 'forager', $result_table_mapping);
+                push @table_comparisons, $table_comparison;
+            }
+            
+            $detailed_comparison = {
+                table_comparisons => \@table_comparisons,
+                result_table_mapping => $result_table_mapping
+            };
+            
+        } catch {
+            my $error = $_;
+            $logging->log_with_details($c, 'error', __FILE__, __LINE__, 'DatabaseSchemaCache', 
+                "Error loading detailed forager schema: $error");
+            $detailed_comparison->{error} = $error;
+        };
+    }
+    
+    return $detailed_comparison;
+}
+
+# Check if cache needs refresh
+sub _cache_needs_refresh {
+    my ($self) = @_;
+    
+    my $last_check = $self->cache->{last_backend_check} || 0;
+    my $cache_age = time() - $last_check;
+    
+    return $cache_age > $self->cache_ttl;
+}
+
+# Refresh cache manually
+sub refresh_cache {
+    my ($self, $c) = @_;
+    
+    my $logging = Comserv::Util::Logging->new();
+    $logging->log_with_details($c, 'info', __FILE__, __LINE__, 'DatabaseSchemaCache', 
+        "Manual cache refresh requested");
+    
+    $self->cache_production_backend($c);
+    $self->cache_basic_database_info($c);
+}
+
+# Get cache status for admin monitoring
+sub get_cache_status {
+    my ($self) = @_;
+    
+    return {
+        cache_age => time() - ($self->cache->{last_backend_check} || 0),
+        cache_ttl => $self->cache_ttl,
+        needs_refresh => $self->_cache_needs_refresh(),
+        production_backend => $self->cache->{production_backend},
+        ency_status => $self->cache->{ency}->{connection_status},
+        forager_status => $self->cache->{forager}->{connection_status},
+        ency_table_count => $self->cache->{ency}->{table_count},
+        forager_table_count => $self->cache->{forager}->{table_count}
+    };
+}
+
+__PACKAGE__->meta->make_immutable;
+
+1;

