+# Load detailed schema comparison for a specific database (AJAX endpoint)
+sub schema_compare_details :Path('/admin/schema_compare_details') :Args(1) {
+    my ($self, $c, $database_name) = @_;
+    
+    $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'schema_compare_details', 
+        "Loading detailed schema for database: $database_name");
+    
+    # Check if the user has admin role
+    unless ($c->user_exists && $c->check_user_roles('admin')) {
+        $c->response->status(403);
+        $c->response->body('Access denied');
+        return;
+    }
+    
+    # Validate database name
+    unless ($database_name && ($database_name eq 'ency' || $database_name eq 'forager')) {
+        $c->response->status(400);
+        $c->response->body('Invalid database name');
+        return;
+    }
+    
+    # Get detailed schema comparison from cache
+    require Comserv::Util::DatabaseSchemaCache;
+    my $cache = Comserv::Util::DatabaseSchemaCache->instance();
+    my $detailed_comparison = $cache->get_detailed_schema_comparison($c, $database_name);
+    
+    # Return JSON response
+    $c->response->content_type('application/json');
+    $c->response->body($c->model('JSON')->encode($detailed_comparison));
+    
+    $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'schema_compare_details', 
+        "Completed detailed schema load for database: $database_name");
+}
+
+# Refresh database schema cache (admin endpoint)
+sub schema_cache_refresh :Path('/admin/schema_cache_refresh') :Args(0) {
+    my ($self, $c) = @_;
+    
+    $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'schema_cache_refresh', 
+        "Manual cache refresh requested");
+    
+    # Check if the user has admin role
+    unless ($c->user_exists && $c->check_user_roles('admin')) {
+        $c->flash->{error_msg} = "You need to be an administrator to refresh the cache.";
+        $c->response->redirect($c->uri_for('/admin/schema_compare'));
+        return;
+    }
+    
+    # Refresh the cache
+    require Comserv::Util::DatabaseSchemaCache;
+    my $cache = Comserv::Util::DatabaseSchemaCache->instance();
+    $cache->refresh_cache($c);
+    
+    $c->flash->{success_msg} = "Database schema cache has been refreshed successfully.";
+    $c->response->redirect($c->uri_for('/admin/schema_compare'));
+    
+    $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'schema_cache_refresh', 
+        "Cache refresh completed");
+}
