package Comserv::Util::DatabaseSchemaCache;

use strict;
use warnings;
use Moose;
use Try::Tiny;
use Time::HiRes qw(time);
use Data::Dumper;

# Cache storage
has 'cache' => (
    is => 'rw',
    isa => 'HashRef',
    default => sub { {} }
);

# Cache timestamps
has 'cache_timestamps' => (
    is => 'rw',
    isa => 'HashRef',
    default => sub { {} }
);

# Cache TTL in seconds (default: 5 minutes)
has 'cache_ttl' => (
    is => 'rw',
