/* ===== UNIVERSAL COLLAPSIBLE TOGGLE SYSTEM ===== */
/* Added 2025-01-20 - Universal toggle icons for all themes */

/* Collapsible Section Styles - Universal across all themes */
.section {
    margin-bottom: 20px;
}

.section-header {
    cursor: pointer;
    padding: 10px 15px;
    background: var(--card-bg);
    border: 1px solid var(--border-color);
    border-radius: 8px 8px 0 0;
    transition: background-color 0.3s ease;
}

.section-header:hover {
    background: var(--table-header-bg);
}

.section-header h2,
.section-header h3 {
    margin: 0;
    display: flex;
    align-items: center;
    justify-content: space-between;
}

/* Simple Toggle Icon System - No FontAwesome dependency */
.toggle-icon {
    font-size: 1.2em;
    transition: all 0.3s ease;
    display: inline-block !important;
    color: #007bff !important;
    margin-left: 10px;
    font-weight: bold;
    cursor: pointer;
}

.toggle-icon::before {
    content: "‚ñº";
    font-family: Arial, sans-serif;
}

.toggle-icon.expanded::before {
    content: "‚ñ≤";
}

.section-content {
    border: 1px solid var(--border-color);
    border-top: none;
    border-radius: 0 0 8px 8px;
    padding: 15px;
    background: var(--bg-color);
}

.collapsible-content {
    transition: all 0.3s ease;
    overflow: hidden;
}

/* Collapsible Header Styles */
.collapsible-header {
    cursor: pointer;
    user-select: none;
}

.collapsible-header:hover .toggle-icon {
    color: var(--accent-color);
}

/* Mailing List System Styles */
.newsletter-signup-banner {
    background: var(--primary-color);
    color: white;
    padding: 15px 20px;
    position: relative;
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 20px;
    border-radius: 8px;
    box-shadow: 0 2px 8px rgba(0,0,0,0.1);
}

.newsletter-content {
    flex: 1;
}

.newsletter-content h3 {
    margin: 0 0 5px 0;
    font-size: 1.2rem;
    font-weight: bold;
}

.newsletter-content p {
    margin: 0 0 10px 0;
    font-size: 0.9rem;
    opacity: 0.9;
}

.newsletter-form {
    display: flex;
    gap: 10px;
    align-items: center;
    margin-bottom: 5px;
}

.newsletter-form input[type="email"] {
    padding: 8px 12px;
    border: none;
    border-radius: 4px;
    font-size: 0.9rem;
    min-width: 250px;
}

.newsletter-form .btn {
    padding: 8px 16px;
    font-size: 0.9rem;
    white-space: nowrap;
}

.newsletter-close {
    background: none;
    border: none;
    color: white;
    font-size: 1.5rem;
    cursor: pointer;
    padding: 5px;
    line-height: 1;
}

.newsletter-close:hover {
    opacity: 0.7;
}

/* Newsletter Modal Styles */
.modal {
    display: none;
    position: fixed;
    z-index: 1000;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    backdrop-filter: blur(2px);
}

.modal-content {
    background-color: var(--background-color);
    margin: 10% auto;
    padding: 0;
    border: 1px solid var(--border-color);
    border-radius: 8px;
    width: 90%;
    max-width: 500px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.3);
    animation: modalSlideIn 0.3s ease-out;
}

@keyframes modalSlideIn {
    from {
        opacity: 0;
        transform: translateY(-50px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.modal-header {
    background: var(--primary-color);
    color: white;
    padding: 20px;
    border-radius: 8px 8px 0 0;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.modal-header h2 {
    margin: 0;
    font-size: 1.5rem;
    font-weight: 600;
}

.modal-header .close {
    background: none;
    border: none;
    color: white;
    font-size: 1.8rem;
    cursor: pointer;
    padding: 0;
    line-height: 1;
    width: 30px;
    height: 30px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    transition: background-color 0.3s ease;
}

.modal-header .close:hover {
    background-color: rgba(255, 255, 255, 0.2);
}

.modal-body {
    padding: 25px;
    color: var(--text-color);
}

.modal-body p {
    margin: 0 0 20px 0;
    font-size: 1rem;
    line-height: 1.5;
}

.newsletter-modal-form .form-group {
    margin-bottom: 20px;
}

.newsletter-modal-form label {
    display: block;
    margin-bottom: 8px;
    font-weight: 600;
    color: var(--text-color);
    font-size: 0.95rem;
}

.newsletter-modal-form input[type="email"] {
    width: 100%;
    padding: 12px 15px;
    border: 2px solid var(--border-color);
    border-radius: 6px;
    background: var(--background-color);
    color: var(--text-color);
    font-size: 1rem;
    transition: border-color 0.3s ease;
    box-sizing: border-box;
}

.newsletter-modal-form input[type="email"]:focus {
    outline: none;
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(var(--primary-color-rgb), 0.1);
}

.form-actions {
    display: flex;
    gap: 12px;
    justify-content: flex-end;
    margin-top: 25px;
}

.form-actions .btn {
    padding: 10px 20px;
    border: none;
    border-radius: 6px;
    font-size: 0.95rem;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    text-decoration: none;
    display: inline-flex;
    align-items: center;
    justify-content: center;
}

.form-actions .btn-primary {
    background: var(--primary-color);
    color: white;
}

.form-actions .btn-primary:hover {
    background: var(--link-hover-color);
    transform: translateY(-1px);
}

.form-actions .btn-secondary {
    background: var(--secondary-color);
    color: var(--text-color);
    border: 1px solid var(--border-color);
}

.form-actions .btn-secondary:hover {
    background: var(--table-header-bg);
}

.signup-note {
    margin-top: 20px;
    padding: 15px;
    background: var(--table-header-bg);
    border-radius: 6px;
    border-left: 4px solid var(--accent-color);
}

.signup-note small {
    color: var(--text-color);
    opacity: 0.8;
    font-size: 0.9rem;
}

.signup-note a {
    color: var(--link-color);
    text-decoration: none;
    font-weight: 500;
}

.signup-note a:hover {
    color: var(--link-hover-color);
    text-decoration: underline;
}

/* Responsive Modal Design */
@media (max-width: 768px) {
    .modal-content {
        margin: 5% auto;
        width: 95%;
        max-width: none;
    }

    .modal-header {
        padding: 15px;
    }

    .modal-header h2 {
        font-size: 1.3rem;
    }

    .modal-body {
        padding: 20px;
    }

    .form-actions {
        flex-direction: column;
    }

    .form-actions .btn {
        width: 100%;
        justify-content: center;
    }

    .newsletter-form {
        flex-direction: column;
        align-items: stretch;
    }

    .newsletter-form input[type="email"] {
        min-width: auto;
        margin-bottom: 10px;
    }
}

@media (max-width: 480px) {
    .modal-content {
        margin: 2% auto;
    }

    .newsletter-signup-banner {
        flex-direction: column;
        text-align: center;
        gap: 15px;
    }

    .newsletter-close {
        position: absolute;
        top: 10px;
        right: 10px;
    }
}

