/* Apiary Management System Theme */
/* File: apiary.css,v 1.0 2025/01/27 shanta Exp shanta */

/* Color Variables */
:root {
    --apiary-primary: #f4a261;      /* Honey orange */
    --apiary-secondary: #e76f51;    /* Warm red */
    --apiary-accent: #2a9d8f;       /* Teal green */
    --apiary-dark: #264653;         /* Dark green */
    --apiary-light: #f1faee;        /* Light cream */
    --apiary-warning: #e9c46a;      /* Golden yellow */
    --apiary-success: #2a9d8f;      /* Success green */
    --apiary-danger: #e76f51;       /* Danger red */
    --apiary-info: #219ebc;         /* Info blue */

    /* Bee-themed colors */
    --bee-yellow: #ffd60a;
    --bee-black: #003566;
    --hive-brown: #8b5a3c;
    --honey-gold: #ffb700;
    --pollen-orange: #ff8500;
}

/* Container Styles */
.apiary-container {
    max-width: 1400px;
    margin: 0 auto;
    padding: 20px;
    background: linear-gradient(135deg, var(--apiary-light) 0%, #ffffff 100%);
    min-height: calc(100vh - 200px);
}

/* Header Styles */
.apiary-header {
    background: linear-gradient(135deg, var(--apiary-primary) 0%, var(--apiary-secondary) 100%);
    color: white;
    padding: 30px;
    border-radius: 12px;
    margin-bottom: 30px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}

.apiary-header h1,
.apiary-header h2 {
    margin: 0 0 15px 0;
    font-size: 2.5rem;
    font-weight: 700;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
}

.apiary-header h1 i,
.apiary-header h2 i {
    color: var(--bee-yellow);
    margin-right: 15px;
    text-shadow: 1px 1px 2px rgba(0,0,0,0.5);
}

.apiary-context {
    display: flex;
    flex-wrap: wrap;
    gap: 20px;
    font-size: 1rem;
}

.context-item {
    background: rgba(255,255,255,0.2);
    padding: 8px 15px;
    border-radius: 20px;
    backdrop-filter: blur(10px);
}

.status-active {
    color: var(--bee-yellow);
    font-weight: bold;
}

/* Dashboard Stats */
.dashboard-stats {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 20px;
    margin-bottom: 30px;
}

.stat-card {
    background: white;
    border-radius: 12px;
    padding: 25px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
    border-left: 5px solid var(--apiary-primary);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.stat-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
}

.stat-card.hives {
    border-left-color: var(--apiary-primary);
}

.stat-card.inspections {
    border-left-color: var(--apiary-accent);
}

.stat-card.todos {
    border-left-color: var(--apiary-warning);
}

.stat-card.inventory {
    border-left-color: var(--apiary-secondary);
}

.stat-card .stat-icon {
    font-size: 2.5rem;
    color: var(--apiary-primary);
    margin-bottom: 15px;
}

.stat-card.inspections .stat-icon {
    color: var(--apiary-accent);
}

.stat-card.todos .stat-icon {
    color: var(--apiary-warning);
}

.stat-card.inventory .stat-icon {
    color: var(--apiary-secondary);
}

.stat-content {
    display: flex;
    flex-direction: column;
}

.stat-number {
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--apiary-dark);
    line-height: 1;
}

.stat-label {
    font-size: 1rem;
    color: #666;
    margin-top: 5px;
}

.stat-detail {
    font-size: 0.9rem;
    color: #888;
    margin-top: 3px;
}

/* Quick Actions */
.quick-actions {
    background: white;
    border-radius: 12px;
    padding: 25px;
    margin-bottom: 30px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
}

.quick-actions h3 {
    margin: 0 0 20px 0;
    color: var(--apiary-dark);
    font-size: 1.5rem;
}

.quick-actions h3 i {
    color: var(--bee-yellow);
    margin-right: 10px;
}

.action-buttons {
    display: flex;
    flex-wrap: wrap;
    gap: 15px;
}

/* Feature Grid */
.feature-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: 25px;
    margin-bottom: 30px;
}

.feature-card {
    background: white;
    border-radius: 12px;
    overflow: hidden;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    border-top: 4px solid var(--apiary-primary);
}

.feature-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
}

.feature-card.readonly {
    border-top-color: #ccc;
    opacity: 0.8;
}

.feature-header {
    background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
    padding: 20px;
    border-bottom: 1px solid #dee2e6;
}

.feature-header h3, .feature-header h4 {
    margin: 0;
    color: var(--apiary-dark);
    font-size: 1.3rem;
    display: flex;
    align-items: center;
}

.feature-icon {
    font-size: 1.5rem;
    color: var(--apiary-primary);
    margin-right: 12px;
}

.feature-content {
    padding: 20px;
}

.feature-content p {
    margin: 0 0 15px 0;
    color: #666;
    line-height: 1.6;
}

.feature-list {
    list-style: none;
    padding: 0;
    margin: 0;
}

.feature-list li {
    padding: 5px 0;
    color: #555;
    position: relative;
    padding-left: 20px;
}

.feature-list li:before {
    content: "";
    position: absolute;
    left: 0;
    top: 5px;
}

.feature-actions {
    padding: 20px;
    background: #f8f9fa;
    border-top: 1px solid #dee2e6;
    display: flex;
    gap: 10px;
    flex-wrap: wrap;
}

/* Button Styles */
.btn {
    display: inline-flex;
    align-items: center;
    padding: 10px 20px;
    border: none;
    border-radius: 8px;
    text-decoration: none;
    font-weight: 500;
    transition: all 0.3s ease;
    cursor: pointer;
    font-size: 0.95rem;
}

.btn i {
    margin-right: 8px;
}

.btn-primary {
    background: var(--apiary-primary);
    color: white;
}

.btn-primary:hover {
    background: #e8956b;
    transform: translateY(-2px);
}

.btn-secondary {
    background: var(--apiary-accent);
    color: white;
}

.btn-secondary:hover {
    background: #238a7a;
    transform: translateY(-2px);
}

.btn-success {
    background: var(--apiary-success);
    color: white;
}

.btn-success:hover {
    background: #238a7a;
    transform: translateY(-2px);
}

.btn-info {
    background: var(--apiary-info);
    color: white;
}

.btn-info:hover {
    background: #1a7a96;
    transform: translateY(-2px);
}

.btn-warning {
    background: var(--apiary-warning);
    color: var(--apiary-dark);
}

.btn-warning:hover {
    background: #e6b85c;
    transform: translateY(-2px);
}

.btn-outline {
    background: transparent;
    color: var(--apiary-primary);
    border: 2px solid var(--apiary-primary);
}

.btn-outline:hover {
    background: var(--apiary-primary);
    color: white;
}

.btn-sm {
    padding: 8px 16px;
    font-size: 0.9rem;
}

/* Alert Styles */
.alert {
    padding: 15px 20px;
    border-radius: 8px;
    margin-bottom: 20px;
    border-left: 4px solid;
}

.alert-info {
    background: #e7f3ff;
    border-left-color: var(--apiary-info);
    color: #0c5460;
}

.alert-warning {
    background: #fff3cd;
    border-left-color: var(--apiary-warning);
    color: #856404;
}

.alert h3 {
    margin: 0 0 10px 0;
    font-size: 1.2rem;
}

/* Public View Styles */
.public-view {
    text-align: center;
    max-width: 800px;
    margin: 0 auto;
}

.welcome-section h2 {
    color: var(--apiary-dark);
    font-size: 2.2rem;
    margin-bottom: 20px;
}

.welcome-section h2 i {
    color: var(--bee-yellow);
    margin-right: 15px;
}

.info-cards {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 20px;
    margin: 30px 0;
}

.info-card {
    background: white;
    padding: 25px;
    border-radius: 12px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
    text-align: center;
}

.info-icon {
    font-size: 3rem;
    color: var(--apiary-primary);
    margin-bottom: 15px;
}

.info-card h3 {
    color: var(--apiary-dark);
    margin: 0 0 15px 0;
}

.access-section {
    background: white;
    padding: 30px;
    border-radius: 12px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
    margin-top: 30px;
}

.access-buttons {
    display: flex;
    justify-content: center;
    gap: 15px;
    margin-top: 20px;
}

/* Admin Section */
.admin-section {
    background: white;
    border-radius: 12px;
    padding: 25px;
    margin-top: 30px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
    border-top: 4px solid var(--apiary-dark);
}

.admin-section h3 {
    color: var(--apiary-dark);
    margin: 0 0 20px 0;
    font-size: 1.5rem;
}

.admin-section h3 i {
    color: var(--apiary-primary);
    margin-right: 10px;
}

.admin-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 20px;
}

.admin-card {
    background: #f8f9fa;
    padding: 20px;
    border-radius: 8px;
    border-left: 4px solid var(--apiary-primary);
}

.admin-card h4 {
    margin: 0 0 15px 0;
    color: var(--apiary-dark);
}

.admin-card ul {
    list-style: none;
    padding: 0;
    margin: 0;
}

.admin-card li {
    padding: 5px 0;
}

.admin-card a {
    color: var(--apiary-accent);
    text-decoration: none;
    transition: color 0.3s ease;
}

.admin-card a:hover {
    color: var(--apiary-primary);
}

/* Debug Styles */
.debug-messages {
    background: #f8f9fa;
    border: 1px solid #dee2e6;
    border-radius: 8px;
    padding: 15px;
    margin-bottom: 20px;
    font-family: monospace;
}

.debug {
    color: #6c757d;
    font-size: 0.9rem;
    margin: 5px 0;
}

/* Responsive Design */
@media (max-width: 768px) {
    .apiary-container {
        padding: 15px;
    }

    .apiary-header {
        padding: 20px;
        text-align: center;
    }

    .apiary-header h1 {
        font-size: 2rem;
    }

    .apiary-context {
        justify-content: center;
    }

    .dashboard-stats {
        grid-template-columns: 1fr;
    }

    .feature-grid {
        grid-template-columns: 1fr;
    }

    .action-buttons {
        justify-content: center;
    }

    .access-buttons {
        flex-direction: column;
        align-items: center;
    }

    .admin-grid {
        grid-template-columns: 1fr;
    }
}

@media (max-width: 480px) {
    .apiary-header h1 {
        font-size: 1.8rem;
    }

    .stat-number {
        font-size: 2rem;
    }

    .feature-actions {
        flex-direction: column;
    }

    .btn {
        justify-content: center;
        width: 100%;
    }
}

/* Graph Container Styles */
.graph-container {
    border: 1px solid var(--apiary-dark);
    padding: 10px;
    margin: 10px;
    position: static;
    z-index: auto;
    background: white;
    border-radius: 8px;
    box-shadow: 0 2px 8px rgba(0,0,0,0.1);
}

.graph-container iframe {
    width: 100%;
    height: 600px;
    border: none;
    border-radius: 4px;
}

.no-data-message {
    text-align: center;
    padding: 40px 20px;
    background: linear-gradient(135deg, var(--apiary-light) 0%, #ffffff 100%);
    border-radius: 8px;
    border: 2px dashed var(--apiary-primary);
}

.no-data-message a {
    display: inline-block;
    padding: 15px 30px;
    background: var(--apiary-primary);
    color: white;
    text-decoration: none;
    border-radius: 8px;
    font-weight: 500;
    transition: all 0.3s ease;
}

.no-data-message a:hover {
    background: var(--apiary-secondary);
    transform: translateY(-2px);
}

/* Animation for loading states */
@keyframes pulse {
    0% { opacity: 1; }
    50% { opacity: 0.5; }
    100% { opacity: 1; }
}

.loading {
    animation: pulse 1.5s ease-in-out infinite;
}

/* Migration Planning Styles */
.migration-planning {
    background: white;
    border-radius: 12px;
    padding: 30px;
    margin: 30px 0;
