    font-size: 1.3rem;
    display: flex;
    align-items: center;
}

.feature-icon {
    font-size: 1.5rem;
    color: var(--apiary-primary);
    margin-right: 12px;
}

.feature-content {
    padding: 20px;
}

.feature-content p {
    margin: 0 0 15px 0;
    color: #666;
    line-height: 1.6;
}

.feature-list {
    list-style: none;
    padding: 0;
    margin: 0;
}

.feature-list li {
    padding: 5px 0;
    color: #555;
    position: relative;
    padding-left: 20px;
}

.feature-list li:before {
    content: "";
    position: absolute;
    left: 0;
    top: 5px;
}

.feature-actions {
    padding: 20px;
    background: #f8f9fa;
    border-top: 1px solid #dee2e6;
    display: flex;
    gap: 10px;
    flex-wrap: wrap;
}

/* Button Styles */
.btn {
    display: inline-flex;
    align-items: center;
    padding: 10px 20px;
    border: none;
    border-radius: 8px;
    text-decoration: none;
    font-weight: 500;
    transition: all 0.3s ease;
    cursor: pointer;
    font-size: 0.95rem;
}

.btn i {
    margin-right: 8px;
}

.btn-primary {
    background: var(--apiary-primary);
    color: white;
}

.btn-primary:hover {
    background: #e8956b;
    transform: translateY(-2px);
}

.btn-secondary {
    background: var(--apiary-accent);
    color: white;
}

.btn-secondary:hover {
    background: #238a7a;
    transform: translateY(-2px);
}

.btn-success {
    background: var(--apiary-success);
    color: white;
}

.btn-success:hover {
    background: #238a7a;
    transform: translateY(-2px);
}

.btn-info {
    background: var(--apiary-info);
    color: white;
}

.btn-info:hover {
    background: #1a7a96;
    transform: translateY(-2px);
}

.btn-warning {
    background: var(--apiary-warning);
    color: var(--apiary-dark);
}

.btn-warning:hover {
    background: #e6b85c;
    transform: translateY(-2px);
}

.btn-outline {
    background: transparent;
    color: var(--apiary-primary);
    border: 2px solid var(--apiary-primary);
}

.btn-outline:hover {
    background: var(--apiary-primary);
    color: white;
}

.btn-sm {
    padding: 8px 16px;
    font-size: 0.9rem;
}

/* Alert Styles */
.alert {
    padding: 15px 20px;
    border-radius: 8px;
    margin-bottom: 20px;
    border-left: 4px solid;
}

.alert-info {
    background: #e7f3ff;
