/* Public View Styles */
.public-view {
    text-align: center;
    max-width: 800px;
    margin: 0 auto;
}

.welcome-section h2 {
    color: var(--apiary-dark);
    font-size: 2.2rem;
    margin-bottom: 20px;
}

