}

/* Admin Section */
.admin-section {
    background: white;
    border-radius: 12px;
    padding: 25px;
    margin-top: 30px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
    border-top: 4px solid var(--apiary-dark);
}

.admin-section h3 {
    color: var(--apiary-dark);
    margin: 0 0 20px 0;
    font-size: 1.5rem;
}

.admin-section h3 i {
    color: var(--apiary-primary);
    margin-right: 10px;
}

.admin-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 20px;
}

.admin-card {
    background: #f8f9fa;
    padding: 20px;
    border-radius: 8px;
    border-left: 4px solid var(--apiary-primary);
}

.admin-card h4 {
    margin: 0 0 15px 0;
    color: var(--apiary-dark);
}

.admin-card ul {
    list-style: none;
    padding: 0;
    margin: 0;
}

.admin-card li {
    padding: 5px 0;
}

.admin-card a {
    color: var(--apiary-accent);
    text-decoration: none;
    transition: color 0.3s ease;
}

.admin-card a:hover {
