}

/* Debug Styles */
.debug-messages {
    background: #f8f9fa;
    border: 1px solid #dee2e6;
    border-radius: 8px;
    padding: 15px;
    margin-bottom: 20px;
    font-family: monospace;
}

.debug {
    color: #6c757d;
    font-size: 0.9rem;
    margin: 5px 0;
}

/* Responsive Design */
@media (max-width: 768px) {
    .apiary-container {
        padding: 15px;
    }

    .apiary-header {
        padding: 20px;
        text-align: center;
    }

    .apiary-header h1 {
        font-size: 2rem;
    }

    .apiary-context {
        justify-content: center;
    }

    .dashboard-stats {
        grid-template-columns: 1fr;
    }

    .feature-grid {
        grid-template-columns: 1fr;
    }

    .action-buttons {
        justify-content: center;
    }

    .access-buttons {
        flex-direction: column;
        align-items: center;
    }

    .admin-grid {
        grid-template-columns: 1fr;
    }
}

@media (max-width: 480px) {
    .apiary-header h1 {
        font-size: 1.8rem;
    }

    .stat-number {
        font-size: 2rem;
    }

    .feature-actions {
        flex-direction: column;
    }

    .btn {
        justify-content: center;
        width: 100%;
    }
}

/* Graph Container Styles */
.graph-container {
    border: 1px solid var(--apiary-dark);
    padding: 10px;
    margin: 10px;
    position: static;
    z-index: auto;
    background: white;
    border-radius: 8px;
    box-shadow: 0 2px 8px rgba(0,0,0,0.1);
}

