}

.dev-tasks li.completed i {
    color: var(--apiary-success);
}

.dev-tasks li.in-progress {
    color: var(--apiary-warning);
}

.dev-tasks li.in-progress i {
    color: var(--apiary-warning);
    animation: spin 2s linear infinite;
}

.dev-tasks li.planned {
    color: #888;
}

.dev-tasks li.planned i {
    color: #ccc;
}

.dev-notes-list {
    list-style: none;
    padding: 0;
    margin: 0;
}

.dev-notes-list li {
    padding: 8px 0;
    color: #555;
    line-height: 1.5;
    border-bottom: 1px solid #eee;
}

.dev-notes-list li:last-child {
    border-bottom: none;
}

.dev-actions {
    padding: 15px 20px;
    background: white;
    border-top: 1px solid #dee2e6;
}

.dev-quick-actions {
    background: #f8f9fa;
    padding: 25px;
    border-radius: 8px;
    border-left: 4px solid var(--apiary-primary);
}

.dev-quick-actions h4 {
    margin: 0 0 20px 0;
    color: var(--apiary-dark);
    font-size: 1.3rem;
}

.dev-quick-actions h4 i {
    color: var(--apiary-primary);
    margin-right: 10px;
}

.dev-action-buttons {
    display: flex;
    flex-wrap: wrap;
    gap: 15px;
}

/* Spin animation for in-progress tasks */
@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

/* Focus styles for accessibility */
.btn:focus,
.admin-card a:focus {
    outline: 2px solid var(--apiary-primary);
    outline-offset: 2px;
}
