+}
+
+.form-help {
+    display: block;
+    margin-top: 0.25rem;
+    font-size: 0.9rem;
+    color: var(--text-muted, #666);
+}
+
+.checkbox-group {
+    display: flex;
+    align-items: flex-start;
+    gap: 0.5rem;
+}
+
+.checkbox-label {
+    display: flex;
+    align-items: flex-start;
+    gap: 0.5rem;
+    cursor: pointer;
+    font-weight: normal;
+    line-height: 1.4;
+}
+
+.checkbox-label input[type="checkbox"] {
+    width: auto;
+    margin: 0;
+}
+
+.form-actions {
+    display: flex;
+    gap: 1rem;
+    justify-content: center;
+    margin-top: 2rem;
+    flex-wrap: wrap;
+}
+
+.btn {
+    padding: 0.75rem 1.5rem;
+    border: none;
+    border-radius: 4px;
+    text-decoration: none;
+    font-weight: bold;
+    cursor: pointer;
+    transition: background-color 0.2s ease;
+    display: inline-block;
+    text-align: center;
+    font-size: 1rem;
+}
+
+.btn-primary {
+    background-color: var(--primary-color, #006633);
+    color: white;
+}
+
+.btn-primary:hover {
+    background-color: var(--primary-hover, #004d26);
+}
+
+.btn-secondary {
+    background-color: var(--secondary-color, #6c757d);
+    color: white;
+}
+
+.btn-secondary:hover {
+    background-color: var(--secondary-hover, #545b62);
+}
+
+.error-section {
+    background-color: #f8d7da;
+    border: 1px solid #f5c6cb;
+    border-radius: 4px;
+    padding: 1rem;
+    margin-bottom: 1.5rem;
+}
+
+.error-section h3 {
+    margin-top: 0;
+    color: #721c24;
+}
+
+.error-list {
+    margin: 0.5rem 0 0 0;
+    padding-left: 1.5rem;
+}
+
+.error-list li {
+    color: #721c24;
+    margin-bottom: 0.25rem;
+}
+
+.benefits-reminder {
+    background: linear-gradient(135deg, #f8fff8 0%, #e8f5e8 100%);
+    border-radius: 8px;
+    padding: 2rem;
+    margin-top: 2rem;
+}
+
+.benefits-summary {
+    display: grid;
+    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
+    gap: 1rem;
+    margin-top: 1rem;
+}
+
+.benefit-item {
+    text-align: center;
+    padding: 1rem;
+}
+
+.benefit-item h3 {
+    margin-top: 0;
+    color: var(--primary-color, #006633);
+    font-size: 1.1rem;
+}
+
+.benefit-item p {
+    margin-bottom: 0;
+    font-size: 0.9rem;
+    color: var(--text-muted, #666);
+}
+
+/* Responsive adjustments */
+@media (max-width: 768px) {
+    .form-actions {
+        flex-direction: column;
+    }
+    
+    .btn {
+        width: 100%;
+    }
+    
+    .benefits-summary {
+        grid-template-columns: 1fr;
+    }
+}
+</style>

