+  border: 1px solid #ddd;
+}
+
+.debug-footer details {
+  margin-bottom: 0;
+}
+
+.debug-footer summary {
+  cursor: pointer;
+  font-weight: bold;
+  padding: 5px 0;
+}
+
+.debug-footer ul {
+  margin-top: 10px;
+  padding-left: 20px;
+}
+
+.debug-footer li {
+  margin-bottom: 5px;
