--- Comserv/root/static/css/themes/admin-consolidated.css
+++ Comserv/root/static/css/themes/admin-consolidated.css
@@ -0,0 +1,760 @@
+/* 
+ * Consolidated Admin Theme CSS
+ * File: admin-consolidated.css
+ * Version: 1.0
+ * Date: 2025/01/27
+ * 
+ * This file contains ALL admin-related styles in one place:
+ * - Theme variables
+ * - Universal container styles
+ * - Admin-specific components
+ * - All page layouts
+ * 
+ * This approach provides:
+ * - Single file maintenance
+ * - Consistent styling across all admin pages
+ * - Easy theme management
+ * - Reduced HTTP requests
+ */
+
+/* ========================================
+   THEME VARIABLES
+   ======================================== */
+:root {
+  /* Admin Color Palette */
+  --primary-color: #007bff;
+  --secondary-color: #6c757d;
+  --success-color: #28a745;
+  --warning-color: #ffc107;
+  --danger-color: #dc3545;
+  --info-color: #17a2b8;
+  --light-color: #f8f9fa;
+  --dark-color: #343a40;
+  
+  /* Admin-specific colors */
+  --admin-bg: #ffffff;
+  --admin-text: #212529;
+  --admin-border: #dee2e6;
+  --admin-hover: #e9ecef;
+  --admin-accent: #007bff;
+  
+  /* Container colors */
+  --container-bg: var(--admin-bg);
+  --container-border: var(--admin-border);
+  --container-shadow: rgba(0, 0, 0, 0.1);
+  
+  /* Typography */
+  --font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
+  --font-size-base: 1rem;
+  --line-height-base: 1.5;
+  
+  /* Spacing */
+  --spacing-xs: 0.25rem;
+  --spacing-sm: 0.5rem;
+  --spacing-md: 1rem;
+  --spacing-lg: 1.5rem;
+  --spacing-xl: 3rem;
+  
+  /* Border radius */
+  --border-radius-sm: 0.25rem;
+  --border-radius: 0.375rem;
+  --border-radius-lg: 0.5rem;
+  
+  /* Shadows */
+  --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
+  --shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
+  --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
+}
+
+/* ========================================
+   UNIVERSAL CONTAINER SYSTEM
+   ======================================== */
+
+/* Main Container */
+.admin-container, .apiary-container {
+  max-width: 1400px;
+  margin: 0 auto;
+  padding: var(--spacing-lg);
+  background: var(--container-bg);
+  min-height: calc(100vh - 200px);
+  font-family: var(--font-family);
+}
+
+/* Header Styles */
+.admin-header, .apiary-header {
+  background: linear-gradient(135deg, var(--primary-color) 0%, var(--secondary-color) 100%);
+  color: white;
+  padding: var(--spacing-xl);
+  border-radius: var(--border-radius-lg);
+  margin-bottom: var(--spacing-xl);
+  box-shadow: var(--shadow-lg);
+}
+
+.admin-header h1, .admin-header h2,
+.apiary-header h1, .apiary-header h2 {
+  margin: 0 0 var(--spacing-lg) 0;
+  font-size: 2.5rem;
+  font-weight: 700;
+  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
+}
+
+.admin-header h1 i, .admin-header h2 i,
+.apiary-header h1 i, .apiary-header h2 i {
+  color: var(--warning-color);
+  margin-right: var(--spacing-lg);
+  text-shadow: 1px 1px 2px rgba(0,0,0,0.5);
+}
+
+/* Context Information */
+.admin-context, .apiary-context {
+  display: flex;
+  flex-wrap: wrap;
+  gap: var(--spacing-lg);
+  font-size: var(--font-size-base);
+}
+
+.context-item {
+  background: rgba(255,255,255,0.2);
+  padding: var(--spacing-sm) var(--spacing-lg);
+  border-radius: 20px;
+  backdrop-filter: blur(10px);
+  color: white;
+}
+
+.status-active {
+  color: var(--warning-color);
+  font-weight: bold;
+}
+
+/* ========================================
+   ADMIN SECTIONS & CARDS
+   ======================================== */
+
+/* Admin Menu Sections */
+.admin-menu {
+  display: grid;
+  gap: var(--spacing-xl);
+}
+
+.admin-section {
+  background: var(--container-bg);
+  border-radius: var(--border-radius-lg);
+  padding: var(--spacing-xl);
+  box-shadow: var(--shadow);
+  border-left: 4px solid var(--primary-color);
+  transition: transform 0.3s ease, box-shadow 0.3s ease;
+}
+
+.admin-section:hover {
+  transform: translateY(-2px);
+  box-shadow: var(--shadow-lg);
+}
+
+.admin-section h2, .admin-section h3 {
+  color: var(--admin-text);
+  margin: 0 0 var(--spacing-lg) 0;
+  font-size: 1.5rem;
+  display: flex;
+  align-items: center;
+}
+
+.admin-section h3 i {
+  color: var(--primary-color);
+  margin-right: var(--spacing-md);
+  font-size: 1.2em;
+}
+
+.admin-section ul {
+  list-style: none;
+  padding: 0;
+  margin: 0;
+}
+
+.admin-section li {
+  padding: var(--spacing-sm) 0;
+  border-bottom: 1px solid var(--admin-border);
+}
+
+.admin-section li:last-child {
+  border-bottom: none;
+}
+
+.admin-section a {
+  color: var(--primary-color);
+  text-decoration: none;
+  font-weight: 500;
+  transition: color 0.3s ease;
+  display: flex;
+  align-items: center;
+  padding: var(--spacing-sm) var(--spacing-md);
+  border-radius: var(--border-radius);
+}
+
+.admin-section a:hover {
+  color: var(--primary-color);
+  background: var(--admin-hover);
+  text-decoration: none;
+}
+
+.admin-section a i {
+  margin-right: var(--spacing-sm);
+  width: 1.2em;
+  text-align: center;
+}
+
+/* Special highlighting for Production Deployment */
+.admin-section:has(h3:contains("Production Deployment")),
+.admin-section:nth-child(4) {
+  border-left-color: var(--warning-color);
+  background: linear-gradient(135deg, #fff3cd 0%, #ffffff 100%);
+}
+
+.admin-section:has(h3:contains("Production Deployment")) h3,
+.admin-section:nth-child(4) h3 {
+  color: #856404;
+}
+
+/* ========================================
+   DASHBOARD COMPONENTS
+   ======================================== */
+
+/* Dashboard Stats */
+.dashboard-stats {
+  display: grid;
+  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
+  gap: var(--spacing-lg);
+  margin-bottom: var(--spacing-xl);
+}
+
+.stat-card {
+  background: var(--container-bg);
+  border-radius: var(--border-radius-lg);
+  padding: var(--spacing-xl);
+  box-shadow: var(--shadow);
+  border-left: 5px solid var(--primary-color);
+  transition: transform 0.3s ease, box-shadow 0.3s ease;
+}
+
+.stat-card:hover {
+  transform: translateY(-5px);
+  box-shadow: var(--shadow-lg);
+}
+
+.stat-card.databases { border-left-color: var(--primary-color); }
+.stat-card.connected { border-left-color: var(--success-color); }
+.stat-card.tables { border-left-color: var(--info-color); }
+.stat-card.results { border-left-color: var(--success-color); }
+.stat-card.hives { border-left-color: var(--primary-color); }
+.stat-card.inspections { border-left-color: var(--info-color); }
+.stat-card.todos { border-left-color: var(--warning-color); }
+.stat-card.inventory { border-left-color: var(--secondary-color); }
+
+.stat-card .stat-icon {
+  font-size: 2.5rem;
+  margin-bottom: var(--spacing-lg);
+}
+
+.stat-card.databases .stat-icon { color: var(--primary-color); }
+.stat-card.connected .stat-icon { color: var(--success-color); }
+.stat-card.tables .stat-icon { color: var(--info-color); }
+.stat-card.results .stat-icon { color: var(--success-color); }
+.stat-card.hives .stat-icon { color: var(--primary-color); }
+.stat-card.inspections .stat-icon { color: var(--info-color); }
+.stat-card.todos .stat-icon { color: var(--warning-color); }
+.stat-card.inventory .stat-icon { color: var(--secondary-color); }
+
+.stat-content {
+  display: flex;
+  flex-direction: column;
+}
+
+.stat-number {
+  font-size: 2.5rem;
+  font-weight: 700;
+  color: var(--admin-text);
+  line-height: 1;
+}
+
+.stat-label {
+  font-size: var(--font-size-base);
+  color: #666;
+  margin-top: var(--spacing-xs);
+}
+
+.stat-detail {
+  font-size: 0.9rem;
+  color: #888;
+  margin-top: var(--spacing-xs);
+}
+
+/* ========================================
+   FEATURE GRID & CARDS
+   ======================================== */
+
+.feature-grid {
+  display: grid;
+  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
+  gap: var(--spacing-xl);
+  margin-bottom: var(--spacing-xl);
+}
+
+.feature-card {
+  background: var(--container-bg);
+  border-radius: var(--border-radius-lg);
+  overflow: hidden;
+  box-shadow: var(--shadow);
+  transition: transform 0.3s ease, box-shadow 0.3s ease;
+  border-top: 4px solid var(--primary-color);
+}
+
+.feature-card:hover {
+  transform: translateY(-5px);
+  box-shadow: var(--shadow-lg);
+}
+
+.feature-card.readonly {
+  border-top-color: #ccc;
+  opacity: 0.8;
+}
+
+.feature-header {
+  background: linear-gradient(135deg, var(--light-color) 0%);
+  padding: var(--spacing-lg);
+  border-bottom: 1px solid var(--admin-border);
+}
+
+.feature-header h3, .feature-header h4 {
+  margin: 0;
+  color: var(--admin-text);
+  font-size: 1.3rem;
+  display: flex;
+  align-items: center;
+}
+
+.feature-icon {
+  font-size: 1.5rem;
+  color: var(--primary-color);
+  margin-right: var(--spacing-md);
+}
+
+.feature-content {
+  padding: var(--spacing-lg);
+}
+
+.feature-content p {
+  margin: 0 0 var(--spacing-lg) 0;
+  color: var(--admin-text);
+  line-height: var(--line-height-base);
+}
+
+.feature-list {
+  list-style: none;
+  padding: 0;
+  margin: 0;
+}
+
+.feature-list li {
+  padding: var(--spacing-xs) 0;
+  color: var(--admin-text);
+  position: relative;
+  padding-left: var(--spacing-lg);
+}
+
+.feature-list li:before {
+  content: "•";
+  position: absolute;
+  left: 0;
+  top: var(--spacing-xs);
+  color: var(--primary-color);
+}
+
+.feature-actions {
+  padding: var(--spacing-lg);
+  background: var(--light-color);
+  border-top: 1px solid var(--admin-border);
+  display: flex;
+  gap: var(--spacing-md);
+  flex-wrap: wrap;
+}
+
+/* ========================================
+   BUTTONS & INTERACTIVE ELEMENTS
+   ======================================== */
+
+.btn {
+  display: inline-flex;
+  align-items: center;
+  padding: var(--spacing-md) var(--spacing-lg);
+  border: none;
+  border-radius: var(--border-radius);
+  text-decoration: none;
+  font-weight: 500;
+  transition: all 0.3s ease;
+  cursor: pointer;
+  font-size: 0.95rem;
+  font-family: var(--font-family);
+}
+
+.btn i {
+  margin-right: var(--spacing-sm);
+}
+
+.btn-primary {
+  background: var(--primary-color);
+  color: white;
+}
+
+.btn-primary:hover {
+  background: #0056b3;
+  transform: translateY(-2px);
+  color: white;
+  text-decoration: none;
+}
+
+.btn-secondary {
+  background: var(--secondary-color);
+  color: white;
+}
+
+.btn-secondary:hover {
+  background: #545b62;
+  transform: translateY(-2px);
+  color: white;
+  text-decoration: none;
+}
+
+.btn-success {
+  background: var(--success-color);
+  color: white;
+}
+
+.btn-success:hover {
+  background: #218838;
+  transform: translateY(-2px);
+  color: white;
+  text-decoration: none;
+}
+
+.btn-info {
+  background: var(--info-color);
+  color: white;
+}
+
+.btn-info:hover {
+  background: #138496;
+  transform: translateY(-2px);
+  color: white;
+  text-decoration: none;
+}
+
+.btn-warning {
+  background: var(--warning-color);
+  color: var(--dark-color);
+}
+
+.btn-warning:hover {
+  background: #e0a800;
+  transform: translateY(-2px);
+  color: var(--dark-color);
+  text-decoration: none;
+}
+
+.btn-outline {
+  background: transparent;
+  color: var(--primary-color);
+  border: 2px solid var(--primary-color);
+}
+
+.btn-outline:hover {
+  background: var(--primary-color);
+  color: white;
+  text-decoration: none;
+}
+
+.btn-sm {
+  padding: var(--spacing-sm) var(--spacing-md);
+  font-size: 0.9rem;
+}
+
+/* Quick Actions */
+.quick-actions {
+  background: var(--container-bg);
+  border-radius: var(--border-radius-lg);
+  padding: var(--spacing-xl);
+  margin-bottom: var(--spacing-xl);
+  box-shadow: var(--shadow);
+}
+
+.quick-actions h3 {
+  margin: 0 0 var(--spacing-lg) 0;
+  color: var(--admin-text);
+  font-size: 1.5rem;
+}
+
+.quick-actions h3 i {
+  color: var(--primary-color);
+  margin-right: var(--spacing-md);
+}
+
+.action-buttons {
+  display: flex;
+  flex-wrap: wrap;
+  gap: var(--spacing-lg);
+}
+
+/* ========================================
+   ALERTS & NOTIFICATIONS
+   ======================================== */
+
+.alert {
+  padding: var(--spacing-lg) var(--spacing-lg);
+  border-radius: var(--border-radius);
+  margin-bottom: var(--spacing-lg);
+  border-left: 4px solid;
+  display: flex;
+  align-items: flex-start;
+}
+
+.alert i {
+  margin-right: var(--spacing-md);
+  margin-top: 2px;
+}
+
+.alert-info {
+  background: #e7f3ff;
+  border-left-color: var(--info-color);
+  color: #0c5460;
+}
+
+.alert-warning {
+  background: #fff3cd;
+  border-left-color: var(--warning-color);
+  color: #856404;
+}
+
+.alert-success {
+  background: #d4edda;
+  border-left-color: var(--success-color);
+  color: #155724;
+}
+
+.alert-danger {
+  background: #f8d7da;
+  border-left-color: var(--danger-color);
+  color: #721c24;
+}
+
+.alert h3 {
+  margin: 0 0 var(--spacing-md) 0;
+  font-size: 1.2rem;
+}
+
+/* ========================================
+   SERVER OUTPUT & DEVELOPMENT
+   ======================================== */
+
+.server-output, .command-output {
+  background-color: var(--dark-color);
+  color: #00ff00;
+  padding: var(--spacing-lg);
+  border-radius: var(--border-radius);
+  white-space: pre-wrap;
+  word-wrap: break-word;
+  max-height: 400px;
+  overflow-y: auto;
+  font-family: 'Courier New', monospace;
+  margin: var(--spacing-lg) 0;
+  border: 1px solid var(--admin-border);
+}
+
+.dev-server-link {
+  color: var(--success-color);
+  font-weight: bold;
+  text-decoration: none;
+}
+
+.dev-server-link:hover {
+  color: var(--success-color);
+  text-decoration: underline;
+}
+
+.dev-server-status {
+  background-color: #d4edda;
+  border: 1px solid #c3e6cb;
+  color: #155724;
+  padding: var(--spacing-md);
+  border-radius: var(--border-radius);
+  margin: var(--spacing-md) 0;
+}
+
+.dev-server-status a {
+  color: var(--success-color);
+  font-weight: bold;
+  text-decoration: none;
+}
+
+.dev-server-status a:hover {
+  text-decoration: underline;
+}
+
+/* ========================================
+   DEBUG & UTILITIES
+   ======================================== */
+
+.debug-messages {
+  background: var(--light-color);
+  border: 1px solid var(--admin-border);
+  border-radius: var(--border-radius);
+  padding: var(--spacing-lg);
+  margin-bottom: var(--spacing-lg);
+  font-family: monospace;
+}
+
+.debug {
+  color: #6c757d;
+  font-size: 0.9rem;
+  margin: var(--spacing-xs) 0;
+}
+
+.debug-footer {
+  margin-top: var(--spacing-xl);
+  padding: var(--spacing-md);
+  background-color: var(--light-color);
+  border-radius: var(--border-radius);
+  border: 1px solid var(--admin-border);
+}
+
+.debug-footer details {
+  margin-bottom: 0;
+}
+
+.debug-footer summary {
+  cursor: pointer;
+  font-weight: bold;
+  padding: var(--spacing-xs) 0;
+}
+
+.debug-footer ul {
+  margin-top: var(--spacing-md);
+  padding-left: var(--spacing-lg);
+}
+
+.debug-footer li {
+  margin-bottom: var(--spacing-xs);
+}
+
+/* ========================================
+   RESPONSIVE DESIGN
+   ======================================== */
+
+@media (max-width: 768px) {
+  .admin-container, .apiary-container {
+    padding: var(--spacing-lg);
+  }
+  
+  .admin-header, .apiary-header {
+    padding: var(--spacing-lg);
+    text-align: center;
+  }
+  
+  .admin-header h1, .apiary-header h1 {
+    font-size: 2rem;
+  }
+  
+  .admin-context, .apiary-context {
+    justify-content: center;
+  }
+  
+  .dashboard-stats {
+    grid-template-columns: 1fr;
+  }
+  
+  .feature-grid {
+    grid-template-columns: 1fr;
+  }
+  
+  .action-buttons {
+    justify-content: center;
+  }
+  
+  .admin-menu {
+    gap: var(--spacing-lg);
+  }
+}
+
+@media (max-width: 480px) {
+  .admin-container, .apiary-container {
+    padding: var(--spacing-md);
+  }
+  
+  .admin-header, .apiary-header {
+    padding: var(--spacing-md);
+  }
+  
+  .admin-header h1, .apiary-header h1 {
+    font-size: 1.5rem;
+  }
+  
+  .btn {
+    width: 100%;
+    justify-content: center;
+  }
+  
+  .action-buttons {
+    flex-direction: column;
+  }
+}
+
+/* ========================================
+   ANIMATION & LOADING STATES
+   ======================================== */
+
+@keyframes pulse {
+  0% { opacity: 1; }
+  50% { opacity: 0.5; }
+  100% { opacity: 1; }
+}
+
+.loading {
+  animation: pulse 1.5s ease-in-out infinite;
+}
+
+@keyframes slideIn {
+  from {
+    opacity: 0;
+    transform: translateY(20px);
+  }
+  to {
+    opacity: 1;
+    transform: translateY(0);
+  }
+}
+
+.admin-section, .stat-card, .feature-card {
+  animation: slideIn 0.5s ease-out;
+}
+
+/* ========================================
+   PRINT STYLES
+   ======================================== */
+
+@media print {
+  .admin-section:hover,
+  .stat-card:hover,
+  .feature-card:hover {
+    transform: none;
+    box-shadow: none;
+  }
+  
+  .btn {
+    background: transparent !important;
+    color: black !important;
+    border: 1px solid black !important;
+  }
+  
+  .server-output, .command-output {
+    background: white !important;
+    color: black !important;
+    border: 1px solid black !important;
+  }
+}

--- Comserv/root/static/css/themes/documentation.css
