     border: 2px dashed var(--border-color);
 }

+/* Recent Updates Section Styles - moved from inline CSS */
+.changelog-header {
+    display: flex;
+    justify-content: space-between;
+    align-items: center;
+    margin-bottom: 20px;
+    padding-bottom: 10px;
+    border-bottom: 1px solid var(--border-color);
+}
+
+.changelog-header h3 {
+    margin: 0;
+    color: var(--text-color);
+}
+
+.view-all-link {
+    background-color: var(--primary-color);
+    color: white;
+    padding: 8px 15px;
+    border-radius: 4px;
+    text-decoration: none;
+    font-size: 14px;
+    transition: background-color 0.3s;
+}
+
+.view-all-link:hover {
+    background-color: var(--link-hover-color);
+    text-decoration: none;
+    color: white;
+}
+
+.changelog-summary {
+    background-color: var(--table-header-bg);
+    border-left: 4px solid var(--primary-color);
+    padding: 15px;
+    margin-bottom: 20px;
+    border-radius: 0 4px 4px 0;
+}
+
+.changelog-summary h3 {
+    margin-top: 0;
+    color: var(--text-color);
+}
+
+.changelog-meta {
+    color: var(--text-color);
+    opacity: 0.7;
+    font-size: 0.9em;
+    margin-bottom: 10px;
+}
+
+.changelog-meta .date {
+    font-weight: bold;
+    margin-right: 10px;
+}
+
+.read-more {
+    display: inline-block;
+    margin-top: 10px;
+    color: var(--link-color);
+    text-decoration: none;
+    font-weight: bold;
+}
+
+.read-more:hover {
+    color: var(--link-hover-color);
+    text-decoration: underline;
+}
+
+/* Quick Access Links for Admins */
+.quick-access-links {
+    background: var(--background-color);
+    border-radius: 12px;
+    padding: 25px;
+    margin-bottom: 30px;
+    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
+}
+
+.quick-access-links h2 {
+    margin: 0 0 20px 0;
+    color: var(--text-color);
+    font-size: 1.5rem;
+}
+
