/* Theme: default - Universal Container System */
:root {
  --accent-color: #FF9900;
  --background-color: #ffffff;
  --border-color: #dddddd;
  --link-color: #0000FF;
  --link-hover-color: #000099;
  --primary-color: #0066cc;
  --secondary-color: #f9f9f9;
  --success-color: #27ae60;
  --table-header-bg: #f2f2f2;
  --text-color: #000000;
  --warning-color: #f39c12;
  --danger-color: #dc3545;
  --info-color: #17a2b8;
}

/* ========================================
   UNIVERSAL CONTAINER SYSTEM
   NO PAGE-SPECIFIC CLASSES
   ======================================== */

/* Main Container - used by ALL pages */
.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
    font-family: Arial, sans-serif;
    background: var(--background-color);
}

/* Header - used by ALL pages */
.header {
    background: linear-gradient(135deg, var(--primary-color) 0%, var(--secondary-color) 100%);
    color: white;
    padding: 30px;
    border-radius: 8px;
    margin-bottom: 30px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}

.header h1 {
    margin: 0 0 15px 0;
    font-size: 2.5rem;
    font-weight: 700;
    color: white;
}

/* Context Information - used by ALL pages */
.context {
    display: flex;
    flex-wrap: wrap;
    gap: 20px;
    font-size: 1rem;
}

.context-item {
    background: rgba(255,255,255,0.2);
    padding: 8px 15px;
    border-radius: 20px;
    color: white;
}

/* Sections - used by ALL pages */
.section {
    background: var(--background-color);
    border-radius: 8px;
    margin-bottom: 20px;
    box-shadow: 0 2px 8px rgba(0,0,0,0.1);
    border: 1px solid var(--border-color);
}

/* Section Headers - used by ALL pages */
.section-header {
    background: var(--table-header-bg);
    padding: 20px;
    margin: 0;
    color: var(--text-color);
    font-size: 1.3rem;
    border-bottom: 1px solid var(--border-color);
}

.section-header.toggle {
    cursor: pointer;
    position: relative;
    padding-right: 50px;
}

.section-header.toggle::after {
    content: "▼";
    position: absolute;
    right: 20px;
    top: 50%;
    transform: translateY(-50%);
    font-size: 0.8em;
    color: var(--accent-color);
    transition: transform 0.3s;
}

.section-header.toggle.expanded::after {
    transform: translateY(-50%) rotate(180deg);
}

/* Section Content - used by ALL pages */
.section-content {
    padding: 20px;
}

.section-content.collapsible {
    max-height: 0;
    overflow: hidden;
    transition: max-height 0.5s ease;
    padding: 0 20px;
}

.section-content.collapsible.expanded {
    max-height: 5000px;
    overflow: visible;
    padding: 20px;
}

/* Cards - used by ALL pages */
.card {
    display: block;
    padding: 15px;
    background-color: var(--background-color);
    border-radius: 6px;
    box-shadow: 0 2px 5px rgba(0,0,0,0.1);
    text-decoration: none;
    color: var(--text-color);
    transition: transform 0.2s, box-shadow 0.2s;
    border-left: 4px solid var(--primary-color);
    margin-bottom: 15px;
}

.card:hover {
    transform: translateY(-3px);
    box-shadow: 0 4px 8px rgba(0,0,0,0.15);
    text-decoration: none;
    color: var(--text-color);
}

.card h3 {
    margin-top: 0;
    color: var(--primary-color);
    font-size: 1.2em;
}

.card p {
    margin: 0 0 15px 0;
    color: var(--text-color);
    line-height: 1.5;
}

/* Grid Layouts - used by ALL pages */
.grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
    gap: 15px;
}

/* Buttons - used by ALL pages */
.btn {
    display: inline-block;
    padding: 10px 20px;
    border: none;
    border-radius: 4px;
    text-decoration: none;
    font-weight: 500;
    transition: all 0.3s ease;
    cursor: pointer;
    font-size: 0.95rem;
    margin-right: 10px;
}

.btn-primary {
    background: var(--primary-color);
    color: white;
}

.btn-primary:hover {
    background: var(--link-hover-color);
    color: white;
    text-decoration: none;
}

/* Badges - used by ALL pages */
.badge {
    display: inline-block;
    padding: 4px 8px;
    border-radius: 4px;
    font-size: 0.75em;
    font-weight: 500;
    margin-right: 5px;
}

.badge-warning { 
    background: var(--warning-color); 
    color: var(--text-color); 
}

/* Alerts - used by ALL pages */
.alert {
    padding: 15px 20px;
    border-radius: 8px;
    margin-bottom: 20px;
    border-left: 4px solid;
}

.alert-info {
    background: #e7f3ff;
    border-left-color: var(--info-color);
    color: #0c5460;
}

/* Special Sections */
.highlight-section {
    margin: 20px 0 30px;
    padding: 15px;
    background-color: #e3f2fd;
    border-radius: 8px;
    border-left: 5px solid var(--primary-color);
}

.highlight-section h2 {
    color: var(--text-color);
    margin-top: 0;
    font-size: 1.4em;
}

/* Debug Information */
.debug {
    background: var(--table-header-bg);
    border: 1px solid var(--border-color);
    border-radius: 8px;
    padding: 15px;
    margin-bottom: 20px;
    font-family: monospace;
    border-left: 4px solid var(--warning-color);
}

.debug h3 {
    margin: 0 0 10px 0;
    color: var(--warning-color);
    font-size: 1.2rem;
}

/* Text Utilities */
.intro {
    font-size: 1.1rem;
    color: var(--text-color);
    margin-bottom: 30px;
    line-height: 1.6;
}

/* LEGACY SUPPORT - for gradual migration */
.documentation-container { 
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
    font-family: Arial, sans-serif;
    background: var(--background-color);
}
.admin-container { 
