+        data_type => 'time',
+        is_nullable => 1,
+    },
+    end_time => {
+        data_type => 'time',
+        is_nullable => 1,
+    },
+    weather_conditions => {
+        data_type => 'varchar',
+        size => 100,
+        is_nullable => 1,
+    },
+    temperature => {
+        data_type => 'decimal',
+        size => [5,2],
+        is_nullable => 1,
+    },
+    inspector => {
+        data_type => 'varchar',
+        size => 50,
+    },
+    inspection_type => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/routine disease_check harvest treatment emergency/]
+        },
+        default_value => 'routine',
+    },
+    overall_status => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/excellent good fair poor critical/]
+        },
+        default_value => 'good',
+    },
+    queen_seen => {
+        data_type => 'boolean',
+        default_value => 0,
