+    },
+    eggs_seen => {
+        data_type => 'boolean',
+        default_value => 0,
+    },
+    larvae_seen => {
+        data_type => 'boolean',
+        default_value => 0,
+    },
+    capped_brood_seen => {
+        data_type => 'boolean',
+        default_value => 0,
+    },
+    supersedure_cells => {
+        data_type => 'integer',
+        default_value => 0,
+    },
+    swarm_cells => {
+        data_type => 'integer',
+        default_value => 0,
+    },
+    queen_cells => {
+        data_type => 'integer',
+        default_value => 0,
+    },
+    population_estimate => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/very_strong strong moderate weak very_weak/]
