+
+# Custom methods
+sub duration_minutes {
+    my $self = shift;
+    return unless $self->start_time && $self->end_time;
+    
+    my ($start_h, $start_m) = split /:/, $self->start_time;
+    my ($end_h, $end_m) = split /:/, $self->end_time;
+    
+    my $start_minutes = $start_h * 60 + $start_m;
+    my $end_minutes = $end_h * 60 + $end_m;
+    
+    return $end_minutes - $start_minutes;
+}
+
+sub queen_status {
+    my $self = shift;
+    if ($self->queen_seen) {
+        return $self->queen_marked ? 'seen_marked' : 'seen_unmarked';
+    }
+    return 'not_seen';
+}
+
+sub brood_status {
+    my $self = shift;
+    my @stages;
+    push @stages, 'eggs' if $self->eggs_seen;
+    push @stages, 'larvae' if $self->larvae_seen;
+    push @stages, 'capped' if $self->capped_brood_seen;
+    return @stages ? join(', ', @stages) : 'none';
+}
+
+sub has_queen_issues {
+    my $self = shift;
+    return $self->supersedure_cells > 0 || 
+           $self->swarm_cells > 0 || 
+           (!$self->queen_seen && !$self->eggs_seen);
+}
+
+sub needs_attention {
+    my $self = shift;
+    return $self->overall_status =~ /^(poor|critical)$/ ||
+           $self->has_queen_issues ||
+           $self->action_required;
+}
+
+sub is_overdue {
+    my $self = shift;
+    return 0 unless $self->next_inspection_date;
+    
+    my $today = DateTime->today;
+    my ($year, $month, $day) = split /-/, $self->next_inspection_date;
+    my $next_date = DateTime->new(year => $year, month => $month, day => $day);
+    
+    return $today > $next_date;
+}
+
+sub box_summary {
+    my $self = shift;
+    return $self->inspection_details->search({
+        detail_type => 'box_summary'
+    });
+}
+
+sub frame_details {
+    my $self = shift;
+    return $self->inspection_details->search({
+        detail_type => 'frame_detail'
+    });
+}
+
+1;
+
+=head1 NAME
+
+Comserv::Model::Schema::Apiary::Result::Inspection - Inspection table result class
+
+=head1 DESCRIPTION
+
+Represents individual hive inspections. Each inspection captures the overall state
+of a hive at a specific point in time, replacing the time-based entries in the
+legacy ApisQueenLogTb structure.
+
+=head1 RELATIONSHIPS
+
+=over 4
+
+=item * belongs_to: hive
+
+=item * has_many: inspection_details
+
+=back
+
+=head1 METHODS
+
+=over 4
+
+=item * duration_minutes() - Returns inspection duration in minutes
+
+=item * queen_status() - Returns queen status (seen_marked, seen_unmarked, not_seen)
+
+=item * brood_status() - Returns comma-separated list of brood stages seen
+
+=item * has_queen_issues() - Returns true if queen problems detected
+
+=item * needs_attention() - Returns true if hive needs immediate attention
+
+=item * is_overdue() - Returns true if next inspection is overdue
+
+=item * box_summary() - Returns box-level inspection details
+
+=item * frame_details() - Returns frame-level inspection details
+
+=back
+
+=cut

