+# Custom methods
+sub total_percentage {
+    my $self = shift;
+    return $self->brood_percentage + $self->honey_percentage + 
+           $self->pollen_percentage + $self->empty_percentage;
+}
+
+sub is_productive {
+    my $self = shift;
+    return ($self->brood_percentage + $self->honey_percentage + $self->pollen_percentage) > 50;
