/* Apiary Management System Theme */
/* File: apiary.css,v 1.0 2025/01/27 shanta Exp shanta */

/* Color Variables */
:root {
    --apiary-primary: #f4a261;      /* Honey orange */
    --apiary-secondary: #e76f51;    /* Warm red */
    --apiary-accent: #2a9d8f;       /* Teal green */
    --apiary-dark: #264653;         /* Dark green */
    --apiary-light: #f1faee;        /* Light cream */
    --apiary-warning: #e9c46a;      /* Golden yellow */
    --apiary-success: #2a9d8f;      /* Success green */
    --apiary-danger: #e76f51;       /* Danger red */
    --apiary-info: #219ebc;         /* Info blue */

    /* Bee-themed colors */
    --bee-yellow: #ffd60a;
    --bee-black: #003566;
    --hive-brown: #8b5a3c;
    --honey-gold: #ffb700;
    --pollen-orange: #ff8500;
}

/* Container Styles */
.apiary-container {
    max-width: 1400px;
    margin: 0 auto;
    padding: 20px;
    background: linear-gradient(135deg, var(--apiary-light) 0%, #ffffff 100%);
    min-height: calc(100vh - 200px);
}

/* Header Styles */
.apiary-header {
    background: linear-gradient(135deg, var(--apiary-primary) 0%, var(--apiary-secondary) 100%);
    color: white;
    padding: 30px;
    border-radius: 12px;
    margin-bottom: 30px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}

.apiary-header h1,
.apiary-header h2 {
    margin: 0 0 15px 0;
    font-size: 2.5rem;
    font-weight: 700;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
}

.apiary-header h1 i,
.apiary-header h2 i {
    color: var(--bee-yellow);
    margin-right: 15px;
    text-shadow: 1px 1px 2px rgba(0,0,0,0.5);
}

.apiary-context {
    display: flex;
    flex-wrap: wrap;
    gap: 20px;
    font-size: 1rem;
}

.context-item {
    background: rgba(255,255,255,0.2);
    padding: 8px 15px;
    border-radius: 20px;
    backdrop-filter: blur(10px);
}

.status-active {
    color: var(--bee-yellow);
    font-weight: bold;
}

/* Dashboard Stats */
.dashboard-stats {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 20px;
    margin-bottom: 30px;
}

.stat-card {
    background: white;
    border-radius: 12px;
    padding: 25px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
    border-left: 5px solid var(--apiary-primary);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.stat-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
}

.stat-card.hives {
    border-left-color: var(--apiary-primary);
}

.stat-card.inspections {
    border-left-color: var(--apiary-accent);
}

.stat-card.todos {
    border-left-color: var(--apiary-warning);
}

.stat-card.inventory {
    border-left-color: var(--apiary-secondary);
}

.stat-card .stat-icon {
    font-size: 2.5rem;
    color: var(--apiary-primary);
    margin-bottom: 15px;
}

.stat-card.inspections .stat-icon {
    color: var(--apiary-accent);
}

.stat-card.todos .stat-icon {
    color: var(--apiary-warning);
}

.stat-card.inventory .stat-icon {
    color: var(--apiary-secondary);
}

.stat-content {
    display: flex;
    flex-direction: column;
}

.stat-number {
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--apiary-dark);
    line-height: 1;
}

.stat-label {
    font-size: 1rem;
    color: #666;
    margin-top: 5px;
}

.stat-detail {
    font-size: 0.9rem;
    color: #888;
    margin-top: 3px;
}

/* Quick Actions */
.quick-actions {
    background: white;
    border-radius: 12px;
    padding: 25px;
    margin-bottom: 30px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
}

.quick-actions h3 {
    margin: 0 0 20px 0;
    color: var(--apiary-dark);
    font-size: 1.5rem;
}

.quick-actions h3 i {
    color: var(--bee-yellow);
    margin-right: 10px;
}

.action-buttons {
    display: flex;
    flex-wrap: wrap;
    gap: 15px;
