    padding: 20px;
    border-bottom: 1px solid #dee2e6;
}

.feature-header h3, .feature-header h4 {
    margin: 0;
    color: var(--apiary-dark);
    font-size: 1.3rem;
    display: flex;
    align-items: center;
}

.feature-icon {
    font-size: 1.5rem;
    color: var(--apiary-primary);
    margin-right: 12px;
