    background: #e8956b;
    transform: translateY(-2px);
}

.btn-secondary {
    background: var(--apiary-accent);
    color: white;
}

.btn-secondary:hover {
    background: #238a7a;
    transform: translateY(-2px);
}

.btn-success {
    background: var(--apiary-success);
    color: white;
}

.btn-success:hover {
    background: #238a7a;
    transform: translateY(-2px);
}

.btn-info {
    background: var(--apiary-info);
    color: white;
}

.btn-info:hover {
    background: #1a7a96;
    transform: translateY(-2px);
}

.btn-warning {
    background: var(--apiary-warning);
    color: var(--apiary-dark);
}

.btn-warning:hover {
    background: #e6b85c;
    transform: translateY(-2px);
}

.btn-outline {
    background: transparent;
    color: var(--apiary-primary);
    border: 2px solid var(--apiary-primary);
}

.btn-outline:hover {
    background: var(--apiary-primary);
    color: white;
