/* Responsive Design */
@media (max-width: 768px) {
    .apiary-container {
        padding: 15px;
    }

    .apiary-header {
        padding: 20px;
        text-align: center;
    }

    .apiary-header h1 {
        font-size: 2rem;
    }

    .apiary-context {
        justify-content: center;
    }

    .dashboard-stats {
        grid-template-columns: 1fr;
    }

    .feature-grid {
