    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

/* Focus styles for accessibility */
.btn:focus,
.admin-card a:focus {
    outline: 2px solid var(--apiary-primary);
    outline-offset: 2px;
}

