            $schema_info = $self->get_forager_table_schema($c, $table_name);
        } else {
            die "Invalid database: $database";
        }

        $c->stash(json => { 
            success => 1, 
            schema => $schema_info,
            table_name => $table_name,
            database => $database
        });

    } catch {
