+++ Comserv/script/check_documentation_routes.pl
@@ -0,0 +1,172 @@
+#!/usr/bin/env perl
+
+use strict;
+use warnings;
+use FindBin;
+use lib "$FindBin::Bin/../lib";
+use File::Find;
+use File::Basename;
+use JSON;
+use Data::Dumper;
+
+# Configuration
+my $doc_root = "$FindBin::Bin/../root/Documentation";
+my $config_file = "$doc_root/documentation_config.json";
+
+# Load the documentation configuration
+my $config_json;
+{
+    local $/;
+    open my $fh, '<', $config_file or die "Cannot open $config_file: $!";
+    $config_json = <$fh>;
+    close $fh;
+}
+
+my $config = decode_json($config_json);
+
+# Extract all pages from the configuration
+my %configured_pages;
+my %configured_paths;
+
+# Process categories
+foreach my $category (keys %{$config->{categories}}) {
+    foreach my $page (@{$config->{categories}{$category}{pages}}) {
+        $configured_pages{$page} = 1;
+    }
+}
+
+# Process default paths
+foreach my $page (keys %{$config->{default_paths}}) {
+    my $path = $config->{default_paths}{$page};
+    $configured_paths{$path} = $page;
+    $configured_pages{$page} = 1;
+}
+
+# Find all documentation files
+my @all_files;
+find(
+    {
+        wanted => sub {
+            return if -d $_;
+            return unless /\.(md|tt|html|txt)$/i;
+            
+            my $full_path = $File::Find::name;
+            my $rel_path = $full_path;
+            $rel_path =~ s/^$FindBin::Bin\/\.\.\/root\///;
+            
+            push @all_files, {
+                full_path => $full_path,
+                rel_path => $rel_path,
+                filename => basename($_),
+                key => basename($_, qr/\.[^.]*$/),
+            };
+        },
+        no_chdir => 1
+    },
+    $doc_root
+);
+
+# Check for files not in the configuration
+my @unconfigured_files;
+my @duplicate_files;
+my %file_keys;
+
+foreach my $file (@all_files) {
+    my $key = $file->{key};
+    my $rel_path = $file->{rel_path};
+    
+    # Check if this file's path is in the configured paths
+    my $path_configured = 0;
+    foreach my $config_path (keys %configured_paths) {
+        if ($rel_path eq $config_path) {
+            $path_configured = 1;
+            last;
+        }
+    }
+    
+    # Check if this file's key is in the configured pages
+    if (!$configured_pages{$key} && !$path_configured) {
+        push @unconfigured_files, $file;
+    }
+    
+    # Check for duplicate keys (same filename with different extensions)
+    if ($file_keys{$key}) {
+        push @duplicate_files, {
+            key => $key,
+            files => [$file_keys{$key}, $file]
+        };
+    } else {
+        $file_keys{$key} = $file;
+    }
+}
+
+# Check for configured pages that don't exist
+my @missing_files;
+foreach my $page (keys %configured_pages) {
+    if (!$file_keys{$page}) {
+        # Check if it has a default path
+        my $default_path = $config->{default_paths}{$page};
+        if ($default_path) {
+            # Check if the file exists at the default path
+            my $full_path = "$FindBin::Bin/../root/$default_path";
+            if (!-e $full_path) {
+                push @missing_files, {
+                    key => $page,
+                    expected_path => $default_path
+                };
+            }
+        } else {
+            push @missing_files, {
+                key => $page,
+                expected_path => "unknown"
+            };
+        }
+    }
+}
+
+# Print the results
+print "Documentation Route Check Results\n";
+print "================================\n\n";
+
+print "Total documentation files found: " . scalar(@all_files) . "\n";
+print "Total configured pages: " . scalar(keys %configured_pages) . "\n\n";
+
+if (@unconfigured_files) {
+    print "Files not configured in documentation_config.json (" . scalar(@unconfigured_files) . "):\n";
+    foreach my $file (sort { $a->{rel_path} cmp $b->{rel_path} } @unconfigured_files) {
+        print "  - $file->{rel_path} (key: $file->{key})\n";
+    }
+    print "\n";
+    
+    print "Suggested additions to documentation_config.json:\n";
+    print "{\n";
+    print "  \"default_paths\": {\n";
+    my $count = 0;
+    foreach my $file (sort { $a->{rel_path} cmp $b->{rel_path} } @unconfigured_files) {
+        my $comma = (++$count < scalar(@unconfigured_files)) ? "," : "";
+        print "    \"$file->{key}\": \"$file->{rel_path}\"$comma\n";
+    }
+    print "  }\n";
+    print "}\n\n";
+}
+
+if (@duplicate_files) {
+    print "Duplicate keys found (" . scalar(@duplicate_files) . "):\n";
+    foreach my $dup (sort { $a->{key} cmp $b->{key} } @duplicate_files) {
+        print "  - Key: $dup->{key}\n";
+        foreach my $file (@{$dup->{files}}) {
+            print "    * $file->{rel_path}\n";
+        }
+    }
+    print "\n";
+}
+
+if (@missing_files) {
+    print "Configured pages with missing files (" . scalar(@missing_files) . "):\n";
+    foreach my $missing (sort { $a->{key} cmp $b->{key} } @missing_files) {
+        print "  - $missing->{key} (expected at: $missing->{expected_path})\n";
+    }
+    print "\n";
+}
+
+print "Documentation Route Check Complete\n";

