     return $status;
 }

+# Backup management
+sub backup :Path('/admin/backup') :Args(0) {
+    my ($self, $c) = @_;
+    
+    $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'backup', 
+        "Starting backup action");
+    
+    # Check if the user has admin role
+    unless ($c->user_exists && $c->check_user_roles('admin')) {
+        $self->logging->log_with_details($c, 'warn', __FILE__, __LINE__, 'backup', 
+            "Access denied: User does not have admin role");
+        
+        # Set error message in flash
+        $c->flash->{error_msg} = "You need to be an administrator to access this area.";
+        
+        # Redirect to login page with destination parameter
+        $c->response->redirect($c->uri_for('/user/login', {
+            destination => $c->req->uri
+        }));
+        return;
+    }
+    
+    # Get existing backups
+    my $backups = $self->get_backup_list($c);
+    
+    # Use the standard debug message system
+    if ($c->session->{debug_mode}) {
+        $c->stash->{debug_msg} = [] unless ref($c->stash->{debug_msg}) eq 'ARRAY';
+        push @{$c->stash->{debug_msg}}, "Admin controller backup view - Template: admin/backup.tt";
+        push @{$c->stash->{debug_msg}}, "Found " . scalar(@$backups) . " backup files";
+    }
+    
+    # Pass data to the template
+    $c->stash(
+        template => 'admin/backup.tt',
+        backups => $backups
+    );
+    
+    $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'backup', 
+        "Completed backup action");
+}
+
+# Create a new backup
+sub create_backup :Path('/admin/backup/create') :Args(0) {
+    my ($self, $c) = @_;
+    
+    $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'create_backup', 
+        "Starting create_backup action");
+    
+    # Check if the user has admin role
+    unless ($c->user_exists && $c->check_user_roles('admin')) {
+        $self->logging->log_with_details($c, 'warn', __FILE__, __LINE__, 'create_backup', 
+            "Access denied: User does not have admin role");
+        
+        # Set error message in flash
+        $c->flash->{error_msg} = "You need to be an administrator to access this area.";
+        
+        # Redirect to login page with destination parameter
+        $c->response->redirect($c->uri_for('/user/login', {
+            destination => $c->req->uri
+        }));
+        return;
+    }
+    
+    # Get backup type from parameters
+    my $backup_type = $c->req->param('type') || 'full';
+    my $description = $c->req->param('description') || '';
+    
+    # Create the backup
+    my $result = $self->perform_backup($c, $backup_type, $description);
+    
+    if ($result->{success}) {
+        $c->flash->{success_msg} = $result->{message};
+        $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'create_backup', 
+            "Backup created successfully: " . $result->{filename});
+    } else {
+        $c->flash->{error_msg} = $result->{message};
+        $self->logging->log_with_details($c, 'error', __FILE__, __LINE__, 'create_backup', 
+            "Backup creation failed: " . $result->{message});
+    }
+    
+    # Redirect back to backup page
+    $c->response->redirect($c->uri_for('/admin/backup'));
+}
+
+# Download a backup file
+sub download_backup :Path('/admin/backup/download') :Args(1) {
+    my ($self, $c, $filename) = @_;
+    
+    $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'download_backup', 
+        "Starting download_backup action for file: $filename");
+    
+    # Check if the user has admin role
+    unless ($c->user_exists && $c->check_user_roles('admin')) {
+        $self->logging->log_with_details($c, 'warn', __FILE__, __LINE__, 'download_backup', 
+            "Access denied: User does not have admin role");
+        
+        # Set error message in flash
+        $c->flash->{error_msg} = "You need to be an administrator to access this area.";
+        
+        # Redirect to login page with destination parameter
+        $c->response->redirect($c->uri_for('/user/login', {
+            destination => $c->req->uri
+        }));
+        return;
+    }
+    
+    # Validate filename (security check)
+    unless ($filename && $filename =~ /^backup_\d{8}_\d{6}_[a-zA-Z0-9_-]+\.tar\.gz$/) {
+        $c->flash->{error_msg} = "Invalid backup filename";
+        $c->response->redirect($c->uri_for('/admin/backup'));
+        return;
+    }
+    
+    # Get backup directory path
+    my $backup_dir = $self->get_backup_directory($c);
+    my $backup_file = File::Spec->catfile($backup_dir, $filename);
+    
+    # Check if file exists
+    unless (-f $backup_file) {
+        $c->flash->{error_msg} = "Backup file not found";
+        $c->response->redirect($c->uri_for('/admin/backup'));
+        return;
+    }
+    
+    # Set response headers for download
+    $c->response->content_type('application/gzip');
+    $c->response->header('Content-Disposition' => "attachment; filename=\"$filename\"");
+    
+    # Stream the file
+    $c->serve_static_file($backup_file);
+    
+    $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'download_backup', 
+        "Backup file downloaded: $filename");
+}
+
+# Delete a backup file
+sub delete_backup :Path('/admin/backup/delete') :Args(1) {
+    my ($self, $c, $filename) = @_;
+    
+    $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'delete_backup', 
+        "Starting delete_backup action for file: $filename");
+    
+    # Check if the user has admin role
+    unless ($c->user_exists && $c->check_user_roles('admin')) {
+        $self->logging->log_with_details($c, 'warn', __FILE__, __LINE__, 'delete_backup', 
+            "Access denied: User does not have admin role");
+        
+        # Set error message in flash
+        $c->flash->{error_msg} = "You need to be an administrator to access this area.";
+        
+        # Redirect to login page with destination parameter
+        $c->response->redirect($c->uri_for('/user/login', {
+            destination => $c->req->uri
+        }));
+        return;
+    }
+    
+    # Validate filename (security check)
+    unless ($filename && $filename =~ /^backup_\d{8}_\d{6}_[a-zA-Z0-9_-]+\.tar\.gz$/) {
+        $c->flash->{error_msg} = "Invalid backup filename";
+        $c->response->redirect($c->uri_for('/admin/backup'));
+        return;
+    }
+    
+    # Get backup directory path
+    my $backup_dir = $self->get_backup_directory($c);
+    my $backup_file = File::Spec->catfile($backup_dir, $filename);
+    
+    # Check if file exists
+    unless (-f $backup_file) {
+        $c->flash->{error_msg} = "Backup file not found";
+        $c->response->redirect($c->uri_for('/admin/backup'));
+        return;
+    }
+    
+    # Delete the file
+    if (unlink($backup_file)) {
+        $c->flash->{success_msg} = "Backup file deleted successfully";
+        $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'delete_backup', 
+            "Backup file deleted: $filename");
+    } else {
+        $c->flash->{error_msg} = "Failed to delete backup file: $!";
+        $self->logging->log_with_details($c, 'error', __FILE__, __LINE__, 'delete_backup', 
+            "Failed to delete backup file: $filename - $!");
+    }
+    
+    # Redirect back to backup page
+    $c->response->redirect($c->uri_for('/admin/backup'));
+}
+
+# Get backup directory path
+sub get_backup_directory {
+    my ($self, $c) = @_;
+    
+    # Get the application root directory
+    my $app_root = $c->path_to('');
+    my $backup_dir = File::Spec->catdir($app_root, 'backups');
+    
+    # Create backup directory if it doesn't exist
+    unless (-d $backup_dir) {
+        make_path($backup_dir, { mode => 0755 });
+        $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'get_backup_directory', 
+            "Created backup directory: $backup_dir");
+    }
+    
+    return $backup_dir;
+}
+
+# Get list of existing backups
+sub get_backup_list {
+    my ($self, $c) = @_;
+    
+    my $backup_dir = $self->get_backup_directory($c);
+    my @backups = ();
+    
+    # Read backup directory
+    if (opendir(my $dh, $backup_dir)) {
+        while (my $file = readdir($dh)) {
+            next unless $file =~ /^backup_(\d{8})_(\d{6})_([a-zA-Z0-9_-]+)\.tar\.gz$/;
+            
+            my ($date, $time, $type) = ($1, $2, $3);
+            my $filepath = File::Spec->catfile($backup_dir, $file);
+            
+            # Get file stats
+            my @stat = stat($filepath);
+            my $size = $stat[7] || 0;
+            my $mtime = $stat[9] || 0;
+            
+            # Format date and time
+            my $formatted_date = sprintf("%s-%s-%s", 
+                substr($date, 0, 4), substr($date, 4, 2), substr($date, 6, 2));
+            my $formatted_time = sprintf("%s:%s:%s", 
+                substr($time, 0, 2), substr($time, 2, 2), substr($time, 4, 2));
+            
+            # Format file size
+            my $formatted_size = $self->format_file_size($size);
+            
+            push @backups, {
+                filename => $file,
+                type => $type,
+                date => $formatted_date,
+                time => $formatted_time,
+                size => $formatted_size,
+                size_bytes => $size,
+                mtime => $mtime
+            };
+        }
+        closedir($dh);
+    }
+    
+    # Sort by modification time (newest first)
+    @backups = sort { $b->{mtime} <=> $a->{mtime} } @backups;
+    
+    return \@backups;
+}
+
+# Perform backup operation
+sub perform_backup {
+    my ($self, $c, $backup_type, $description) = @_;
+    
+    $backup_type ||= 'full';
+    $description ||= '';
+    
+    # Generate backup filename
+    my ($sec, $min, $hour, $mday, $mon, $year) = localtime();
+    my $timestamp = sprintf("%04d%02d%02d_%02d%02d%02d", 
+        $year + 1900, $mon + 1, $mday, $hour, $min, $sec);
+    
+    my $backup_filename = "backup_${timestamp}_${backup_type}.tar.gz";
+    
+    # Get paths
+    my $app_root = $c->path_to('');
+    my $backup_dir = $self->get_backup_directory($c);
+    my $backup_file = File::Spec->catfile($backup_dir, $backup_filename);
+    
+    # Create backup based on type
+    my $success = 0;
+    my $message = '';
+    
+    eval {
+        if ($backup_type eq 'full') {
+            $success = $self->create_full_backup($c, $app_root, $backup_file);
+            $message = $success ? "Full backup created successfully" : "Failed to create full backup";
+        }
+        elsif ($backup_type eq 'database') {
+            $success = $self->create_database_backup($c, $backup_file);
+            $message = $success ? "Database backup created successfully" : "Failed to create database backup";
+        }
+        elsif ($backup_type eq 'config') {
+            $success = $self->create_config_backup($c, $app_root, $backup_file);
+            $message = $success ? "Configuration backup created successfully" : "Failed to create configuration backup";
+        }
+        else {
+            $message = "Unknown backup type: $backup_type";
+        }
+    };
+    
+    if ($@) {
+        $message = "Backup failed with error: $@";
+        $success = 0;
+    }
+    
+    # Save backup metadata if successful
+    if ($success) {
+        $self->save_backup_metadata($c, $backup_filename, $backup_type, $description);
+    }
+    
+    return {
+        success => $success,
+        message => $message,
+        filename => $backup_filename
+    };
+}
+
+# Create full backup
+sub create_full_backup {
+    my ($self, $c, $app_root, $backup_file) = @_;
+    
+    # Create temporary exclude file
+    my $exclude_file = File::Spec->catfile($app_root, '.backup_exclude');
+    my @exclude_patterns = (
+        'backups/',
+        'logs/',
+        'local/',
+        '.git/',
+        '.idea/',
+        '*.log',
+        '*.tmp',
+        'workspace.xml'
+    );
+    
+    write_file($exclude_file, join("\n", @exclude_patterns));
+    
+    # Create tar command
+    my $tar_cmd = sprintf("cd %s && tar --exclude-from=%s -czf %s .", 
+        $app_root, $exclude_file, $backup_file);
+    
+    # Execute backup
+    my $result = system($tar_cmd);
+    
+    # Clean up exclude file
+    unlink($exclude_file);
+    
+    return $result == 0;
+}
+
+# Create database backup
+sub create_database_backup {
+    my ($self, $c, $backup_file) = @_;
+    
+    # This is a placeholder - you'll need to implement based on your database type
+    # For SQLite:
+    eval {
+        my $db_file = $c->model('DBEncy')->schema->storage->connect_info->[0];
+        if ($db_file && $db_file =~ /^dbi:SQLite:(.+)$/) {
+            my $sqlite_file = $1;
+            if (-f $sqlite_file) {
+                # Create tar with just the database file
+                my $tar_cmd = sprintf("tar -czf %s -C %s %s", 
+                    $backup_file, dirname($sqlite_file), basename($sqlite_file));
+                return system($tar_cmd) == 0;
+            }
+        }
+    };
+    
+    return 0;
+}
+
+# Create configuration backup
+sub create_config_backup {
+    my ($self, $c, $app_root, $backup_file) = @_;
+    
+    # Backup configuration files
+    my @config_files = (
+        'comserv.conf',
+        'db_config.json',
+        'root/static/config/',
+        'config/'
+    );
+    
+    my @existing_files = ();
+    foreach my $file (@config_files) {
+        my $full_path = File::Spec->catfile($app_root, $file);
+        if (-e $full_path) {
+            push @existing_files, $file;
+        }
+    }
+    
+    if (@existing_files) {
+        my $tar_cmd = sprintf("cd %s && tar -czf %s %s", 
+            $app_root, $backup_file, join(' ', @existing_files));
+        return system($tar_cmd) == 0;
+    }
+    
+    return 0;
+}
+
+# Save backup metadata
+sub save_backup_metadata {
+    my ($self, $c, $filename, $type, $description) = @_;
+    
+    # Try to save to database if available
+    eval {
+        $c->model('DBEncy::Backup')->create({
+            filename => $filename,
+            backup_type => $type,
+            description => $description,
+            created_at => \'NOW()',
+            created_by => $c->session->{user_id} || 0
+        });
+    };
+    
+    # If database save fails, save to file
+    if ($@) {
+        my $backup_dir = $self->get_backup_directory($c);
+        my $metadata_file = File::Spec->catfile($backup_dir, "${filename}.meta");
+        
+        my $metadata = {
+            filename => $filename,
+            type => $type,
+            description => $description,
+            created_at => time(),
+            created_by => $c->session->{username} || 'unknown'
+        };
+        
+        eval {
+            write_file($metadata_file, JSON::encode_json($metadata));
+        };
+    }
+}
+
+# Format file size for display
+sub format_file_size {
+    my ($self, $size) = @_;
+    
+    return '0 B' unless $size;
+    
+    my @units = ('B', 'KB', 'MB', 'GB', 'TB');
+    my $unit_index = 0;
+    
+    while ($size >= 1024 && $unit_index < $#units) {
+        $size /= 1024;
+        $unit_index++;
+    }
+    
+    return sprintf("%.1f %s", $size, $units[$unit_index]);
+}
+
 __PACKAGE__->meta->make_immutable;

 1;

