+#!/usr/bin/perl
+use strict;
+use warnings;
+use FindBin;
+use lib "$FindBin::Bin/Comserv/lib";
+use Data::Dumper;
+
+# Test database connection format
+eval {
+    require Comserv::Model::DBEncy;
+    
+    # Create a mock context object
+    my $mock_c = bless {}, 'MockContext';
+    
+    # Try to get the model configuration
+    my $model = Comserv::Model::DBEncy->new();
+    
+    print "Model created successfully\n";
+    print "Config: " . Dumper($model->config) . "\n";
+    
+    # Try to get connect_info
+    if ($model->can('connect_info')) {
+        my $connect_info = $model->connect_info;
+        print "Connect info type: " . ref($connect_info) . "\n";
+        print "Connect info: " . Dumper($connect_info) . "\n";
+    } else {
+        print "Model doesn't have connect_info method\n";
+    }
+    
+};
+
+if ($@) {
+    print "Error: $@\n";
+}
+
+package MockContext;
+sub new { return bless {}, shift; }
+1;
