--- Comserv/lib/Comserv/Model/HybridDB.pm
+++ Comserv/lib/Comserv/Model/HybridDB.pm
@@ -0,0 +1,399 @@
+package Comserv::Model::HybridDB;
+
+use strict;
+use warnings;
+use base 'Catalyst::Model';
+use DBI;
+use DBD::SQLite;
+use DBD::mysql;
+use Try::Tiny;
+use File::Spec;
+use JSON;
+use Data::Dumper;
+
+=head1 NAME
+
+Comserv::Model::HybridDB - Hybrid Database Backend Manager
+
+=head1 DESCRIPTION
+
+This model provides database backend abstraction for Comserv offline mode.
+It supports automatic detection and switching between local MySQL and SQLite backends.
+
+=head1 ARCHITECTURE
+
+- Primary: Local MySQL server (when available)
+- Fallback: SQLite database (when MySQL unavailable)
+- Auto-detection: Checks MySQL server availability
+- Production sync: Robust synchronization with production MySQL
+
+=cut
+
+# Configuration constants
+use constant {
+    MYSQL_DETECTION_TIMEOUT => 5,
+    SQLITE_DB_PATH => 'data/comserv_offline.db',
+    CONFIG_FILE => 'db_config.json',
+};
+
+=head1 METHODS
+
+=head2 new
+
+Initialize the HybridDB model with backend detection
+
+=cut
+
+sub new {
+    my ($class, $c, $args) = @_;
+    my $self = $class->next::method($c, $args);
+    
+    # Initialize backend detection
+    $self->{backend_type} = undef;
+    $self->{mysql_available} = undef;
+    $self->{sqlite_path} = undef;
+    $self->{config} = undef;
+    
+    # Load database configuration
+    $self->_load_config($c);
+    
+    # Detect available backends
+    $self->_detect_backends($c);
+    
+    return $self;
+}
+
+=head2 _load_config
+
+Load database configuration from db_config.json
+
+=cut
+
+sub _load_config {
+    my ($self, $c) = @_;
+    
+    try {
+        # Try to find config file using same logic as DBEncy.pm
+        my $config_file = $self->_find_config_file($c);
+        
+        if ($config_file && -f $config_file) {
+            local $/;
+            open my $fh, '<', $config_file or die "Cannot open $config_file: $!";
+            my $json_text = <$fh>;
+            close $fh;
+            
+            $self->{config} = decode_json($json_text);
+            $c->log->info("HybridDB: Loaded configuration from $config_file");
+        } else {
+            $c->log->error("HybridDB: Configuration file not found");
+            die "Database configuration file not found";
+        }
+    } catch {
+        my $error = $_;
+        $c->log->error("HybridDB: Error loading configuration: $error");
+        die "Failed to load database configuration: $error";
+    };
+}
+
+=head2 _find_config_file
+
+Find the database configuration file using same logic as DBEncy.pm
+
+=cut
+
+sub _find_config_file {
+    my ($self, $c) = @_;
+    
+    # Try Catalyst::Utils first
+    my $config_file;
+    eval {
+        require Catalyst::Utils;
+        $config_file = Catalyst::Utils::path_to(CONFIG_FILE);
+    };
+    
+    # Check environment variable
+    if ($@ || !defined $config_file) {
+        if ($ENV{COMSERV_CONFIG_PATH}) {
+            $config_file = File::Spec->catfile($ENV{COMSERV_CONFIG_PATH}, CONFIG_FILE);
+        }
+    }
+    
+    # Fallback to FindBin locations
+    if ($@ || !defined $config_file || !-f $config_file) {
+        require FindBin;
+        
+        my @possible_paths = (
+            File::Spec->catfile($FindBin::Bin, 'config', CONFIG_FILE),
+            File::Spec->catfile($FindBin::Bin, '..', 'config', CONFIG_FILE),
+            File::Spec->catfile($FindBin::Bin, CONFIG_FILE),
+            File::Spec->catfile($FindBin::Bin, '..', CONFIG_FILE),
+            '/opt/comserv/config/' . CONFIG_FILE,
+            '/opt/comserv/' . CONFIG_FILE,
+            '/etc/comserv/' . CONFIG_FILE
+        );
+        
+        foreach my $path (@possible_paths) {
+            if (-f $path) {
+                $config_file = $path;
+                last;
+            }
+        }
+    }
+    
+    return $config_file;
+}
+
+=head2 _detect_backends
+
+Detect available database backends (MySQL and SQLite)
+
+=cut
+
+sub _detect_backends {
+    my ($self, $c) = @_;
+    
+    # Detect MySQL availability
+    $self->{mysql_available} = $self->_detect_mysql($c);
+    
+    # Set SQLite path
+    $self->{sqlite_path} = File::Spec->catfile($FindBin::Bin, '..', SQLITE_DB_PATH);
+    
+    # Determine backend type
+    if ($self->{mysql_available}) {
+        $self->{backend_type} = 'mysql';
+        $c->log->info("HybridDB: Using MySQL backend (local server detected)");
+    } else {
+        $self->{backend_type} = 'sqlite';
+        $c->log->info("HybridDB: Using SQLite backend (MySQL not available)");
+    }
+}
+
+=head2 _detect_mysql
+
+Detect if local MySQL server is available and accessible
+
+=cut
+
+sub _detect_mysql {
+    my ($self, $c) = @_;
+    
+    return 0 unless $self->{config} && $self->{config}->{shanta_ency};
+    
+    my $config = $self->{config}->{shanta_ency};
+    
+    try {
+        # Create test connection with timeout
+        local $SIG{ALRM} = sub { die "MySQL detection timeout\n" };
+        alarm(MYSQL_DETECTION_TIMEOUT);
+        
+        my $dsn = "dbi:mysql:database=$config->{database};host=$config->{host};port=$config->{port}";
+        my $dbh = DBI->connect(
+            $dsn,
+            $config->{username},
+            $config->{password},
+            {
+                RaiseError => 1,
+                PrintError => 0,
+                mysql_connect_timeout => MYSQL_DETECTION_TIMEOUT,
+                mysql_read_timeout => MYSQL_DETECTION_TIMEOUT,
+                mysql_write_timeout => MYSQL_DETECTION_TIMEOUT,
+            }
+        );
+        
+        if ($dbh) {
+            # Test basic query
+            my $sth = $dbh->prepare("SELECT 1");
+            $sth->execute();
+            $sth->finish();
+            $dbh->disconnect();
+            
+            alarm(0);
+            $c->log->info("HybridDB: MySQL server detected and accessible");
+            return 1;
+        }
+        
+        alarm(0);
+        return 0;
+        
+    } catch {
+        alarm(0);
+        my $error = $_;
+        $c->log->debug("HybridDB: MySQL detection failed: $error");
+        return 0;
+    };
+}
+
+=head2 get_backend_type
+
+Get current backend type (mysql or sqlite)
+
+=cut
+
+sub get_backend_type {
+    my ($self) = @_;
+    return $self->{backend_type} || 'unknown';
+}
+
+=head2 is_mysql_available
+
+Check if MySQL backend is available
+
+=cut
+
+sub is_mysql_available {
+    my ($self) = @_;
+    return $self->{mysql_available} || 0;
+}
+
+=head2 get_connection_info
+
+Get connection information for current backend
+
+=cut
+
+sub get_connection_info {
+    my ($self, $c) = @_;
+    
+    if ($self->{backend_type} eq 'mysql') {
+        return $self->_get_mysql_connection_info($c);
+    } elsif ($self->{backend_type} eq 'sqlite') {
+        return $self->_get_sqlite_connection_info($c);
+    } else {
+        die "Unknown backend type: " . ($self->{backend_type} || 'undefined');
+    }
+}
+
+=head2 _get_mysql_connection_info
+
+Get MySQL connection information
+
+=cut
+
+sub _get_mysql_connection_info {
+    my ($self, $c) = @_;
+    
+    my $config = $self->{config}->{shanta_ency};
+    
+    return {
+        dsn => "dbi:mysql:database=$config->{database};host=$config->{host};port=$config->{port}",
+        user => $config->{username},
+        password => $config->{password},
+        mysql_enable_utf8 => 1,
+        on_connect_do => ["SET NAMES 'utf8'", "SET CHARACTER SET 'utf8'"],
+        quote_char => '`',
+    };
+}
+
+=head2 _get_sqlite_connection_info
+
+Get SQLite connection information
+
+=cut
+
+sub _get_sqlite_connection_info {
+    my ($self, $c) = @_;
+    
+    # Ensure SQLite database directory exists
+    my $db_dir = File::Spec->catdir($FindBin::Bin, '..', 'data');
+    unless (-d $db_dir) {
+        mkdir $db_dir or die "Cannot create data directory: $!";
+    }
+    
+    return {
+        dsn => "dbi:SQLite:dbname=" . $self->{sqlite_path},
+        user => '',
+        password => '',
+        sqlite_unicode => 1,
+        on_connect_do => [
+            'PRAGMA foreign_keys = ON',
+            'PRAGMA journal_mode = WAL',
+            'PRAGMA synchronous = NORMAL',
+        ],
+        quote_char => '"',
+    };
+}
+
+=head2 switch_backend
+
+Switch to specified backend (mysql or sqlite)
+
+=cut
+
+sub switch_backend {
+    my ($self, $c, $backend_type) = @_;
+    
+    unless ($backend_type && ($backend_type eq 'mysql' || $backend_type eq 'sqlite')) {
+        die "Invalid backend type: " . ($backend_type || 'undefined');
+    }
+    
+    if ($backend_type eq 'mysql' && !$self->{mysql_available}) {
+        die "Cannot switch to MySQL: server not available";
+    }
+    
+    my $old_backend = $self->{backend_type};
+    $self->{backend_type} = $backend_type;
+    
+    $c->log->info("HybridDB: Switched backend from $old_backend to $backend_type");
+    
+    return 1;
+}
+
+=head2 get_status
+
+Get current backend status information
+
+=cut
+
+sub get_status {
+    my ($self) = @_;
+    
+    return {
+        current_backend => $self->{backend_type},
+        mysql_available => $self->{mysql_available},
+        sqlite_path => $self->{sqlite_path},
+        config_loaded => defined($self->{config}) ? 1 : 0,
+    };
+}
+
+=head2 test_connection
+
+Test connection to current backend
+
+=cut
+
+sub test_connection {
+    my ($self, $c) = @_;
+    
+    try {
+        my $conn_info = $self->get_connection_info($c);
+        my $dbh = DBI->connect(
+            $conn_info->{dsn},
+            $conn_info->{user},
+            $conn_info->{password},
+            { RaiseError => 1, PrintError => 0 }
+        );
+        
+        if ($dbh) {
+            $dbh->disconnect();
+            return 1;
+        }
+        return 0;
+        
+    } catch {
+        my $error = $_;
+        $c->log->error("HybridDB: Connection test failed: $error");
+        return 0;
+    };
+}
+
+1;
+
+=head1 AUTHOR
+
+Comserv Development Team
+
+=head1 COPYRIGHT
+
+Copyright (c) 2025 Comserv. All rights reserved.
+
+=cut

--- Comserv/lib/Comserv/Controller/DatabaseMode.pm
+++ Comserv/lib/Comserv/Controller/DatabaseMode.pm
@@ -0,0 +1,329 @@
+package Comserv::Controller::DatabaseMode;
+use Moose;
+use namespace::autoclean;
+use Try::Tiny;
+use JSON;
+
+BEGIN { extends 'Catalyst::Controller'; }
+
+=head1 NAME
+
+Comserv::Controller::DatabaseMode - Database Backend Selection Controller
+
+=head1 DESCRIPTION
+
+This controller provides interface for selecting and managing database backends
+in the hybrid offline mode system. Supports MySQL and SQLite backend switching.
+
+=cut
+
+=head1 METHODS
+
+=head2 index
+
+Main database mode selection interface
+
+=cut
+
+sub index :Path :Args(0) {
+    my ($self, $c) = @_;
+    
+    # Check admin/developer permissions
+    unless ($c->check_user_roles(qw/admin developer/)) {
+        $c->response->redirect($c->uri_for('/'));
+        $c->detach();
+    }
+    
+    try {
+        # Get HybridDB model
+        my $hybrid_db = $c->model('HybridDB');
+        my $status = $hybrid_db->get_status();
+        
+        # Test current connection
+        my $connection_test = $hybrid_db->test_connection($c);
+        
+        $c->stash(
+            template => 'database_mode/index.tt',
+            page_title => 'Database Backend Selection',
+            status => $status,
+            connection_test => $connection_test,
+        );
+        
+        $self->log_with_details($c, 'info', 'Database mode interface accessed', {
+            current_backend => $status->{current_backend},
+            mysql_available => $status->{mysql_available},
+        });
+        
+    } catch {
+        my $error = $_;
+        $c->stash(
+            template => 'database_mode/index.tt',
+            page_title => 'Database Backend Selection - Error',
+            error_message => "Failed to load database status: $error",
+        );
+        
+        $self->log_with_details($c, 'error', 'Database mode interface error', {
+            error => $error,
+        });
+    };
+}
+
+=head2 switch_backend
+
+Switch database backend (AJAX endpoint)
+
+=cut
+
+sub switch_backend :Local :Args(1) {
+    my ($self, $c, $backend_type) = @_;
+    
+    # Check admin/developer permissions
+    unless ($c->check_user_roles(qw/admin developer/)) {
+        $c->response->status(403);
+        $c->stash(json => { success => 0, error => 'Access denied' });
+        $c->detach('View::JSON');
+    }
+    
+    try {
+        # Validate backend type
+        unless ($backend_type && ($backend_type eq 'mysql' || $backend_type eq 'sqlite')) {
+            $c->response->status(400);
+            $c->stash(json => { 
+                success => 0, 
+                error => "Invalid backend type: " . ($backend_type || 'undefined')
+            });
+            $c->detach('View::JSON');
+        }
+        
+        # Get HybridDB model
+        my $hybrid_db = $c->model('HybridDB');
+        
+        # Check if MySQL is available when switching to MySQL
+        if ($backend_type eq 'mysql' && !$hybrid_db->is_mysql_available()) {
+            $c->response->status(400);
+            $c->stash(json => { 
+                success => 0, 
+                error => 'MySQL server is not available'
+            });
+            $c->detach('View::JSON');
+        }
+        
+        # Switch backend
+        $hybrid_db->switch_backend($c, $backend_type);
+        
+        # Test new connection
+        my $connection_test = $hybrid_db->test_connection($c);
+        my $status = $hybrid_db->get_status();
+        
+        $c->stash(json => {
+            success => 1,
+            message => "Successfully switched to $backend_type backend",
+            status => $status,
+            connection_test => $connection_test,
+        });
+        
+        $self->log_with_details($c, 'info', 'Database backend switched', {
+            new_backend => $backend_type,
+            connection_test => $connection_test,
+        });
+        
+    } catch {
+        my $error = $_;
+        $c->response->status(500);
+        $c->stash(json => { 
+            success => 0, 
+            error => "Failed to switch backend: $error"
+        });
+        
+        $self->log_with_details($c, 'error', 'Database backend switch failed', {
+            requested_backend => $backend_type,
+            error => $error,
+        });
+    };
+    
+    $c->detach('View::JSON');
+}
+
+=head2 status
+
+Get current database status (AJAX endpoint)
+
+=cut
+
+sub status :Local :Args(0) {
+    my ($self, $c) = @_;
+    
+    # Check admin/developer permissions
+    unless ($c->check_user_roles(qw/admin developer/)) {
+        $c->response->status(403);
+        $c->stash(json => { success => 0, error => 'Access denied' });
+        $c->detach('View::JSON');
+    }
+    
+    try {
+        # Get HybridDB model
+        my $hybrid_db = $c->model('HybridDB');
+        my $status = $hybrid_db->get_status();
+        
+        # Test current connection
+        my $connection_test = $hybrid_db->test_connection($c);
+        
+        # Re-detect MySQL availability
+        $hybrid_db->_detect_backends($c);
+        my $updated_status = $hybrid_db->get_status();
+        
+        $c->stash(json => {
+            success => 1,
+            status => $updated_status,
+            connection_test => $connection_test,
+        });
+        
+    } catch {
+        my $error = $_;
+        $c->response->status(500);
+        $c->stash(json => { 
+            success => 0, 
+            error => "Failed to get status: $error"
+        });
+        
+        $self->log_with_details($c, 'error', 'Database status check failed', {
+            error => $error,
+        });
+    };
+    
+    $c->detach('View::JSON');
+}
+
+=head2 test_connection
+
+Test connection to specified backend (AJAX endpoint)
+
+=cut
+
+sub test_connection :Local :Args(1) {
+    my ($self, $c, $backend_type) = @_;
+    
+    # Check admin/developer permissions
+    unless ($c->check_user_roles(qw/admin developer/)) {
+        $c->response->status(403);
+        $c->stash(json => { success => 0, error => 'Access denied' });
+        $c->detach('View::JSON');
+    }
+    
+    try {
+        # Validate backend type
+        unless ($backend_type && ($backend_type eq 'mysql' || $backend_type eq 'sqlite')) {
+            $c->response->status(400);
+            $c->stash(json => { 
+                success => 0, 
+                error => "Invalid backend type: " . ($backend_type || 'undefined')
+            });
+            $c->detach('View::JSON');
+        }
+        
+        # Get HybridDB model
+        my $hybrid_db = $c->model('HybridDB');
+        
+        # Temporarily switch to test backend
+        my $original_backend = $hybrid_db->get_backend_type();
+        
+        if ($backend_type eq 'mysql' && !$hybrid_db->is_mysql_available()) {
+            $c->stash(json => { 
+                success => 0, 
+                error => 'MySQL server is not available',
+                connection_test => 0,
+            });
+            $c->detach('View::JSON');
+        }
+        
+        # Test connection
+        $hybrid_db->switch_backend($c, $backend_type);
+        my $connection_test = $hybrid_db->test_connection($c);
+        
+        # Restore original backend
+        $hybrid_db->switch_backend($c, $original_backend);
+        
+        $c->stash(json => {
+            success => 1,
+            backend_type => $backend_type,
+            connection_test => $connection_test,
+            message => $connection_test ? 
+                "Connection to $backend_type successful" : 
+                "Connection to $backend_type failed",
+        });
+        
+        $self->log_with_details($c, 'info', 'Database connection tested', {
+            backend_type => $backend_type,
+            connection_test => $connection_test,
+        });
+        
+    } catch {
+        my $error = $_;
+        $c->response->status(500);
+        $c->stash(json => { 
+            success => 0, 
+            error => "Connection test failed: $error"
+        });
+        
+        $self->log_with_details($c, 'error', 'Database connection test failed', {
+            backend_type => $backend_type,
+            error => $error,
+        });
+    };
+    
+    $c->detach('View::JSON');
+}
+
+=head2 log_with_details
+
+Enhanced logging method with structured details
+
+=cut
+
+sub log_with_details {
+    my ($self, $c, $level, $message, $details) = @_;
+    
+    my $log_entry = {
+        controller => 'DatabaseMode',
+        action => $c->action->name,
+        message => $message,
+        user => $c->user ? $c->user->username : 'anonymous',
+        session_id => $c->sessionid || 'no_session',
+        timestamp => scalar(localtime()),
+        details => $details || {},
+    };
+    
+    my $log_message = sprintf(
+        "[%s] %s - %s (User: %s, Session: %s)",
+        uc($level),
+        $log_entry->{controller},
+        $message,
+        $log_entry->{user},
+        substr($log_entry->{session_id}, 0, 8)
+    );
+    
+    if ($level eq 'error') {
+        $c->log->error($log_message);
+    } elsif ($level eq 'warn') {
+        $c->log->warn($log_message);
+    } else {
+        $c->log->info($log_message);
+    }
+    
+    # Store detailed log in stash for potential database logging
+    push @{$c->stash->{detailed_logs} ||= []}, $log_entry;
+}
+
+__PACKAGE__->meta->make_immutable;
+
+1;
+
+=head1 AUTHOR
+
+Comserv Development Team
+
+=head1 COPYRIGHT
+
+Copyright (c) 2025 Comserv. All rights reserved.
+
+=cut

--- Comserv/root/database_mode/index.tt
+++ Comserv/root/database_mode/index.tt
@@ -0,0 +1,377 @@
+[% PageVersion = 'Comserv/root/database_mode/index.tt,v 0.01 2025/01/28 shanta Exp shanta ' %]
+[% IF c.session.debug_mode == 1 %]
+    [% PageVersion %]
+[% END %]
+
+[% META title = 'Database Backend Selection - Hybrid Offline Mode' %]
+
+<div class="apiary-container">
+    <header class="feature-header">
+        <h1><i class="fas fa-database"></i> Database Backend Selection</h1>
+        <p class="context-info">Hybrid Offline Mode - Choose your database backend</p>
+        <div class="version-info">
+            <span class="badge badge-info">Hybrid Mode</span>
+            <span class="badge badge-warning">Admin Only</span>
+        </div>
+    </header>
+
+    [% IF error_message %]
+        <div class="alert alert-danger">
+            <i class="fas fa-exclamation-triangle"></i>
+            <strong>Error:</strong> [% error_message | html %]
+        </div>
+    [% END %]
+
+    <div class="feature-grid">
+        <!-- Current Status Card -->
+        <div class="feature-card">
+            <div class="feature-header">
+                <h3><i class="fas fa-info-circle"></i> Current Status</h3>
+            </div>
+            <div class="feature-content">
+                [% IF status %]
+                    <div class="status-grid">
+                        <div class="status-item">
+                            <strong>Active Backend:</strong>
+                            <span class="badge [% IF status.current_backend == 'mysql' %]badge-success[% ELSE %]badge-info[% END %]">
+                                [% status.current_backend | upper %]
+                            </span>
+                        </div>
+                        <div class="status-item">
+                            <strong>MySQL Available:</strong>
+                            <span class="badge [% IF status.mysql_available %]badge-success[% ELSE %]badge-danger[% END %]">
+                                [% IF status.mysql_available %]Yes[% ELSE %]No[% END %]
+                            </span>
+                        </div>
+                        <div class="status-item">
+                            <strong>Connection Test:</strong>
+                            <span class="badge [% IF connection_test %]badge-success[% ELSE %]badge-danger[% END %]">
+                                [% IF connection_test %]Success[% ELSE %]Failed[% END %]
+                            </span>
+                        </div>
+                        [% IF status.sqlite_path %]
+                        <div class="status-item">
+                            <strong>SQLite Path:</strong>
+                            <code>[% status.sqlite_path | html %]</code>
+                        </div>
+                        [% END %]
+                    </div>
+                [% ELSE %]
+                    <div class="alert alert-warning">
+                        <i class="fas fa-exclamation-triangle"></i>
+                        Status information not available
+                    </div>
+                [% END %]
+            </div>
+            <div class="feature-actions">
+                <button type="button" class="btn btn-secondary" onclick="refreshStatus()">
+                    <i class="fas fa-sync-alt"></i> Refresh Status
+                </button>
+            </div>
+        </div>
+
+        <!-- MySQL Backend Card -->
+        <div class="feature-card">
+            <div class="feature-header">
+                <h3><i class="fas fa-server"></i> MySQL Backend</h3>
+                [% IF status && status.mysql_available %]
+                    <span class="badge badge-success">Available</span>
+                [% ELSE %]
+                    <span class="badge badge-danger">Unavailable</span>
+                [% END %]
+            </div>
+            <div class="feature-content">
+                <div class="backend-info">
+                    <h4>Advantages:</h4>
+                    <ul>
+                        <li>Full feature compatibility</li>
+                        <li>Same schema as production</li>
+                        <li>Better performance for large datasets</li>
+                        <li>Easier synchronization</li>
+                    </ul>
+                    
+                    <h4>Requirements:</h4>
+                    <ul>
+                        <li>Local MySQL server running</li>
+                        <li>Database configuration accessible</li>
+                        <li>Network connectivity to MySQL</li>
+                    </ul>
+                </div>
+            </div>
+            <div class="feature-actions">
+                [% IF status && status.mysql_available %]
+                    <button type="button" class="btn btn-primary" onclick="switchBackend('mysql')"
+                            [% IF status.current_backend == 'mysql' %]disabled[% END %]>
+                        <i class="fas fa-exchange-alt"></i>
+                        [% IF status.current_backend == 'mysql' %]Currently Active[% ELSE %]Switch to MySQL[% END %]
+                    </button>
+                [% ELSE %]
+                    <button type="button" class="btn btn-secondary" disabled>
+                        <i class="fas fa-times"></i> MySQL Not Available
+                    </button>
+                [% END %]
+                <button type="button" class="btn btn-info" onclick="testConnection('mysql')">
+                    <i class="fas fa-plug"></i> Test Connection
+                </button>
+            </div>
+        </div>
+
+        <!-- SQLite Backend Card -->
+        <div class="feature-card">
+            <div class="feature-header">
+                <h3><i class="fas fa-cube"></i> SQLite Backend</h3>
+                <span class="badge badge-info">Always Available</span>
+            </div>
+            <div class="feature-content">
+                <div class="backend-info">
+                    <h4>Advantages:</h4>
+                    <ul>
+                        <li>No server required</li>
+                        <li>Lightweight and portable</li>
+                        <li>File-based storage</li>
+                        <li>Works offline</li>
+                    </ul>
+                    
+                    <h4>Limitations:</h4>
+                    <ul>
+                        <li>Schema mapping required</li>
+                        <li>Limited concurrent access</li>
+                        <li>Different SQL dialect</li>
+                        <li>Synchronization complexity</li>
+                    </ul>
+                </div>
+            </div>
+            <div class="feature-actions">
+                <button type="button" class="btn btn-primary" onclick="switchBackend('sqlite')"
+                        [% IF status && status.current_backend == 'sqlite' %]disabled[% END %]>
+                    <i class="fas fa-exchange-alt"></i>
+                    [% IF status && status.current_backend == 'sqlite' %]Currently Active[% ELSE %]Switch to SQLite[% END %]
+                </button>
+                <button type="button" class="btn btn-info" onclick="testConnection('sqlite')">
+                    <i class="fas fa-plug"></i> Test Connection
+                </button>
+            </div>
+        </div>
+
+        <!-- System Information Card -->
+        <div class="feature-card">
+            <div class="feature-header">
+                <h3><i class="fas fa-cogs"></i> System Information</h3>
+            </div>
+            <div class="feature-content">
+                <div class="system-info">
+                    <h4>Hybrid Database Architecture:</h4>
+                    <p>This system automatically detects available database backends and allows you to choose between local MySQL and SQLite storage.</p>
+                    
+                    <div class="alert alert-info">
+                        <strong>Production Safety:</strong> All changes are synchronized with the production MySQL database using transactional updates with rollback capability.
+                    </div>
+                    
+                    <h4>Backend Selection Guidelines:</h4>
+                    <ul>
+                        <li><strong>Use MySQL</strong> when local server is available for best compatibility</li>
+                        <li><strong>Use SQLite</strong> as fallback when MySQL is unavailable</li>
+                        <li><strong>Auto-detection</strong> will choose the best available option</li>
+                    </ul>
+                </div>
+            </div>
+            <div class="feature-actions">
+                <a href="/Documentation/developer/offline_mode_system" class="btn btn-secondary">
+                    <i class="fas fa-book"></i> View Documentation
+                </a>
+            </div>
+        </div>
+    </div>
+
+    <!-- Status Messages -->
+    <div id="status-messages" class="mt-3"></div>
+</div>
+
+<script>
+// Database backend management JavaScript
+function showMessage(message, type = 'info') {
+    const container = document.getElementById('status-messages');
+    const alertClass = type === 'error' ? 'alert-danger' : 
+                      type === 'success' ? 'alert-success' : 
+                      type === 'warning' ? 'alert-warning' : 'alert-info';
+    
+    const messageHtml = `
+        <div class="alert ${alertClass} alert-dismissible fade show" role="alert">
+            <i class="fas fa-${type === 'error' ? 'exclamation-triangle' : 
+                              type === 'success' ? 'check-circle' : 
+                              type === 'warning' ? 'exclamation-triangle' : 'info-circle'}"></i>
+            ${message}
+            <button type="button" class="btn-close" data-bs-dismiss="alert"></button>
+        </div>
+    `;
+    
+    container.innerHTML = messageHtml;
+    
+    // Auto-hide after 5 seconds
+    setTimeout(() => {
+        const alert = container.querySelector('.alert');
+        if (alert) {
+            alert.remove();
+        }
+    }, 5000);
+}
+
+function switchBackend(backendType) {
+    const button = event.target;
+    const originalText = button.innerHTML;
+    
+    // Show loading state
+    button.disabled = true;
+    button.innerHTML = '<i class="fas fa-spinner fa-spin"></i> Switching...';
+    
+    fetch(`/database_mode/switch_backend/${backendType}`, {
+        method: 'POST',
+        headers: {
+            'Content-Type': 'application/json',
+        }
+    })
+    .then(response => response.json())
+    .then(data => {
+        if (data.success) {
+            showMessage(data.message, 'success');
+            // Reload page to update status
+            setTimeout(() => {
+                window.location.reload();
+            }, 1500);
+        } else {
+            showMessage(data.error, 'error');
+            button.disabled = false;
+            button.innerHTML = originalText;
+        }
+    })
+    .catch(error => {
+        showMessage('Network error: ' + error.message, 'error');
+        button.disabled = false;
+        button.innerHTML = originalText;
+    });
+}
+
+function testConnection(backendType) {
+    const button = event.target;
+    const originalText = button.innerHTML;
+    
+    // Show loading state
+    button.disabled = true;
+    button.innerHTML = '<i class="fas fa-spinner fa-spin"></i> Testing...';
+    
+    fetch(`/database_mode/test_connection/${backendType}`, {
+        method: 'GET',
+        headers: {
+            'Content-Type': 'application/json',
+        }
+    })
+    .then(response => response.json())
+    .then(data => {
+        if (data.success) {
+            const messageType = data.connection_test ? 'success' : 'warning';
+            showMessage(data.message, messageType);
+        } else {
+            showMessage(data.error, 'error');
+        }
+        
+        button.disabled = false;
+        button.innerHTML = originalText;
+    })
+    .catch(error => {
+        showMessage('Network error: ' + error.message, 'error');
+        button.disabled = false;
+        button.innerHTML = originalText;
+    });
+}
+
+function refreshStatus() {
+    const button = event.target;
+    const originalText = button.innerHTML;
+    
+    // Show loading state
+    button.disabled = true;
+    button.innerHTML = '<i class="fas fa-spinner fa-spin"></i> Refreshing...';
+    
+    fetch('/database_mode/status', {
+        method: 'GET',
+        headers: {
+            'Content-Type': 'application/json',
+        }
+    })
+    .then(response => response.json())
+    .then(data => {
+        if (data.success) {
+            showMessage('Status refreshed successfully', 'success');
+            // Reload page to update status display
+            setTimeout(() => {
+                window.location.reload();
+            }, 1000);
+        } else {
+            showMessage(data.error, 'error');
+        }
+        
+        button.disabled = false;
+        button.innerHTML = originalText;
+    })
+    .catch(error => {
+        showMessage('Network error: ' + error.message, 'error');
+        button.disabled = false;
+        button.innerHTML = originalText;
+    });
+}
+</script>
+
+<style>
+.status-grid {
+    display: grid;
+    grid-template-columns: 1fr;
+    gap: 0.5rem;
+}
+
+.status-item {
+    display: flex;
+    justify-content: space-between;
+    align-items: center;
+    padding: 0.5rem;
+    background: var(--card-bg);
+    border-radius: 4px;
+    border: 1px solid var(--border-color);
+}
+
+.backend-info h4 {
+    color: var(--primary-color);
+    margin-top: 1rem;
+    margin-bottom: 0.5rem;
+}
+
+.backend-info ul {
+    margin-bottom: 1rem;
+}
+
+.system-info h4 {
+    color: var(--primary-color);
+    margin-top: 1rem;
+    margin-bottom: 0.5rem;
+}
+
+.feature-actions {
+    display: flex;
+    gap: 0.5rem;
+    flex-wrap: wrap;
+}
+
+.feature-actions .btn {
+    flex: 1;
+    min-width: 120px;
+}
+
+@media (max-width: 768px) {
+    .feature-actions {
+        flex-direction: column;
+    }
+    
+    .feature-actions .btn {
+        width: 100%;
+    }
+}
+</style>

