+    print "‚ùå HybridDB model failed to load: $@\n";
+}
+
+# Test DatabaseMode controller loading
+eval {
+    require Comserv::Controller::DatabaseMode;
+    print "‚úÖ DatabaseMode controller loaded successfully\n";
+};
+if ($@) {
+    print "‚ùå DatabaseMode controller failed to load: $@\n";
+}
+
+# Test basic functionality
+eval {
+    # Create a mock context object for testing
+    my $mock_c = MockContext->new();
+    
+    # Test HybridDB instantiation
+    my $hybrid_db = Comserv::Model::HybridDB->new($mock_c, {});
+    print "‚úÖ HybridDB model instantiated successfully\n";
+    
+    # Test basic methods
+    my $backend_type = $hybrid_db->get_backend_type();
+    print "‚úÖ Backend type: $backend_type\n";
+    
+    my $status = $hybrid_db->get_status();
+    print "‚úÖ Status retrieved: " . (ref($status) eq 'HASH' ? 'HASH' : 'SCALAR') . "\n";
+    
+};
+if ($@) {
+    print "‚ùå Functionality test failed: $@\n";
+}
+
+print "\nTest completed.\n";
+
+# Mock context class for testing
+package MockContext;
+
+sub new {
+    my $class = shift;
+    return bless {
+        log => MockLog->new(),
+        session => {},
+    }, $class;
+}
+
+sub log { return shift->{log}; }
+sub session { return shift->{session}; }
+
+package MockLog;
+
+sub new { return bless {}, shift; }
+sub info { my ($self, $msg) = @_; print "LOG: $msg\n"; }
+sub error { my ($self, $msg) = @_; print "ERROR: $msg\n"; }
+sub debug { my ($self, $msg) = @_; print "DEBUG: $msg\n"; }
