+package Comserv::Model::Weather;
+use Moose;
+use namespace::autoclean;
+use DBI;
+use JSON;
+use DateTime;
+use Try::Tiny;
+use Comserv::Util::Logging;
+
+=head1 NAME
+
+Comserv::Model::Weather - Weather Data Model
+
+=head1 DESCRIPTION
+
+This model handles weather configuration and data storage operations.
+
+=cut
+
+has 'dbh' => (
+    is => 'rw',
+    isa => 'DBI::db',
+    lazy => 1,
+    builder => '_build_dbh'
+);
+
+has 'logging' => (
+    is => 'ro',
+    default => sub { Comserv::Util::Logging->instance }
+);
+
+sub _build_dbh {
+    my $self = shift;
+    
+    # Load database configuration
+    my $config_file = '/home/shanta/PycharmProjects/comserv2/Comserv/db_config.json';
+    
+    if (-f $config_file) {
+        open my $fh, '<', $config_file or die "Cannot open config file: $!";
+        my $config_json = do { local $/; <$fh> };
+        close $fh;
+        
+        my $config = decode_json($config_json);
+        
+        my $dsn = "DBI:mysql:database=$config->{database};host=$config->{host};port=$config->{port}";
+        my $dbh = DBI->connect($dsn, $config->{username}, $config->{password}, {
+            RaiseError => 1,
+            AutoCommit => 1,
+            mysql_enable_utf8 => 1,
+        });
+        
+        return $dbh;
+    } else {
+        die "Database configuration file not found: $config_file";
+    }
+}
+
+=head1 METHODS
+
+=head2 get_weather_config
+
+Get the active weather configuration
+
+=cut
+
+sub get_weather_config {
+    my ($self) = @_;
+    
+    my $sql = "SELECT * FROM weather_config WHERE is_active = 1 ORDER BY id DESC LIMIT 1";
+    my $sth = $self->dbh->prepare($sql);
+    $sth->execute();
+    
+    return $sth->fetchrow_hashref();
+}
+
+=head2 save_weather_config
+
+Save or update weather configuration
+
+=cut
+
+sub save_weather_config {
+    my ($self, $config) = @_;
+    
+    # First, deactivate all existing configs
+    my $deactivate_sql = "UPDATE weather_config SET is_active = 0";
+    $self->dbh->do($deactivate_sql);
+    
+    # Insert new configuration
+    my $sql = "INSERT INTO weather_config (
+        api_service, api_key, location_method, location_value, country_code,
+        update_interval, temperature_units, language, is_active
+    ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, 1)";
+    
+    my $sth = $self->dbh->prepare($sql);
+    $sth->execute(
+        $config->{api_service},
+        $config->{api_key},
+        $config->{location_method},
+        $config->{location_value},
+        $config->{country_code} || 'US',
+        $config->{update_interval} || 30,
+        $config->{temperature_units} || 'metric',
+        $config->{language} || 'en'
+    );
+    
+    return $self->dbh->last_insert_id(undef, undef, 'weather_config', 'id');
+}
+
+=head2 get_cached_weather_data
+
+Get cached weather data
+
+=cut
+
+sub get_cached_weather_data {
+    my ($self, $data_type, $max_age_minutes) = @_;
+    
+    $max_age_minutes ||= 30;
+    
+    my $sql = "SELECT * FROM weather_data wd 
+               JOIN weather_config wc ON wd.config_id = wc.id 
+               WHERE wd.data_type = ? 
+               AND wc.is_active = 1 
+               AND wd.retrieved_at > DATE_SUB(NOW(), INTERVAL ? MINUTE)
+               ORDER BY wd.retrieved_at DESC LIMIT 1";
+    
+    my $sth = $self->dbh->prepare($sql);
+    $sth->execute($data_type, $max_age_minutes);
+    
+    my $data = $sth->fetchrow_hashref();
+    
+    if ($data && $data->{raw_data}) {
+        $data->{raw_data} = decode_json($data->{raw_data});
+    }
+    
+    return $data;
+}
+
+=head2 cache_weather_data
+
+Cache weather data
+
+=cut
+
+sub cache_weather_data {
+    my ($self, $config_id, $data_type, $weather_data) = @_;
+    
+    # Clean old data first (keep only last 100 records per type)
+    $self->_cleanup_old_weather_data($config_id, $data_type);
+    
+    if ($data_type eq 'current') {
+        return $self->_cache_current_weather($config_id, $weather_data);
+    } elsif ($data_type eq 'forecast') {
+        return $self->_cache_forecast_weather($config_id, $weather_data);
+    }
+}
+
+sub _cache_current_weather {
+    my ($self, $config_id, $data) = @_;
+    
+    my $sql = "INSERT INTO weather_data (
+        config_id, data_type, temperature, feels_like, humidity, pressure,
+        wind_speed, wind_direction, wind_gust, visibility, uv_index,
+        condition_main, condition_description, weather_icon, cloudiness,
+        sunrise, sunset, location_name, raw_data
+    ) VALUES (?, 'current', ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)";
+    
+    my $sth = $self->dbh->prepare($sql);
+    $sth->execute(
+        $config_id,
+        $data->{temperature},
+        $data->{feels_like},
+        $data->{humidity},
+        $data->{pressure},
+        $data->{wind_speed},
+        $data->{wind_direction},
+        $data->{wind_gust},
+        $data->{visibility},
+        $data->{uv_index},
+        $data->{condition_main},
+        $data->{condition_description},
+        $data->{weather_icon},
+        $data->{cloudiness},
+        $data->{sunrise},
+        $data->{sunset},
+        $data->{location_name},
+        encode_json($data->{raw_data} || {})
+    );
+    
+    return $self->dbh->last_insert_id(undef, undef, 'weather_data', 'id');
+}
+
+sub _cache_forecast_weather {
+    my ($self, $config_id, $data) = @_;
+    
+    my @inserted_ids = ();
+    
+    foreach my $forecast_item (@{$data->{forecast}}) {
+        my $sql = "INSERT INTO weather_data (
+            config_id, data_type, temperature, feels_like, humidity, pressure,
+            wind_speed, wind_direction, condition_main, condition_description,
+            weather_icon, cloudiness, precipitation, forecast_date, forecast_time,
+            location_name, raw_data
+        ) VALUES (?, 'forecast', ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)";
+        
+        my $sth = $self->dbh->prepare($sql);
+        $sth->execute(
+            $config_id,
+            $forecast_item->{temperature},
+            $forecast_item->{feels_like},
+            $forecast_item->{humidity},
+            $forecast_item->{pressure},
+            $forecast_item->{wind_speed},
+            $forecast_item->{wind_direction},
+            $forecast_item->{condition_main},
+            $forecast_item->{condition_description},
+            $forecast_item->{weather_icon},
+            $forecast_item->{cloudiness},
+            $forecast_item->{precipitation},
+            $forecast_item->{forecast_date},
+            $forecast_item->{forecast_time},
+            $data->{location_name},
+            encode_json($forecast_item)
+        );
+        
+        push @inserted_ids, $self->dbh->last_insert_id(undef, undef, 'weather_data', 'id');
+    }
+    
+    return \@inserted_ids;
+}
+
+=head2 get_weather_providers
+
+Get list of available weather providers
+
+=cut
+
+sub get_weather_providers {
+    my ($self) = @_;
+    
+    my $sql = "SELECT * FROM weather_providers WHERE is_active = 1 ORDER BY provider_name";
+    my $sth = $self->dbh->prepare($sql);
+    $sth->execute();
+    
+    return $sth->fetchall_arrayref({});
+}
+
+=head2 track_api_usage
+
+Track API usage for rate limiting
+
+=cut
+
+sub track_api_usage {
+    my ($self, $config_id, $provider_name) = @_;
+    
+    my $today = DateTime->now->ymd;
+    
+    my $sql = "INSERT INTO weather_api_usage (config_id, provider_name, usage_date, api_calls_count)
+               VALUES (?, ?, ?, 1)
+               ON DUPLICATE KEY UPDATE api_calls_count = api_calls_count + 1";
+    
+    my $sth = $self->dbh->prepare($sql);
+    $sth->execute($config_id, $provider_name, $today);
+}
+
+=head2 get_api_usage
+
+Get API usage statistics
+
+=cut
+
+sub get_api_usage {
+    my ($self, $config_id, $provider_name, $date) = @_;
+    
+    $date ||= DateTime->now->ymd;
+    
+    my $sql = "SELECT * FROM weather_api_usage 
+               WHERE config_id = ? AND provider_name = ? AND usage_date = ?";
+    
+    my $sth = $self->dbh->prepare($sql);
+    $sth->execute($config_id, $provider_name, $date);
+    
+    return $sth->fetchrow_hashref();
+}
+
+=head2 _cleanup_old_weather_data
+
+Clean up old weather data to prevent database bloat
+
+=cut
+
+sub _cleanup_old_weather_data {
+    my ($self, $config_id, $data_type) = @_;
+    
+    # Keep only the latest 100 records per data type
+    my $sql = "DELETE FROM weather_data 
+               WHERE config_id = ? AND data_type = ? 
+               AND id NOT IN (
+                   SELECT id FROM (
+                       SELECT id FROM weather_data 
+                       WHERE config_id = ? AND data_type = ? 
+                       ORDER BY retrieved_at DESC LIMIT 100
+                   ) AS keep_records
+               )";
+    
+    my $sth = $self->dbh->prepare($sql);
+    $sth->execute($config_id, $data_type, $config_id, $data_type);
+}
+
+=head2 get_weather_history
+
+Get historical weather data
+
+=cut
+
+sub get_weather_history {
+    my ($self, $data_type, $days_back) = @_;
+    
+    $days_back ||= 7;
+    
+    my $sql = "SELECT * FROM weather_data wd
+               JOIN weather_config wc ON wd.config_id = wc.id
+               WHERE wd.data_type = ? 
+               AND wc.is_active = 1
+               AND wd.retrieved_at > DATE_SUB(NOW(), INTERVAL ? DAY)
+               ORDER BY wd.retrieved_at DESC";
+    
+    my $sth = $self->dbh->prepare($sql);
+    $sth->execute($data_type, $days_back);
+    
+    return $sth->fetchall_arrayref({});
+}
+
+sub DEMOLISH {
+    my $self = shift;
+    $self->dbh->disconnect if $self->dbh;
+}
+
+__PACKAGE__->meta->make_immutable;
+
+1;
+
+=head1 AUTHOR
+
+Shanta
+
+=head1 LICENSE
+
+This library is free software. You can redistribute it and/or modify
+it under the same terms as Perl itself.
+
+=cut

--- Comserv/lib/Comserv/Controller/Weather.pm
