+sub get_weather :Path('/Apiary/weather/current') :Args(0) {
+    my ( $self, $c ) = @_;
+
+    # Initialize debug_errors array
+    $c->stash->{debug_errors} = [] unless defined $c->stash->{debug_errors};
+
+    # Log entry into the get_weather method
+    $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'get_weather', 'Entered get_weather method');
+    push @{$c->stash->{debug_errors}}, "Entered get_weather method";
+
+    eval {
+        # For now, return mock weather data
+        # In production, this would call a weather API like OpenWeatherMap
+        my $weather_data = {
+            temperature => 22,
+            condition => 'Partly Cloudy',
+            humidity => 65,
+            wind_speed => 8,
+            wind_direction => 'SW',
+            description => 'Partly cloudy with light winds',
+            icon => 'partly-cloudy',
+            timestamp => time()
+        };
+
+        # Set response as JSON
+        $c->response->content_type('application/json');
+        $c->response->body(JSON::XS->new->utf8->encode($weather_data));
+        
+        $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'get_weather', 'Weather data retrieved successfully');
+        push @{$c->stash->{debug_errors}}, "Weather data retrieved successfully";
+        
+    } catch {
+        my $error = $_;
+        $self->logging->log_with_details($c, 'error', __FILE__, __LINE__, 'get_weather', "Error retrieving weather: $error");
+        push @{$c->stash->{debug_errors}}, "Error retrieving weather: $error";
+        
+        # Return error response
+        $c->response->status(500);
+        $c->response->content_type('application/json');
+        $c->response->body(JSON::XS->new->utf8->encode({
+            error => 'Failed to retrieve weather data',
+            message => $error
+        }));
+    };
 }

