+# Search API endpoint
+sub search :Path('search') :Args(0) {
+    my ($self, $c) = @_;
+    
+    # Get search query from parameters
+    my $query = $c->request->param('q') || '';
+    
+    $self->logging->log_with_details($c, 'info', __FILE__, __LINE__, 'search', 
+        "Documentation search requested: $query");
+    
+    # Return empty results if no query
+    if (!$query || length($query) < 2) {
+        $c->response->content_type('application/json');
+        $c->response->body('{"results": [], "message": "Query too short"}');
+        return;
+    }
+    
+    # Get all documentation pages
+    my $pages = $self->documentation_pages;
+    my @results;
+    
+    # Search through pages
+    foreach my $page_name (keys %$pages) {
+        my $metadata = $pages->{$page_name};
+        my $title = $self->_format_title($page_name);
+        
+        # Check if query matches title, path, or page name
+        if ($title =~ /\Q$query\E/i || 
+            $metadata->{path} =~ /\Q$query\E/i || 
+            $page_name =~ /\Q$query\E/i) {
+            
+            push @results, {
+                name => $page_name,
+                title => $title,
+                path => $metadata->{path},
+                url => $c->uri_for($self->action_for('view'), [$page_name])->as_string,
+                site => $metadata->{site} || 'all'
+            };
+        }
+    }
+    
+    # Sort results by relevance (title matches first)
+    @results = sort {
+        my $a_title_match = $a->{title} =~ /\Q$query\E/i ? 1 : 0;
+        my $b_title_match = $b->{title} =~ /\Q$query\E/i ? 1 : 0;
+        $b_title_match <=> $a_title_match || $a->{title} cmp $b->{title};
+    } @results;
+    
+    # Limit results to 20
+    @results = splice(@results, 0, 20);
+    
+    # Return JSON response
+    require JSON;
+    my $json_response = JSON::encode_json({
+        results => \@results,
+        query => $query,
+        count => scalar(@results)
+    });
+    
+    $c->response->content_type('application/json');
+    $c->response->body($json_response);
+}
