# Get all database configurations from JSON config file
sub get_all_database_configs {
    my ($self, $c) = @_;

    # Read the database configuration file
    my $config_file = $c->path_to('Comserv', 'db_config.json');

    unless (-f $config_file) {
        $self->logging->log_with_details($c, 'error', __FILE__, __LINE__, 'get_all_database_configs',
            "Database config file not found: $config_file");
        return {};
    }

    my $json_text;
    eval {
        open my $fh, '<', $config_file or die "Cannot open $config_file: $!";
        local $/;
        $json_text = <$fh>;
        close $fh;
    };

    if ($@) {
        $self->logging->log_with_details($c, 'error', __FILE__, __LINE__, 'get_all_database_configs',
            "Error reading config file: $@");
        return {};
    }

    my $configs;
    eval {
        $configs = JSON::MaybeXS::decode_json($json_text);
    };

    if ($@) {
        $self->logging->log_with_details($c, 'error', __FILE__, __LINE__, 'get_all_database_configs',
            "Error parsing JSON config: $@");
        return {};
    }

    return $configs || {};
}

# Test database connection
sub test_database_connection {
    my ($self, $c, $db_key, $config) = @_;

    my $result = {
        connected => 0,
        error => undef
    };

    eval {
        if ($config->{db_type} eq 'sqlite') {
            # Test SQLite connection
            my $db_path = $config->{database_path};
            if (-f $db_path) {
                $result->{connected} = 1;
            } else {
                $result->{error} = "SQLite database file not found: $db_path";
            }
        } elsif ($config->{db_type} eq 'mysql') {
            # Test MySQL connection
            my $dsn = "DBI:mysql:database=" . $config->{database} . ";host=" . $config->{host} . ";port=" . $config->{port};
            my $dbh = DBI->connect($dsn, $config->{username}, $config->{password}, {
                RaiseError => 0,
                PrintError => 0,
                mysql_connect_timeout => 5
            });

            if ($dbh) {
                $result->{connected} = 1;
                $dbh->disconnect();
            } else {
                $result->{error} = $DBI::errstr || "Connection failed";
            }
        } else {
            $result->{error} = "Unsupported database type: " . $config->{db_type};
        }
    };

    if ($@) {
        $result->{error} = "Connection test failed: $@";
    }

    return $result;
}
```

