--- Comserv/root/static/css/components/proxmox-documentation.css
+++ Comserv/root/static/css/components/proxmox-documentation.css
@@ -0,0 +1,95 @@
+/* Proxmox Documentation Styles */
+.documentation-page {
+    max-width: 1200px;
+    margin: 0 auto;
+    padding: 20px;
+    font-family: Arial, sans-serif;
+}
+
+.documentation-page h1 {
+    color: #37474F;
+    border-bottom: 2px solid #E91E63;
+    padding-bottom: 10px;
+    margin-bottom: 20px;
+}
+
+.documentation-page h2 {
+    color: #455A64;
+    margin-top: 30px;
+    border-bottom: 1px solid #CFD8DC;
+    padding-bottom: 5px;
+}
+
+.documentation-page h3 {
+    color: #546E7A;
+    margin-top: 20px;
+}
+
+.documentation-page pre {
+    background-color: #263238;
+    color: #ECEFF1;
+    padding: 15px;
+    border-radius: 5px;
+    overflow-x: auto;
+    margin: 15px 0;
+}
+
+.documentation-page code {
+    font-family: 'Courier New', monospace;
+}
+
+.documentation-page .toc {
+    background-color: #ECEFF1;
+    padding: 15px;
+    border-radius: 5px;
+    margin: 20px 0;
+}
+
+.documentation-page .toc ul {
+    list-style-type: none;
+    padding-left: 20px;
+}
+
+.documentation-page .toc li {
+    margin: 5px 0;
+}
+
+.documentation-page .toc a {
+    color: #0288D1;
+    text-decoration: none;
+}
+
+.documentation-page .toc a:hover {
+    text-decoration: underline;
+}
+
+.documentation-page section {
+    margin-bottom: 40px;
+}
+
+.documentation-page .additional-resources {
+    background-color: #E1F5FE;
+    padding: 15px;
+    border-radius: 5px;
+    margin-top: 40px;
+}
+
+.documentation-page .additional-resources ul {
+    padding-left: 20px;
+}
+
+.documentation-page .additional-resources a {
+    color: #0288D1;
+    text-decoration: none;
+}
+
+.documentation-page .additional-resources a:hover {
+    text-decoration: underline;
+}
+
+.documentation-page .last-updated {
+    font-style: italic;
+    color: #78909C;
+    margin-top: 30px;
+    text-align: right;
+}

