--- Comserv/lib/Comserv/Model/Schema/Ency/Result/QueenEnhanced.pm
+++ Comserv/lib/Comserv/Model/Schema/Ency/Result/QueenEnhanced.pm
@@ -0,0 +1,473 @@
+package Comserv::Model::Schema::Ency::Result::QueenEnhanced;
+use base 'DBIx::Class::Core';
+
+__PACKAGE__->table('queens_enhanced');
+
+__PACKAGE__->add_columns(
+    id => {
+        data_type => 'integer',
+        is_auto_increment => 1,
+    },
+    tag_number => {
+        data_type => 'varchar',
+        size => 50,
+        is_nullable => 0,
+    },
+    birth_date => {
+        data_type => 'date',
+        is_nullable => 1,
+    },
+    introduction_date => {
+        data_type => 'date',
+        is_nullable => 1,
+    },
+    removal_date => {
+        data_type => 'date',
+        is_nullable => 1,
+    },
+    breed => {
+        data_type => 'varchar',
+        size => 100,
+        is_nullable => 1,
+    },
+    genetic_line => {
+        data_type => 'varchar',
+        size => 100,
+        is_nullable => 1,
+        comment => 'Genetic lineage or breeding line',
+    },
+    color_marking => {
+        data_type => 'varchar',
+        size => 50,
+        is_nullable => 1,
+        comment => 'Physical color marking on queen',
+    },
+    origin => {
+        data_type => 'varchar',
+        size => 100,
+        is_nullable => 1,
+        comment => 'Source/breeder of queen',
+    },
+    parent_queen_id => {
+        data_type => 'integer',
+        is_nullable => 1,
+        comment => 'Mother queen for genetic tracking',
+    },
+    drone_source => {
+        data_type => 'varchar',
+        size => 100,
+        is_nullable => 1,
+        comment => 'Source of drone genetics',
+    },
+    mating_status => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/virgin mated laying drone_layer superseded missing dead/]
+        },
+        default_value => 'virgin',
+    },
+    laying_status => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/laying_well laying_poor not_laying drone_layer superseded missing/]
+        },
+        is_nullable => 1,
+    },
+    performance_rating => {
+        data_type => 'integer',
+        is_nullable => 1,
+        comment => 'Performance rating 1-10',
+    },
+    temperament_rating => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/calm moderate aggressive very_aggressive/]
+        },
+        default_value => 'calm',
+    },
+    health_status => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/healthy diseased injured missing dead/]
+        },
+        default_value => 'healthy',
+    },
+    current_yard_id => {
+        data_type => 'integer',
+        is_nullable => 1,
+    },
+    current_pallet_id => {
+        data_type => 'integer',
+        is_nullable => 1,
+    },
+    current_position => {
+        data_type => 'integer',
+        is_nullable => 1,
+        comment => 'Position on pallet',
+    },
+    current_hive_configuration_id => {
+        data_type => 'integer',
+        is_nullable => 1,
+    },
+    purpose => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/production breeding replacement sale research/]
+        },
+        default_value => 'production',
+    },
+    acquisition_cost => {
+        data_type => 'decimal',
+        size => [8,2],
+        is_nullable => 1,
+    },
+    acquisition_date => {
+        data_type => 'date',
+        is_nullable => 1,
+    },
+    notes => {
+        data_type => 'text',
+        is_nullable => 1,
+    },
+    status => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/active inactive sold dead missing/]
+        },
+        default_value => 'active',
+    },
+    created_at => {
+        data_type => 'timestamp',
+        default_value => \'CURRENT_TIMESTAMP',
+    },
+    updated_at => {
+        data_type => 'timestamp',
+        default_value => \'CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP',
+    },
+    created_by => {
+        data_type => 'varchar',
+        size => 50,
+        is_nullable => 1,
+    },
+    updated_by => {
+        data_type => 'varchar',
+        size => 50,
+        is_nullable => 1,
+    },
+);
+
+__PACKAGE__->set_primary_key('id');
+
+__PACKAGE__->add_unique_constraint(
+    unique_tag_number => ['tag_number'],
+);
+
+# Relationships
+__PACKAGE__->belongs_to(
+    'parent_queen',
+    'Comserv::Model::Schema::Ency::Result::QueenEnhanced',
+    'parent_queen_id',
+    { is_deferrable => 1, on_delete => 'SET NULL', join_type => 'LEFT' }
+);
+
+__PACKAGE__->has_many(
+    'offspring_queens',
+    'Comserv::Model::Schema::Ency::Result::QueenEnhanced',
+    'parent_queen_id',
+    { cascade_delete => 0 }
+);
+
+__PACKAGE__->belongs_to(
+    'current_yard',
+    'Comserv::Model::Schema::Ency::Result::Yard',
+    'current_yard_id',
+    { is_deferrable => 1, on_delete => 'SET NULL', join_type => 'LEFT' }
+);
+
+__PACKAGE__->belongs_to(
+    'current_pallet',
+    'Comserv::Model::Schema::Ency::Result::Pallet',
+    'current_pallet_id',
+    { is_deferrable => 1, on_delete => 'SET NULL', join_type => 'LEFT' }
+);
+
+__PACKAGE__->belongs_to(
+    'current_hive_configuration',
+    'Comserv::Model::Schema::Ency::Result::HiveConfiguration',
+    'current_hive_configuration_id',
+    { is_deferrable => 1, on_delete => 'SET NULL', join_type => 'LEFT' }
+);
+
+__PACKAGE__->has_many(
+    'hive_configuration_history',
+    'Comserv::Model::Schema::Ency::Result::HiveConfigurationHistory',
+    'queen_id',
+    { cascade_delete => 1 }
+);
+
+__PACKAGE__->has_many(
+    'queen_movements',
+    'Comserv::Model::Schema::Ency::Result::QueenMovement',
+    'queen_id',
+    { cascade_delete => 1 }
+);
+
+__PACKAGE__->has_many(
+    'inspections',
+    'Comserv::Model::Schema::Ency::Result::InspectionEnhanced',
+    'queen_id',
+    { cascade_delete => 1 }
+);
+
+__PACKAGE__->has_many(
+    'production_records',
+    'Comserv::Model::Schema::Ency::Result::ProductionRecord',
+    'queen_id',
+    { cascade_delete => 1 }
+);
+
+__PACKAGE__->has_many(
+    'product_movements',
+    'Comserv::Model::Schema::Ency::Result::ProductMovement',
+    'queen_id'
+);
+
+# Custom methods
+sub display_name {
+    my $self = shift;
+    my $name = $self->tag_number;
+    $name .= " (" . $self->genetic_line . ")" if $self->genetic_line;
+    return $name;
+}
+
+sub current_location {
+    my $self = shift;
+    return unless $self->current_yard && $self->current_pallet;
+    
+    return sprintf("%s - %s - Position %d",
+        $self->current_yard->name,
+        $self->current_pallet->code,
+        $self->current_position || 0
+    );
+}
+
+sub age_in_days {
+    my $self = shift;
+    return unless $self->birth_date;
+    
+    use DateTime;
+    my $birth = DateTime->new(
+        year => substr($self->birth_date, 0, 4),
+        month => substr($self->birth_date, 5, 2),
+        day => substr($self->birth_date, 8, 2)
+    );
+    
+    return DateTime->now->delta_days($birth)->days;
+}
+
+sub is_productive {
+    my $self = shift;
+    return $self->laying_status && $self->laying_status eq 'laying_well';
+}
+
+sub needs_attention {
+    my $self = shift;
+    return $self->health_status ne 'healthy' ||
+           $self->laying_status =~ /^(not_laying|drone_layer|superseded)$/ ||
+           $self->mating_status eq 'missing';
+}
+
+sub genetic_family_tree {
+    my $self = shift;
+    my $depth = shift || 3;
+    
+    my @ancestors;
+    my $current = $self;
+    
+    for my $level (1..$depth) {
+        last unless $current->parent_queen;
+        $current = $current->parent_queen;
+        push @ancestors, {
+            level => $level,
+            queen => $current,
+            tag => $current->tag_number,
+            genetic_line => $current->genetic_line,
+        };
+    }
+    
+    return \@ancestors;
+}
+
+sub offspring_count {
+    my $self = shift;
+    return $self->offspring_queens->count;
+}
+
+sub latest_inspection {
+    my $self = shift;
+    return $self->inspections->search(
+        {},
+        { 
+            order_by => { -desc => 'inspection_date' },
+            rows => 1 
+        }
+    )->first;
+}
+
+sub production_summary {
+    my $self = shift;
+    my $year = shift || (localtime)[5] + 1900;
+    
+    return $self->production_records->search(
+        { 
+            'me.created_at' => { 
+                '>=' => "$year-01-01",
+                '<=' => "$year-12-31"
+            }
+        },
+        {
+            select => [
+                'product_type',
+                { sum => 'quantity' },
+                { count => 'id' }
+            ],
+            as => ['product_type', 'total_quantity', 'record_count'],
+            group_by => 'product_type'
+        }
+    );
+}
+
+sub movement_history {
+    my $self = shift;
+    my $limit = shift || 10;
+    
+    return $self->queen_movements->search(
+        {},
+        {
+            order_by => { -desc => 'movement_date' },
+            rows => $limit,
+            prefetch => ['from_yard', 'to_yard', 'from_pallet', 'to_pallet']
+        }
+    );
+}
+
+sub configuration_changes {
+    my $self = shift;
+    my $limit = shift || 10;
+    
+    return $self->hive_configuration_history->search(
+        {},
+        {
+            order_by => { -desc => 'change_date' },
+            rows => $limit,
+            prefetch => 'hive_configuration'
+        }
+    );
+}
+
+sub performance_metrics {
+    my $self = shift;
+    my $year = shift || (localtime)[5] + 1900;
+    
+    # Calculate various performance metrics
+    my $inspections = $self->inspections->search({
+        'me.inspection_date' => {
+            '>=' => "$year-01-01",
+            '<=' => "$year-12-31"
+        }
+    });
+    
+    my $total_inspections = $inspections->count;
+    return {} unless $total_inspections;
+    
+    my $good_inspections = $inspections->search({
+        overall_status => { -in => ['excellent', 'good'] }
+    })->count;
+    
+    my $production = $self->production_summary($year);
+    my %production_totals;
+    while (my $record = $production->next) {
+        $production_totals{$record->get_column('product_type')} = $record->get_column('total_quantity');
+    }
+    
+    return {
+        total_inspections => $total_inspections,
+        good_inspection_rate => sprintf("%.1f", ($good_inspections / $total_inspections) * 100),
+        honey_production => $production_totals{honey} || 0,
+        queen_cells_produced => $production_totals{queen_cells} || 0,
+        performance_score => $self->calculate_performance_score(),
+    };
+}
+
+sub calculate_performance_score {
+    my $self = shift;
+    
+    my $score = 0;
+    
+    # Base score from performance rating
+    $score += ($self->performance_rating || 5) * 10;
+    
+    # Laying status bonus/penalty
+    if ($self->laying_status) {
+        $score += 20 if $self->laying_status eq 'laying_well';
+        $score += 10 if $self->laying_status eq 'laying_poor';
+        $score -= 30 if $self->laying_status eq 'not_laying';
+        $score -= 50 if $self->laying_status eq 'drone_layer';
+    }
+    
+    # Temperament adjustment
+    if ($self->temperament_rating) {
+        $score += 10 if $self->temperament_rating eq 'calm';
+        $score -= 10 if $self->temperament_rating eq 'aggressive';
+        $score -= 20 if $self->temperament_rating eq 'very_aggressive';
+    }
+    
+    # Health status
+    $score -= 20 if $self->health_status ne 'healthy';
+    
+    # Age factor (peak performance typically 1-2 years)
+    my $age_days = $self->age_in_days;
+    if ($age_days) {
+        if ($age_days > 365 && $age_days < 730) {
+            $score += 10; # Prime age bonus
+        } elsif ($age_days > 1095) {
+            $score -= 15; # Old age penalty
+        }
+    }
+    
+    return $score > 100 ? 100 : ($score < 0 ? 0 : $score);
+}
+
+1;
+
+=head1 NAME
+
+Comserv::Model::Schema::Ency::Result::QueenEnhanced - Enhanced Queen tracking with comprehensive lifecycle management
+
+=head1 DESCRIPTION
+
+This enhanced queen model provides comprehensive tracking of queen bees throughout their lifecycle,
+including genetic lineage, performance metrics, location history, and production records. This is
+the central entity in the queen-centric apiary management system.
+
+Key features:
+- Genetic family tree tracking
+- Performance scoring and metrics
+- Location and movement history
+- Production record integration
+- Lifecycle status management
+- Breeding program support
+
+=head1 RELATIONSHIPS
+
+- parent_queen: Genetic mother for breeding records
+- offspring_queens: All queens bred from this queen
+- current_yard/pallet: Current physical location
+- current_hive_configuration: Current hive setup
+- hive_configuration_history: All configuration changes
+- queen_movements: Location change history
+- inspections: All inspection records
+- production_records: All products created/harvested
+- product_movements: Product transfer history
+
+=cut

--- Comserv/lib/Comserv/Model/Schema/Ency/Result/HiveConfiguration.pm
+++ Comserv/lib/Comserv/Model/Schema/Ency/Result/HiveConfiguration.pm
@@ -0,0 +1,446 @@
+package Comserv::Model::Schema::Ency::Result::HiveConfiguration;
+use base 'DBIx::Class::Core';
+
+__PACKAGE__->table('hive_configurations');
+
+__PACKAGE__->add_columns(
+    id => {
+        data_type => 'integer',
+        is_auto_increment => 1,
+    },
+    configuration_name => {
+        data_type => 'varchar',
+        size => 100,
+        is_nullable => 0,
+        comment => 'Human readable name for this configuration',
+    },
+    hive_type => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/single main 5_frame_nuc mating_nuc top divider custom/]
+        },
+        is_nullable => 0,
+        comment => 'Base hive type classification',
+    },
+    description => {
+        data_type => 'text',
+        is_nullable => 1,
+        comment => 'Detailed description of this configuration',
+    },
+    is_template => {
+        data_type => 'boolean',
+        default_value => 0,
+        comment => 'Whether this is a reusable template',
+    },
+    template_name => {
+        data_type => 'varchar',
+        size => 100,
+        is_nullable => 1,
+        comment => 'Template name if this is a template configuration',
+    },
+    season_type => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/spring summer fall winter year_round/]
+        },
+        is_nullable => 1,
+        comment => 'Optimal season for this configuration',
+    },
+    purpose => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/production breeding overwintering splitting honey_production queen_rearing/]
+        },
+        default_value => 'production',
+    },
+    total_boxes => {
+        data_type => 'integer',
+        default_value => 1,
+        comment => 'Total number of boxes in configuration',
+    },
+    brood_boxes => {
+        data_type => 'integer',
+        default_value => 1,
+        comment => 'Number of brood boxes',
+    },
+    honey_boxes => {
+        data_type => 'integer',
+        default_value => 0,
+        comment => 'Number of honey supers',
+    },
+    total_frames => {
+        data_type => 'integer',
+        is_nullable => 1,
+        comment => 'Total frames across all boxes',
+    },
+    has_queen_excluder => {
+        data_type => 'boolean',
+        default_value => 0,
+    },
+    has_feeder => {
+        data_type => 'boolean',
+        default_value => 0,
+    },
+    feeder_type => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/top_feeder boardman entrance hive_top pail none/]
+        },
+        is_nullable => 1,
+    },
+    bottom_board_type => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/screened solid slatted/]
+        },
+        default_value => 'screened',
+    },
+    inner_cover_type => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/standard ventilated none/]
+        },
+        default_value => 'standard',
+    },
+    outer_cover_type => {
+        data_type => 'enum',
+        extra => {
+            list => [qw/telescoping migratory flat metal/]
+        },
+        default_value => 'telescoping',
+    },
+    estimated_cost => {
+        data_type => 'decimal',
+        size => [8,2],
+        is_nullable => 1,
+        comment => 'Estimated cost to assemble this configuration',
+    },
+    notes => {
+        data_type => 'text',
+        is_nullable => 1,
+    },
+    is_active => {
+        data_type => 'boolean',
+        default_value => 1,
+    },
+    created_at => {
+        data_type => 'timestamp',
+        default_value => \'CURRENT_TIMESTAMP',
+    },
+    updated_at => {
+        data_type => 'timestamp',
+        default_value => \'CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP',
+    },
+    created_by => {
+        data_type => 'varchar',
+        size => 50,
+        is_nullable => 1,
+    },
+    updated_by => {
+        data_type => 'varchar',
+        size => 50,
+        is_nullable => 1,
+    },
+);
+
+__PACKAGE__->set_primary_key('id');
+
+__PACKAGE__->add_unique_constraint(
+    unique_template_name => ['template_name'],
+);
+
+# Relationships
+__PACKAGE__->has_many(
+    'configuration_boxes',
+    'Comserv::Model::Schema::Ency::Result::ConfigurationBox',
+    'configuration_id',
+    { cascade_delete => 1 }
+);
+
+__PACKAGE__->has_many(
+    'configuration_inventory',
+    'Comserv::Model::Schema::Ency::Result::ConfigurationInventory',
+    'configuration_id',
+    { cascade_delete => 1 }
+);
+
+__PACKAGE__->has_many(
+    'queens_using_config',
+    'Comserv::Model::Schema::Ency::Result::QueenEnhanced',
+    'current_hive_configuration_id'
+);
+
+__PACKAGE__->has_many(
+    'configuration_history',
+    'Comserv::Model::Schema::Ency::Result::HiveConfigurationHistory',
+    'hive_configuration_id',
+    { cascade_delete => 1 }
+);
+
+__PACKAGE__->has_many(
+    'hive_assemblies',
+    'Comserv::Model::Schema::Ency::Result::HiveAssembly',
+    'configuration_id',
+    { cascade_delete => 1 }
+);
+
+# Custom methods
+sub display_name {
+    my $self = shift;
+    return $self->configuration_name || 
+           ($self->template_name ? "Template: " . $self->template_name : 
+            ucfirst($self->hive_type) . " Configuration");
+}
+
+sub box_summary {
+    my $self = shift;
+    my @parts;
+    
+    push @parts, $self->brood_boxes . " brood" if $self->brood_boxes;
+    push @parts, $self->honey_boxes . " honey" if $self->honey_boxes;
+    
+    return join(", ", @parts) . " boxes";
+}
+
+sub equipment_list {
+    my $self = shift;
+    my @equipment;
+    
+    # Boxes
+    push @equipment, {
+        item => 'Brood Box',
+        quantity => $self->brood_boxes,
+        type => 'box'
+    } if $self->brood_boxes;
+    
+    push @equipment, {
+        item => 'Honey Super',
+        quantity => $self->honey_boxes,
+        type => 'box'
+    } if $self->honey_boxes;
+    
+    # Frames (estimated based on box type)
+    my $frames_per_box = 10; # Standard deep box
+    push @equipment, {
+        item => 'Frames',
+        quantity => $self->total_boxes * $frames_per_box,
+        type => 'frame'
+    };
+    
+    # Other equipment
+    push @equipment, {
+        item => 'Bottom Board (' . $self->bottom_board_type . ')',
+        quantity => 1,
+        type => 'component'
+    };
+    
+    push @equipment, {
+        item => 'Inner Cover (' . $self->inner_cover_type . ')',
+        quantity => 1,
+        type => 'component'
+    } if $self->inner_cover_type ne 'none';
+    
+    push @equipment, {
+        item => 'Outer Cover (' . $self->outer_cover_type . ')',
+        quantity => 1,
+        type => 'component'
+    };
+    
+    push @equipment, {
+        item => 'Queen Excluder',
+        quantity => 1,
+        type => 'component'
+    } if $self->has_queen_excluder;
+    
+    push @equipment, {
+        item => 'Feeder (' . $self->feeder_type . ')',
+        quantity => 1,
+        type => 'component'
+    } if $self->has_feeder && $self->feeder_type ne 'none';
+    
+    return \@equipment;
+}
+
+sub inventory_requirements {
+    my $self = shift;
+    
+    # Get detailed inventory from configuration_inventory relationship
+    my @requirements = $self->configuration_inventory->search(
+        {},
+        { prefetch => 'inventory_item' }
+    );
+    
+    return \@requirements;
+}
+
+sub seasonal_transitions {
+    my $self = shift;
+    
+    # Define common seasonal transitions based on hive type
+    my %transitions = (
+        single => {
+            spring => 'Add honey super if strong',
+            summer => 'Monitor for swarming, add supers as needed',
+            fall => 'Remove honey supers, prepare for winter',
+            winter => 'Reduce to single brood box if needed'
+        },
+        main => {
+            spring => 'Add honey supers, check for splitting',
+            summer => 'Multiple supers, swarm management',
+            fall => 'Harvest honey, reduce to 2 brood boxes',
+            winter => 'Maintain 2 brood boxes for cluster'
+        },
+        '5_frame_nuc' => {
+            spring => 'Build up to full hive',
+            summer => 'Transfer to 10-frame equipment',
+            fall => 'Combine if weak or overwinter as nuc',
+            winter => 'Insulate and reduce entrance'
+        }
+    );
+    
+    return $transitions{$self->hive_type} || {};
+}
+
+sub clone_as_template {
+    my $self = shift;
+    my $template_name = shift;
+    
+    my %clone_data = $self->get_columns;
+    delete $clone_data{id};
+    delete $clone_data{created_at};
+    delete $clone_data{updated_at};
+    
+    $clone_data{is_template} = 1;
+    $clone_data{template_name} = $template_name;
+    $clone_data{configuration_name} = "Template: $template_name";
+    
+    return $self->result_source->resultset->create(\%clone_data);
+}
+
+sub create_from_template {
+    my $self = shift;
+    my $new_name = shift;
+    
+    return unless $self->is_template;
+    
+    my %clone_data = $self->get_columns;
+    delete $clone_data{id};
+    delete $clone_data{created_at};
+    delete $clone_data{updated_at};
+    
+    $clone_data{is_template} = 0;
+    $clone_data{template_name} = undef;
+    $clone_data{configuration_name} = $new_name;
+    
+    my $new_config = $self->result_source->resultset->create(\%clone_data);
+    
+    # Clone associated boxes and inventory
+    for my $box ($self->configuration_boxes) {
+        my %box_data = $box->get_columns;
+        delete $box_data{id};
+        $box_data{configuration_id} = $new_config->id;
+        $new_config->create_related('configuration_boxes', \%box_data);
+    }
+    
+    for my $inventory ($self->configuration_inventory) {
+        my %inv_data = $inventory->get_columns;
+        delete $inv_data{id};
+        $inv_data{configuration_id} = $new_config->id;
+        $new_config->create_related('configuration_inventory', \%inv_data);
+    }
+    
+    return $new_config;
+}
+
+sub calculate_total_frames {
+    my $self = shift;
+    
+    my $total = 0;
+    for my $box ($self->configuration_boxes) {
+        $total += $box->frame_count;
+    }
+    
+    # Update the stored total
+    $self->update({ total_frames => $total });
+    
+    return $total;
+}
+
+sub is_suitable_for_season {
+    my $self = shift;
+    my $season = shift || $self->current_season();
+    
+    return 1 if $self->season_type eq 'year_round';
+    return $self->season_type eq $season;
+}
+
+sub current_season {
+    my $self = shift;
+    my $month = (localtime)[4] + 1; # 0-based month
+    
+    return 'spring' if $month >= 3 && $month <= 5;
+    return 'summer' if $month >= 6 && $month <= 8;
+    return 'fall' if $month >= 9 && $month <= 11;
+    return 'winter';
+}
+
+sub queens_count {
+    my $self = shift;
+    return $self->queens_using_config->count;
+}
+
+sub active_assemblies_count {
+    my $self = shift;
+    return $self->hive_assemblies->search({ is_active => 1 })->count;
+}
+
+sub estimated_setup_time {
+    my $self = shift;
+    
+    # Estimate setup time based on complexity
+    my $base_time = 15; # minutes for basic setup
+    $base_time += $self->total_boxes * 5; # 5 minutes per box
+    $base_time += 5 if $self->has_queen_excluder;
+    $base_time += 10 if $self->has_feeder;
+    
+    return $base_time;
+}
+
+1;
+
+=head1 NAME
+
+Comserv::Model::Schema::Ency::Result::HiveConfiguration - Hive configuration templates and instances
+
+=head1 DESCRIPTION
+
+This model defines hive configurations that can be used as templates or specific instances.
+It supports the queen-centric approach by defining the "recipe" for different hive types
+and their seasonal variations.
+
+Key features:
+- Template system for reusable configurations
+- Seasonal optimization recommendations
+- Equipment and inventory requirements
+- Cost estimation
+- Setup time calculation
+
+=head1 HIVE TYPES
+
+- single: Single brood box with feeder, suitable for new colonies
+- main: Multiple brood boxes for established colonies
+- 5_frame_nuc: 5-frame nucleus colony setup
+- mating_nuc: Small mating nucleus for queen breeding
+- top: Top box configuration for divider hives
+- divider: Split hive configuration
+- custom: User-defined configuration
+
+=head1 RELATIONSHIPS
+
+- configuration_boxes: Detailed box specifications
+- configuration_inventory: Required inventory items
+- queens_using_config: Queens currently using this configuration
+- configuration_history: Historical usage records
+- hive_assemblies: Physical assemblies using this configuration
+
+=cut

