+
+__PACKAGE__->table('inventory_items');
+
+__PACKAGE__->add_columns(
+    id => {
+        data_type => 'integer',
+        is_auto_increment => 1,
+    },
+    item_code => {
+        data_type => 'varchar',
+        size => 50,
+        is_nullable => 0,
+        comment => 'Unique identifier for inventory tracking',
+    },
+    item_name => {
+        data_type => 'varchar',
+        size => 200,
+        is_nullable => 0,
+    },
+    item_category => {
+        data_type => 'enum',
