+        size => [10,2],
+        default_value => 0,
+    },
+    minimum_stock => {
+        data_type => 'decimal',
+        size => [10,2],
+        default_value => 0,
+        comment => 'Reorder level',
+    },
+    maximum_stock => {
+        data_type => 'decimal',
+        size => [10,2],
+        is_nullable => 1,
+        comment => 'Maximum stock level',
+    },
+    unit_cost => {
+        data_type => 'decimal',
+        size => [8,2],
+        is_nullable => 1,
+    },
+    supplier => {
+        data_type => 'varchar',
+        size => 100,
+        is_nullable => 1,
+    },
+    supplier_part_number => {
