+=head2 get_backend_for_data_type
+
+Get the best available backend for a specific data type
+Supports multiple simultaneous active backends based on data routing requirements
+
+=cut
+
+sub get_backend_for_data_type {
+    my ($self, $c, $data_type, $privacy_level) = @_;
+    
+    $data_type ||= 'ency';  # Default to ency data
+    $privacy_level ||= 'shared';  # Default to shared data
+    
+    $self->logging->log_with_details($c, __FILE__, __LINE__,
+        'get_backend_for_data_type',
+        "Selecting backend for data_type: $data_type, privacy_level: $privacy_level");
+    
+    # Find all backends that support this data type
+    my @suitable_backends = ();
+    
+    foreach my $backend_name (keys %{$self->{available_backends}}) {
+        my $backend = $self->{available_backends}->{$backend_name};
+        
+        # Skip unavailable backends
+        next unless $backend->{available};
+        
+        # Check if backend supports this data type
+        my $config = $backend->{config};
+        if ($config->{data_types} && ref($config->{data_types}) eq 'ARRAY') {
+            my @data_types = @{$config->{data_types}};
+            
+            # Check if backend supports the requested data type and privacy level
+            if (grep { $_ eq $data_type } @data_types) {
+                # For privacy routing
+                if ($privacy_level eq 'private' && grep { $_ eq 'private' } @data_types) {
+                    push @suitable_backends, {
+                        name => $backend_name,
+                        priority => $config->{priority} || 999,
+                        backend => $backend
+                    };
+                } elsif ($privacy_level eq 'shared' && (grep { $_ eq 'shared' } @data_types || grep { $_ eq 'production' } @data_types)) {
+                    push @suitable_backends, {
+                        name => $backend_name,
+                        priority => $config->{priority} || 999,
+                        backend => $backend
+                    };
+                } elsif ($privacy_level eq 'offline' && grep { $_ eq 'offline' } @data_types) {
+                    push @suitable_backends, {
+                        name => $backend_name,
+                        priority => $config->{priority} || 999,
+                        backend => $backend
+                    };
+                }
+            }
+        }
+    }
+    
+    # Sort by priority (lower number = higher priority)
+    @suitable_backends = sort { $a->{priority} <=> $b->{priority} } @suitable_backends;
+    
+    if (@suitable_backends) {
+        my $selected = $suitable_backends[0];
+        $self->logging->log_with_details($c, __FILE__, __LINE__,
+            'get_backend_for_data_type',
+            "Selected backend: $selected->{name} (priority: $selected->{priority}) for $data_type/$privacy_level");
+        return $selected->{name};
+    }
+    
+    # Fallback to default backend
+    $self->logging->log_with_details($c, __FILE__, __LINE__,
+        'get_backend_for_data_type',
+        "No suitable backend found for $data_type/$privacy_level, using default: " . ($self->{backend_type} || 'unknown'));
+    
+    return $self->{backend_type} || 'sqlite_offline';
+}
+
+=head2 get_active_backends
+
+Get all currently active backends with their data type assignments
+
+=cut
+
+sub get_active_backends {
+    my ($self, $c) = @_;
+    
+    my %active_backends = ();
+    
+    foreach my $backend_name (keys %{$self->{available_backends}}) {
+        my $backend = $self->{available_backends}->{$backend_name};
+        
+        if ($backend->{available}) {
+            $active_backends{$backend_name} = {
+                type => $backend->{type},
+                config => $backend->{config},
+                data_types => $backend->{config}->{data_types} || [],
+                priority => $backend->{config}->{priority} || 999,
+                description => $backend->{config}->{description} || $backend_name
+            };
+        }
+    }
+    
+    return \%active_backends;
+}
+
+=head2 route_data_to_backend
+
+Route specific data operation to appropriate backend based on data type and privacy
+
+=cut
+
+sub route_data_to_backend {
+    my ($self, $c, $operation_type, $data_type, $privacy_level) = @_;
+    
+    # Determine backend based on operation and data characteristics
+    my $backend_name = $self->get_backend_for_data_type($c, $data_type, $privacy_level);
+    
+    $self->logging->log_with_details($c, __FILE__, __LINE__,
+        'route_data_to_backend',
+        "Routing $operation_type operation for $data_type/$privacy_level to backend: $backend_name");
+    
+    return $backend_name;
 }

