currentFile.content:
/* Universal Container System */
/* File: base-containers.css - Theme-agnostic container classes */
/* Uses existing theme variables to ensure consistency across all themes */

/* Standard Container Structure */
.apiary-container {
    max-width: 1400px;
    margin: 0 auto;
    padding: 20px;
    background: linear-gradient(135deg, var(--background-color) 0%);
    min-height: calc(100vh - 200px);
}

/* Header Styles */
.apiary-header {
    background: linear-gradient(135deg, var(--primary-color) 0%, var(--secondary-color) 100%);
    color: var(--text-color);
    padding: 30px;
    border-radius: 12px;
    margin-bottom: 30px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}

.apiary-header h1,
.apiary-header h2 {
    margin: 0 0 15px 0;
    font-size: 2.5rem;
    font-weight: 700;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
    color: var(--text-color);
}

.apiary-header h1 i,
.apiary-header h2 i {
    color: var(--accent-color);
    margin-right: 15px;
    text-shadow: 1px 1px 2px rgba(0,0,0,0.5);
}

.apiary-context {
    display: flex;
    flex-wrap: wrap;
    gap: 20px;
    font-size: 1rem;
}

.context-item {
    background: rgba(255,255,255,0.2);
    padding: 8px 15px;
    border-radius: 20px;
    backdrop-filter: blur(10px);
    color: var(--text-color);
}

.status-active {
    color: var(--accent-color);
    font-weight: bold;
}

/* Dashboard Stats */
.dashboard-stats {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 20px;
    margin-bottom: 30px;
}

.stat-card {
    background: var(--background-color);
    border-radius: 12px;
    padding: 25px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
    border-left: 5px solid var(--primary-color);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.stat-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
}

.stat-card.hives {
    border-left-color: var(--primary-color);
}

.stat-card.inspections {
    border-left-color: var(--accent-color);
}

.stat-card.todos {
    border-left-color: var(--warning-color);
}

.stat-card.inventory {
    border-left-color: var(--secondary-color);
}

/* Schema comparison specific stat cards */
.stat-card.databases {
    border-left-color: var(--primary-color);
}

.stat-card.connected {
    border-left-color: var(--success-color);
}

.stat-card.tables {
    border-left-color: var(--accent-color);
}

.stat-card.results {
    border-left-color: var(--success-color);
}

.stat-card .stat-icon {
    font-size: 2.5rem;
    color: var(--primary-color);
    margin-bottom: 15px;
}

.stat-card.inspections .stat-icon {
    color: var(--accent-color);
}

.stat-card.todos .stat-icon {
    color: var(--warning-color);
}

.stat-card.inventory .stat-icon {
    color: var(--secondary-color);
}

/* Schema comparison specific stat card icons */
.stat-card.databases .stat-icon {
    color: var(--primary-color);
}

.stat-card.connected .stat-icon {
    color: var(--success-color);
}

.stat-card.tables .stat-icon {
    color: var(--accent-color);
}

.stat-card.results .stat-icon {
    color: var(--success-color);
}

.stat-content {
    display: flex;
    flex-direction: column;
}

.stat-number {
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--text-color);
    line-height: 1;
}

.stat-label {
    font-size: 1rem;
    color: #666;
    margin-top: 5px;
}

.stat-detail {
    font-size: 0.9rem;
    color: #888;
    margin-top: 3px;
}

/* Quick Actions */
.quick-actions {
    background: var(--background-color);
    border-radius: 12px;
    padding: 25px;
    margin-bottom: 30px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
}

.quick-actions h3 {
    margin: 0 0 20px 0;
    color: var(--text-color);
    font-size: 1.5rem;
}

.quick-actions h3 i {
    color: var(--accent-color);
    margin-right: 10px;
}

.action-buttons {
    display: flex;
    flex-wrap: wrap;
    gap: 15px;
}

/* Feature Grid */
.feature-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: 25px;
    margin-bottom: 30px;
}

.feature-card {
    background: var(--background-color);
    border-radius: 12px;
    overflow: hidden;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    border-top: 4px solid var(--primary-color);
}

.feature-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
}

.feature-card.readonly {
    border-top-color: #ccc;
    opacity: 0.8;
}

.feature-header {
    background: linear-gradient(135deg, var(--table-header-bg) 0%);
    padding: 20px;
    border-bottom: 1px solid var(--border-color);
}

.feature-header h3, .feature-header h4 {
    margin: 0;
    color: var(--text-color);
    font-size: 1.3rem;
    display: flex;
    align-items: center;
}

.feature-icon {
    font-size: 1.5rem;
    color: var(--primary-color);
    margin-right: 12px;
}

.feature-content {
    padding: 20px;
}

.feature-content p {
    margin: 0 0 15px 0;
    color: var(--text-color);
    line-height: 1.6;
}

.feature-list {
    list-style: none;
    padding: 0;
    margin: 0;
}

.feature-list li {
    padding: 5px 0;
    color: var(--text-color);
    position: relative;
    padding-left: 20px;
}

.feature-list li:before {
    content: "•";
    position: absolute;
    left: 0;
    top: 5px;
    color: var(--accent-color);
}

.feature-actions {
    padding: 20px;
    background: var(--table-header-bg);
    border-top: 1px solid var(--border-color);
    display: flex;
    gap: 10px;
    flex-wrap: wrap;
}

/* Button Styles */
.btn {
    display: inline-flex;
    align-items: center;
    padding: 10px 20px;
    border: none;
    border-radius: 8px;
    text-decoration: none;
    font-weight: 500;
    transition: all 0.3s ease;
    cursor: pointer;
    font-size: 0.95rem;
}

.btn i {
    margin-right: 8px;
}

.btn-primary {
    background: var(--primary-color);
    color: var(--text-color);
}

.btn-primary:hover {
    background: var(--link-hover-color);
    transform: translateY(-2px);
}

.btn-secondary {
    background: var(--secondary-color);
    color: var(--text-color);
}

.btn-secondary:hover {
    background: var(--accent-color);
    transform: translateY(-2px);
}

.btn-success {
    background: var(--success-color);
    color: white;
}

.btn-success:hover {
    background: #218838;
    transform: translateY(-2px);
}

.btn-info {
    background: var(--link-color);
    color: white;
}

.btn-info:hover {
    background: var(--link-hover-color);
    transform: translateY(-2px);
}

.btn-warning {
    background: var(--warning-color);
    color: var(--text-color);
}

.btn-warning:hover {
    background: #e0a800;
    transform: translateY(-2px);
}

.btn-outline {
    background: transparent;
    color: var(--primary-color);
    border: 2px solid var(--primary-color);
}

.btn-outline:hover {
    background: var(--primary-color);
    color: white;
}

.btn-sm {
    padding: 8px 16px;
    font-size: 0.9rem;
}

/* Alert Styles */
.alert {
    padding: 15px 20px;
    border-radius: 8px;
    margin-bottom: 20px;
    border-left: 4px solid;
}

.alert-info {
    background: #e7f3ff;
    border-left-color: var(--link-color);
    color: #0c5460;
}

.alert-warning {
    background: #fff3cd;
    border-left-color: var(--warning-color);
    color: #856404;
}

.alert h3 {
    margin: 0 0 10px 0;
    font-size: 1.2rem;
}

/* Public View Styles */
.public-view {
    text-align: center;
    max-width: 800px;
    margin: 0 auto;
}

.welcome-section h2 {
    color: var(--text-color);
    font-size: 2.2rem;
    margin-bottom: 20px;
}

.welcome-section h2 i {
    color: var(--accent-color);
    margin-right: 15px;
}

.info-cards {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 20px;
    margin: 30px 0;
}

.info-card {
    background: var(--background-color);
    padding: 25px;
    border-radius: 12px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
    text-align: center;
}

.info-icon {
    font-size: 3rem;
    color: var(--primary-color);
    margin-bottom: 15px;
}

.info-card h3 {
    color: var(--text-color);
    margin: 0 0 15px 0;
}

.access-section {
    background: var(--background-color);
    padding: 30px;
    border-radius: 12px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
    margin-top: 30px;
}

.access-buttons {
    display: flex;
    justify-content: center;
    gap: 15px;
    margin-top: 20px;
}

/* Admin Section */
.admin-section {
    background: var(--background-color);
    border-radius: 12px;
    padding: 25px;
    margin-top: 30px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
    border-top: 4px solid var(--text-color);
}

.admin-section h3 {
    color: var(--text-color);
    margin: 0 0 20px 0;
    font-size: 1.5rem;
}

.admin-section h3 i {
    color: var(--primary-color);
    margin-right: 10px;
}

.admin-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 20px;
}

.admin-card {
    background: var(--table-header-bg);
    padding: 20px;
    border-radius: 8px;
    border-left: 4px solid var(--primary-color);
}

.admin-card h4 {
    margin: 0 0 15px 0;
    color: var(--text-color);
}

.admin-card ul {
    list-style: none;
    padding: 0;
    margin: 0;
}

.admin-card li {
    padding: 5px 0;
}

.admin-card a {
    color: var(--link-color);
    text-decoration: none;
    transition: color 0.3s ease;
}

.admin-card a:hover {
    color: var(--link-hover-color);
}

/* Debug Styles */
.debug-messages {
    background: var(--table-header-bg);
    border: 1px solid var(--border-color);
    border-radius: 8px;
    padding: 15px;
    margin-bottom: 20px;
    font-family: monospace;
}

.debug {
    color: #6c757d;
    font-size: 0.9rem;
    margin: 5px 0;
}

/* Graph Container Styles */
.graph-container {
    border: 1px solid var(--border-color);
    padding: 10px;
    margin: 10px;
    position: static;
    z-index: auto;
    background: var(--background-color);
    border-radius: 8px;
    box-shadow: 0 2px 8px rgba(0,0,0,0.1);
}

.graph-container iframe {
    width: 100%;
    height: 600px;
    border: none;
    border-radius: 4px;
}

.no-data-message {
    text-align: center;
    padding: 40px 20px;
    background: linear-gradient(135deg, var(--background-color) 0%);
    border-radius: 8px;
    border: 2px dashed var(--primary-color);
}

.no-data-message a {
    display: inline-block;
    padding: 15px 30px;
    background: var(--primary-color);
    color: white;
    text-decoration: none;
    border-radius: 8px;
    font-weight: 500;
    transition: all 0.3s ease;
}

.no-data-message a:hover {
    background: var(--link-hover-color);
    transform: translateY(-2px);
}

/* Animation for loading states */
@keyframes pulse {
    0% { opacity: 1; }
    50% { opacity: 0.5; }
    100% { opacity: 1; }
}

.loading {
    animation: pulse 1.5s ease-in-out infinite;
}

/* Responsive Design */
@media (max-width: 768px) {
    .apiary-container {
        padding: 15px;
    }

    .apiary-header {
        padding: 20px;
        text-align: center;
    }

    .apiary-header h1 {
        font-size: 2rem;
    }

    .apiary-context {
        justify-content: center;
    }

    .dashboard-stats {
        grid-template-columns: 1fr;
    }

    .feature-grid {
        grid-template-columns: 1fr;
    }

    .action-buttons {
        justify-content: center;
    }

    .access-buttons {
        flex-direction: column;
        align-items: center;
    }

    .admin-grid {
        grid-template-columns: 1fr;
    }
}

@media (max-width: 480px) {
    .apiary-header h1 {
        font-size: 1.8rem;
    }

    .stat-number {
        font-size: 2rem;
    }

    .feature-actions {
        flex-direction: column;
    }

    .btn {
        justify-content: center;
        width: 100%;
    }
}

