    background: linear-gradient(135deg, var(--primary-color) 0%, var(--secondary-color) 100%);
    color: var(--text-color);
    padding: 30px;
    border-radius: 12px;
    margin-bottom: 30px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}

.apiary-header h1,
.apiary-header h2 {
    margin: 0 0 15px 0;
    font-size: 2.5rem;
    font-weight: 700;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
    color: var(--text-color);
}

.apiary-header h1 i,
.apiary-header h2 i {
    color: var(--accent-color);
    margin-right: 15px;
    text-shadow: 1px 1px 2px rgba(0,0,0,0.5);
}

.apiary-context {
    display: flex;
    flex-wrap: wrap;
    gap: 20px;
    font-size: 1rem;
}

.context-item {
    background: rgba(255,255,255,0.2);
    padding: 8px 15px;
    border-radius: 20px;
    backdrop-filter: blur(10px);
    color: var(--text-color);
}

.status-active {
    color: var(--accent-color);
    font-weight: bold;
}

