    border-left: 4px solid;
}

.alert-info {
    background: #e7f3ff;
    border-left-color: var(--link-color);
    color: #0c5460;
}

.alert-warning {
    background: #fff3cd;
    border-left-color: var(--warning-color);
    color: #856404;
}

.alert h3 {
    margin: 0 0 10px 0;
    font-size: 1.2rem;
}

