--- Comserv/root/static/css/components/admin-menu.css
+++ Comserv/root/static/css/components/admin-menu.css
@@ -0,0 +1,148 @@
+/* Admin Menu Styling */
+
+/* Basic menu styling */
+.horizontal-menu {
+    list-style-type: none;
+    margin: 0;
+    padding: 0;
+    background-color: #333;
+    overflow: hidden;
+}
+
+.horizontal-menu li {
+    float: left;
+}
+
+.horizontal-menu li a {
+    display: block;
+    color: white;
+    text-align: center;
+    padding: 14px 16px;
+    text-decoration: none;
+}
+
+/* Dropdown styling */
+.horizontal-dropdown {
+    position: relative;
+    display: inline-block;
+}
+
+.dropdown-content {
+    display: none;
+    position: absolute;
+    background-color: #f9f9f9;
+    min-width: 250px;
+    box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
+    z-index: 1000;
+    max-height: 80vh;
+    overflow-y: auto;
+}
+
+.horizontal-dropdown:hover .dropdown-content {
+    display: block;
+}
+
+/* Submenu styling */
+.submenu-item {
+    position: relative;
+    display: block;
+    padding: 5px 0;
+}
+
+.submenu-header {
+    display: block;
+    padding: 8px 16px;
+    font-weight: bold;
+    color: #333;
+}
+
+.submenu {
+    display: none;
+    position: absolute;
+    left: 100%;
+    top: 0;
+    background-color: #f9f9f9;
+    min-width: 220px;
+    box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
+    z-index: 1001;
+    max-height: 80vh;
+    overflow-y: auto;
+}
+
+.submenu-item:hover .submenu {
+    display: block;
+}
+
+/* Right edge detection */
+@media (min-width: 768px) {
+    .submenu-item:nth-child(n+5) .submenu {
+        right: 100%;
+        left: auto;
+    }
+}
+
+/* Additional submenu styling */
+.submenu {
+    border: 1px solid #ddd;
+}
+
+.submenu a {
+    white-space: nowrap;
+    overflow: hidden;
+    text-overflow: ellipsis;
+    max-width: 300px;
+    padding: 8px 16px;
+    text-decoration: none;
+    color: #333;
+    display: block;
+}
+
+/* Submenu section styling */
+.submenu-section {
+    border-top: 1px solid #eee;
+    margin-top: 5px;
+    padding-top: 5px;
+}
+
+.submenu-section-title {
+    display: block;
+    padding: 8px 16px;
+    font-weight: bold;
+    color: #555;
+    background-color: #f0f0f0;
+}
+
+/* Improve hover states */
+.submenu a:hover {
+    background-color: #e9ecef;
+}
+
+/* Mobile responsiveness */
+@media (max-width: 767px) {
+    .submenu {
+        position: static;
+        width: 100%;
+        box-shadow: none;
+        border-left: 3px solid #3a5a78;
+    }
+    
+    .dropdown-content {
+        width: 100%;
+        position: relative;
+    }
+}
+
+/* Improved visibility for active menu items */
+.submenu a:active, 
+.submenu a:focus {
+    background-color: #d0d8e0;
+    outline: none;
+}
+
+/* Icon styling */
+.submenu a i, 
+.submenu-header i {
+    width: 20px;
+    text-align: center;
+    margin-right: 8px;
+}

--- Comserv/root/static/js/admin-menu.js
+++ Comserv/root/static/js/admin-menu.js
@@ -0,0 +1,45 @@
+// JavaScript to handle smart positioning of admin submenus
+document.addEventListener('DOMContentLoaded', function() {
+    const submenuItems = document.querySelectorAll('.submenu-item');
+    
+    submenuItems.forEach(item => {
+        const submenu = item.querySelector('.submenu');
+        if (!submenu) return;
+        
+        item.addEventListener('mouseenter', function() {
+            // Reset position first
+            submenu.style.left = '';
+            submenu.style.right = '';
+            submenu.style.top = '';
+            
+            // Get the submenu's position
+            const rect = submenu.getBoundingClientRect();
+            const windowWidth = window.innerWidth;
+            const windowHeight = window.innerHeight;
+            
+            // If the submenu would go off the right edge of the screen
+            if (rect.right > windowWidth - 20) {
+                submenu.style.left = 'auto';
+                submenu.style.right = '100%';
+            } else {
+                submenu.style.left = '100%';
+                submenu.style.right = 'auto';
+            }
+            
+            // If the submenu would go off the bottom of the screen
+            if (rect.bottom > windowHeight - 20) {
+                const overflow = Math.min(rect.bottom - windowHeight + 20, rect.height - 100);
+                if (overflow > 0) {
+                    submenu.style.top = `-${overflow}px`;
+                }
+            }
+        });
+    });
+});
+
+// Function to activate a site
+function activateSite(siteName) {
+    // This function can be expanded based on requirements
+    console.log('Activating site:', siteName);
+    // Additional site activation logic can be added here
+}

