+package Comserv::Controller::CloudflareAPI;
+use Moose;
+use namespace::autoclean;
+use JSON;
+use Try::Tiny;
+use IPC::Run3;
+
+BEGIN { extends 'Catalyst::Controller'; }
+
+=head1 NAME
+
+Comserv::Controller::CloudflareAPI - Cloudflare API Controller for Comserv2
+
+=head1 DESCRIPTION
+
+This controller provides a bridge between the Comserv2 application and the
+Cloudflare API, using the CloudflareManager.py module for role-based access control.
+
+=head1 METHODS
+
+=cut
+
+=head2 index
+
+The main entry point for the Cloudflare API controller.
+
+=cut
+
+sub index :Path :Args(0) {
+    my ($self, $c) = @_;
+    
+    # Check if user is logged in
+    unless ($c->user_exists) {
+        $c->stash(error_msg => 'You need to be logged in to access this page');
+        $c->detach('/error');
+        return;
+    }
+    
+    # Check if user has admin role
+    unless ($c->check_user_roles(qw/admin developer editor/)) {
+        $c->stash(error_msg => 'You need to be an admin, developer, or editor to access this page');
+        $c->detach('/error');
+        return;
+    }
+    
+    # Get sites the user has access to
+    my @sites = $self->_get_user_sites($c);
+    
+    $c->stash(
+        template => 'cloudflare/index.tt',
+        sites => \@sites,
+    );
+}
+
+=head2 dns_records
+
+List DNS records for a domain.
+
+=cut
+
+sub dns_records :Path('dns') :Args(1) {
+    my ($self, $c, $domain) = @_;
+    
+    # Check if user is logged in
+    unless ($c->user_exists) {
+        $c->stash(json => { error => 'Authentication required' });
+        $c->detach('/api/error');
+        return;
+    }
+    
+    # Get user email
+    my $user_email = $c->user->email;
+    
+    # Call the Python module to list DNS records
+    my $records = $self->_call_cloudflare_manager(
+        'list_dns_records',
+        $user_email,
+        $domain
+    );
+    
+    if ($records->{error}) {
+        $c->stash(json => { error => $records->{error} });
+        $c->detach('/api/error');
+        return;
+    }
+    
+    $c->stash(
+        json => {
+            success => 1,
+            records => $records->{result},
+            domain => $domain
+        }
+    );
+    
+    $c->forward('View::JSON');
+}
+
+=head2 create_dns_record
+
+Create a new DNS record.
+
+=cut
+
+sub create_dns_record :Path('dns/create') :Args(0) {
+    my ($self, $c) = @_;
+    
+    # Check if user is logged in
+    unless ($c->user_exists) {
+        $c->stash(json => { error => 'Authentication required' });
+        $c->detach('/api/error');
+        return;
+    }
+    
+    # Get user email
+    my $user_email = $c->user->email;
+    
+    # Get parameters
+    my $domain = $c->req->params->{domain};
+    my $record_type = $c->req->params->{type};
+    my $name = $c->req->params->{name};
+    my $content = $c->req->params->{content};
+    my $ttl = $c->req->params->{ttl} || 1;
+    my $proxied = $c->req->params->{proxied} ? 1 : 0;
+    
+    # Validate parameters
+    unless ($domain && $record_type && $name && $content) {
+        $c->stash(json => { error => 'Missing required parameters' });
+        $c->detach('/api/error');
+        return;
+    }
+    
+    # Call the Python module to create DNS record
+    my $result = $self->_call_cloudflare_manager(
+        'create_dns_record',
+        $user_email,
+        $domain,
+        $record_type,
+        $name,
+        $content,
+        $ttl,
+        $proxied
+    );
+    
+    if ($result->{error}) {
+        $c->stash(json => { error => $result->{error} });
+        $c->detach('/api/error');
+        return;
+    }
+    
+    $c->stash(
+        json => {
+            success => 1,
+            record => $result->{result},
+            message => "DNS record created successfully"
+        }
+    );
+    
+    $c->forward('View::JSON');
+}
+
+=head2 update_dns_record
+
+Update an existing DNS record.
+
+=cut
+
+sub update_dns_record :Path('dns/update') :Args(0) {
+    my ($self, $c) = @_;
+    
+    # Check if user is logged in
+    unless ($c->user_exists) {
+        $c->stash(json => { error => 'Authentication required' });
+        $c->detach('/api/error');
+        return;
+    }
+    
+    # Get user email
+    my $user_email = $c->user->email;
+    
+    # Get parameters
+    my $domain = $c->req->params->{domain};
+    my $record_id = $c->req->params->{record_id};
+    my $record_type = $c->req->params->{type};
+    my $name = $c->req->params->{name};
+    my $content = $c->req->params->{content};
+    my $ttl = $c->req->params->{ttl} || 1;
+    my $proxied = $c->req->params->{proxied} ? 1 : 0;
+    
+    # Validate parameters
+    unless ($domain && $record_id && $record_type && $name && $content) {
+        $c->stash(json => { error => 'Missing required parameters' });
+        $c->detach('/api/error');
+        return;
+    }
+    
+    # Call the Python module to update DNS record
+    my $result = $self->_call_cloudflare_manager(
+        'update_dns_record',
+        $user_email,
+        $domain,
+        $record_id,
+        $record_type,
+        $name,
+        $content,
+        $ttl,
+        $proxied
+    );
+    
+    if ($result->{error}) {
+        $c->stash(json => { error => $result->{error} });
+        $c->detach('/api/error');
+        return;
+    }
+    
+    $c->stash(
+        json => {
+            success => 1,
+            record => $result->{result},
+            message => "DNS record updated successfully"
+        }
+    );
+    
+    $c->forward('View::JSON');
+}
+
+=head2 delete_dns_record
+
+Delete a DNS record.
+
+=cut
+
+sub delete_dns_record :Path('dns/delete') :Args(0) {
+    my ($self, $c) = @_;
+    
+    # Check if user is logged in
+    unless ($c->user_exists) {
+        $c->stash(json => { error => 'Authentication required' });
+        $c->detach('/api/error');
+        return;
+    }
+    
+    # Get user email
+    my $user_email = $c->user->email;
+    
+    # Get parameters
+    my $domain = $c->req->params->{domain};
+    my $record_id = $c->req->params->{record_id};
+    
+    # Validate parameters
+    unless ($domain && $record_id) {
+        $c->stash(json => { error => 'Missing required parameters' });
+        $c->detach('/api/error');
+        return;
+    }
+    
+    # Call the Python module to delete DNS record
+    my $result = $self->_call_cloudflare_manager(
+        'delete_dns_record',
+        $user_email,
+        $domain,
+        $record_id
+    );
+    
+    if ($result->{error}) {
+        $c->stash(json => { error => $result->{error} });
+        $c->detach('/api/error');
+        return;
+    }
+    
+    $c->stash(
+        json => {
+            success => 1,
+            message => "DNS record deleted successfully"
+        }
+    );
+    
+    $c->forward('View::JSON');
+}
+
+=head2 purge_cache
+
+Purge the cache for a domain.
+
+=cut
+
+sub purge_cache :Path('cache/purge') :Args(0) {
+    my ($self, $c) = @_;
+    
+    # Check if user is logged in
+    unless ($c->user_exists) {
+        $c->stash(json => { error => 'Authentication required' });
+        $c->detach('/api/error');
+        return;
+    }
+    
+    # Get user email
+    my $user_email = $c->user->email;
+    
+    # Get parameters
+    my $domain = $c->req->params->{domain};
+    
+    # Validate parameters
+    unless ($domain) {
+        $c->stash(json => { error => 'Missing required parameters' });
+        $c->detach('/api/error');
+        return;
+    }
+    
+    # Call the Python module to purge cache
+    my $result = $self->_call_cloudflare_manager(
+        'purge_cache',
+        $user_email,
+        $domain
+    );
+    
+    if ($result->{error}) {
+        $c->stash(json => { error => $result->{error} });
+        $c->detach('/api/error');
+        return;
+    }
+    
+    $c->stash(
+        json => {
+            success => 1,
+            message => "Cache purged successfully for $domain"
+        }
+    );
+    
+    $c->forward('View::JSON');
+}
+
+# Private methods
+
+# Get sites the user has access to
+sub _get_user_sites {
+    my ($self, $c) = @_;
+    
+    my @sites;
+    
+    # If user is admin, get all sites
+    if ($c->check_user_roles('admin')) {
+        @sites = $c->model('DB::Site')->all();
+    }
+    # Otherwise, get sites the user has access to
+    else {
+        # This is a placeholder. In a real implementation, you would:
+        # 1. Get the user's roles
+        # 2. Get the sites the user has access to based on their roles
+        
+        # For now, just get all sites
+        @sites = $c->model('DB::Site')->all();
+    }
+    
+    return @sites;
+}
+
+# Call the CloudflareManager.py module
+sub _call_cloudflare_manager {
+    my ($self, $method, @args) = @_;
+    
+    # Prepare the command
+    my $script_path = $ENV{CATALYST_HOME} . "/lib/Comserv/CloudflareManager.py";
+    my $python_cmd = "python3";
+    
+    # Prepare the Python code to execute
+    my $python_code = qq{
+import sys
+sys.path.append("$ENV{CATALYST_HOME}/lib")
+from Comserv.CloudflareManager import CloudflareRoleManager
+
+try:
+    manager = CloudflareRoleManager()
+    result = manager.$method(@{[join(',', map { "'$_'" } @args)]})
+    import json
+    print(json.dumps({"success": True, "result": result}))
+except Exception as e:
+    import json
+    print(json.dumps({"success": False, "error": str(e)}))
+};
+    
+    # Execute the Python code
+    my ($stdout, $stderr);
+    try {
+        run3([$python_cmd, '-c', $python_code], \undef, \$stdout, \$stderr);
+        
+        # Parse the JSON response
+        my $result = decode_json($stdout);
+        return $result;
+    }
+    catch {
+        # Handle errors
+        return {
+            error => "Failed to execute CloudflareManager: $_\nSTDERR: $stderr"
+        };
+    };
+}
+
+__PACKAGE__->meta->make_immutable;
+
+1;

