+
+=head1 DESCRIPTION
+
+This controller handles error pages for the Comserv2 application.
+
+=head1 METHODS
+
+=cut
+
+=head2 index
+
+The main error page.
+
+=cut
+
+sub index :Path :Args(0) {
+    my ($self, $c) = @_;
+    
+    # Set default error message if none provided
+    $c->stash->{error_msg} ||= 'An error occurred while processing your request.';
+    
+    # Set the template
+    $c->stash->{template} = 'error.tt';
+}
+
+=head2 not_found
+
+404 Not Found error page.
+
+=cut
+
+sub not_found :Path('not_found') :Args(0) {
+    my ($self, $c) = @_;
+    
+    $c->response->status(404);
+    $c->stash->{error_title} = 'Page Not Found';
+    $c->stash->{error_msg} = 'The page you requested could not be found: ' . $c->request->path;
+    $c->stash->{template} = 'error.tt';
+}
+
+=head2 unauthorized
+
+401 Unauthorized error page.
+
+=cut
+
+sub unauthorized :Path('unauthorized') :Args(0) {
+    my ($self, $c) = @_;
+    
+    $c->response->status(401);
+    $c->stash->{error_title} = 'Unauthorized';
+    $c->stash->{error_msg} = 'You are not authorized to access this page.';
+    $c->stash->{template} = 'error.tt';
+}
+
+=head2 forbidden
+
+403 Forbidden error page.
+
+=cut
+
+sub forbidden :Path('forbidden') :Args(0) {
+    my ($self, $c) = @_;
+    
+    $c->response->status(403);
+    $c->stash->{error_title} = 'Forbidden';
+    $c->stash->{error_msg} = 'You do not have permission to access this page.';
+    $c->stash->{template} = 'error.tt';
+}
+
+=head2 server_error
+
+500 Internal Server Error page.
+
+=cut
+
+sub server_error :Path('server_error') :Args(0) {
+    my ($self, $c) = @_;
+    
+    $c->response->status(500);
+    $c->stash->{error_title} = 'Server Error';
+    $c->stash->{error_msg} = 'An internal server error occurred while processing your request.';
+    $c->stash->{template} = 'error.tt';
+}
+
+=head2 default
+
+Default error handler for all other errors.
+
+=cut
+
+sub default :Path {
+    my ($self, $c) = @_;
+    
+    # Get the error message from the stash or set a default
+    my $error_msg = $c->stash->{error_msg} || 'An error occurred while processing your request.';
+    
+    # Set the template and error message
+    $c->stash->{error_msg} = $error_msg;
+    $c->stash->{template} = 'error.tt';
+}
+
+__PACKAGE__->meta->make_immutable;
+
+1;

